} from './fsm/functions/activation/connectionNormalization.js';
import { migrateGlobalPATToConnections } from './fsm/functions/secrets/patMigration.js';
import {
  getApplicationStoreActor,
  sendApplicationStoreEvent,
  setActiveConnectionHandler as _setActiveConnectionHandler,
  setActiveConnectionIdReader,
  setExtensionContextRef as setExtensionContextRefBridge,
  setForwardProviderMessage as _setForwardProviderMessage,
  setGetSecretPAT,
  setLoadedConnectionsReader,
  setRegisterAllCommands as _setRegisterAllCommands,
  setWebviewMessageHandler as _setWebviewMessageHandler,
} from './fsm/services/extensionHostBridge.js';
import { registerCommands } from './features/commands/index.js';
import { registerTraceCommands } from './fsm/commands/traceCommands.js';
import { registerQuickDebugCommands } from './fsm/commands/quickDebugCommands.js';
import { FSMSetupService } from './fsm/services/fsmSetupService.js';
import { ConnectionAdapter } from './fsm/adapters/ConnectionAdapter.js';
import { getConnectionFSMManager } from './fsm/ConnectionFSMManager.js';
//import { initializeBridge } from './fsm/services/extensionHostBridge.js';
import type {
  AuthReminderReason,
  AuthReminderState,
  ConnectionState,
  ProjectConnection,
} from './fsm/machines/applicationMachine.js';
import type { WorkItemTimerState, TimeEntry } from './types.js';

type _AuthMethod = 'pat' | 'entra';

// Local lightweight type definitions for internal messaging helpers.
// These were previously only present in a temporary bundle artifact.
// Keeping them here avoids implicit any usage and preserves clarity.
type LoggerFn = (message: string, meta?: any) => void;
type PostWorkItemsSnapshotParams = {
  panel?: vscode.WebviewView;
  logger?: LoggerFn;
  connectionId?: string;
  items?: any[];
  kanbanView?: boolean;
  provider?: WorkItemsProvider;
  types?: string[];
  query?: string;
  branchContext?: BranchContext | null;
};

const STATE_TIMER = 'azureDevOpsInt.timer.state';
const STATE_TIME_ENTRIES = 'azureDevOpsInt.timer.entries';
const STATE_LAST_SAVE = 'azureDevOpsInt.timer.lastSave';
const STATE_TIMER_CONNECTION = 'azureDevOpsInt.timer.connection';
const CONFIG_NS = 'azureDevOpsIntegration';
const LEGACY_CONFIG_NS = 'azureDevOps';
const CONNECTIONS_CONFIG_KEY = 'connections';
const ACTIVE_CONNECTION_STATE_KEY = 'azureDevOpsInt.activeConnectionId';

let panel: vscode.WebviewView | undefined;
let provider: WorkItemsProvider | undefined;
let timer: WorkItemTimer | undefined;
let sessionTelemetry: SessionTelemetryManager | undefined;
let client: AzureDevOpsIntClient | undefined;
let statusBarItem: vscode.StatusBarItem | undefined;
let authStatusBarItem: vscode.StatusBarItem | undefined;
const _PAT_KEY = 'azureDevOpsInt.pat';
const OPENAI_SECRET_KEY = 'azureDevOpsInt.openai.apiKey';
let viewProviderRegistered = false;
const initialRefreshedConnections = new Set<string>();
let connections: ProjectConnection[] = [];
const connectionStates = new Map<string, ConnectionState>();
let connectionAdapterInstance: ConnectionAdapter | undefined;
let activeConnectionId: string | undefined;
let tokenRefreshInterval: NodeJS.Timeout | undefined;
let gcInterval: NodeJS.Timeout | undefined;
let isDeactivating = false;
let rejectionHandler: ((reason: any, promise: Promise<any>) => void) | undefined;
let sharedContextBridge: ReturnType<typeof createSharedContextBridge> | undefined;
let extensionContextRef: vscode.ExtensionContext | undefined;
let openAiClient: OpenAI | undefined;
let cachedExtensionVersion: string | undefined; // cache package.json version for cache-busting
// Track last applied query to avoid redundant provider refreshes
let lastQueriedActiveConnectionId: string | undefined;
let lastQueriedQuery: string | undefined;

function shouldLogDebug(): boolean {
  try {
    return Boolean(getConfig().get('debugLogging'));
  } catch {
    return false;
  }
}
// -------------------------------------------------------------
// TEST HOOKS (Internal) - Minimal message handling used by tests
// -------------------------------------------------------------
// These exports provide a lightweight simulation of the webview  activation
// message contract for unit tests that import activation.ts directly.
// They intentionally avoid FSM pathways for isolation.
export function __setTestContext(ctx: {
  panel?: any;
  provider?: any;
  client?: any;
  timer?: any;
}): void {
  if (ctx.panel) panel = ctx.panel;
  if (ctx.provider) provider = ctx.provider;
  if (ctx.client) client = ctx.client;
  if (ctx.timer) timer = ctx.timer;
}

export function handleMessage(message: any): void {
  switch (message?.type) {
    case 'openExternal': {
      // FSM-requested external URL open
      if (message.url) {
        vscode.env.openExternal(vscode.Uri.parse(message.url));
      }
      break;
    }
    case 'createBranch': {
      // FSM-requested branch creation
      if (message.suggestedName) {
        vscode.window
          .showInputBox({
            prompt: 'Enter branch name',
            value: message.suggestedName,
            placeHolder: 'feature/123-my-work-item',
          })
          .then((name) => {
            if (name) {
              vscode.commands.executeCommand('git.branch', name);
            }
          });
      }
      break;
    }
    case 'getWorkItems': {
      const items = provider?.getWorkItems?.() || [];
      sendToWebview({
        type: 'workItemsLoaded',
        workItems: items,
        connectionId: activeConnectionId,
        query: getStoredQueryForConnection(activeConnectionId),
      });
      const typeOptions = provider?.getWorkItemTypeOptions?.();
      if (Array.isArray(typeOptions)) {
        sendToWebview({ type: 'workItemTypeOptions', options: [...typeOptions] });
      }
      break;
    }
    case 'startTimer': {
      const id = message.workItemId;
      if (!timer || typeof timer.start !== 'function' || !provider) break;
      const items = provider.getWorkItems?.() || [];
      const match = items.find((i: any) => i.id === id);
      const title = match?.fields?.['System.Title'] || `Work Item ${id}`;
      try {
        timer.start(id, title);
      } catch {
        /* ignore */
      }
      break;
    }
    case 'refresh': {
      try {
        provider?.refresh?.(getStoredQueryForConnection(activeConnectionId));
      } catch {
        /* ignore */
      }
      break;
    }
    case 'addComment': {
      const workItemId = message.workItemId;
      if (!message.comment) {
        sendToWebview({ type: 'showComposeComment', mode: 'addComment', workItemId });
        break;
      }
      // fallthrough to submitComposeComment logic if comment provided
    }
    case 'submitComposeComment': {
      const { workItemId, comment, mode, timerData } = message;
      if (!client) {
        sendToWebview({ type: 'composeCommentResult', success: false, mode, workItemId });
        break;
      }
      const hoursDecimal: number | undefined = timerData?.hoursDecimal;
      (async () => {
        try {
          if (mode === 'timerStop' && typeof hoursDecimal === 'number') {
            const wi = await client.getWorkItemById?.(workItemId);
            const completed = Number(wi?.fields?.['Microsoft.VSTS.Scheduling.CompletedWork'] || 0);
            const remaining = Number(wi?.fields?.['Microsoft.VSTS.Scheduling.RemainingWork'] || 0);
            const newCompleted = completed + hoursDecimal;
            const newRemaining = Math.max(remaining - hoursDecimal, 0);
            await client.updateWorkItem?.(workItemId, [
              {
                op: 'add',
                path: '/fields/Microsoft.VSTS.Scheduling.CompletedWork',
                value: newCompleted,
              },
              {
                op: 'add',
                path: '/fields/Microsoft.VSTS.Scheduling.RemainingWork',
                value: newRemaining,
              },
            ]);
            const hoursStr = hoursDecimal.toFixed(2);
            const composed = comment
              ? `${comment} (Logged ${hoursStr}h)`
              : `Logged ${hoursStr}h via timer stop.`;
            await client.addWorkItemComment?.(workItemId, composed);
            sendToWebview({
              type: 'composeCommentResult',
              success: true,
              mode,
              workItemId,
              hours: hoursDecimal,
            });
            return;
          }
          if (mode === 'addComment') {
            if (comment) {
              await client.addWorkItemComment?.(workItemId, comment);
              sendToWebview({ type: 'composeCommentResult', success: true, mode, workItemId });
            } else {
              sendToWebview({ type: 'showComposeComment', mode: 'addComment', workItemId });
            }
            return;
          }
          // Generic fallback
          if (comment) {
            await client.addWorkItemComment?.(workItemId, comment);
          }
          sendToWebview({ type: 'composeCommentResult', success: true, mode, workItemId });
        } catch (err) {
          sendToWebview({
            type: 'composeCommentResult',
            success: false,
            mode,
            workItemId,
            error: String(err),
          });
        }
      })().catch(() => {
        sendToWebview({ type: 'composeCommentResult', success: false, mode, workItemId });
      });
      break;
    }
    default:
      // ignore
      break;
  }
}

// Legacy PAT migration helper (test-only export)
// Moves PAT from globalState key to secrets storage if present.
export async function migrateLegacyPAT(context: vscode.ExtensionContext): Promise<void> {
  try {
    const legacy = context.globalState.get<string>('azureDevOpsInt.pat');
    if (legacy && legacy.trim().length > 0) {
      await context.secrets.store('azureDevOpsInt.pat', legacy);
      // Clear legacy value to prevent re-migration loops (best-effort)
      try {
        await context.globalState.update('azureDevOpsInt.pat', undefined as any);
      } catch {
        /* ignore */
      }
    }
  } catch {
    /* ignore migration errors for tests */
  }
}

// Build minimal webview HTML (test helper) choosing bundle based on feature flag + existence.
export function buildMinimalWebviewHtml(
  context: vscode.ExtensionContext,
  webview: { cspSource: string; asWebviewUri: (u: any) => any },
  nonce: string
): string {
  const cfg = vscode.workspace.getConfiguration('azureDevOpsIntegration');
  const enableSvelte = !!cfg.get('experimentalSvelteUI');
  const basePath = context.extensionPath;
  const sveltePath = path.join(basePath, 'media', 'webview', 'svelte-main.js');
  const scriptFile = enableSvelte && fs.existsSync(sveltePath) ? 'svelte-main.js' : 'main.js';
  const scriptUri = webview.asWebviewUri(
    vscode.Uri.joinPath(context.extensionUri, 'media', 'webview', scriptFile)
  );
  // Link to main.css which contains Svelte component styles from esbuild
  const mainCssUri = webview.asWebviewUri(
    vscode.Uri.joinPath(context.extensionUri, 'media', 'webview', 'main.css')
  );
  const csp = `default-src 'none'; style-src ${webview.cspSource} 'unsafe-inline'; script-src 'nonce-${nonce}' 'unsafe-eval'; img-src ${webview.cspSource} data:; connect-src 'self';`;
  return `<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <meta http-equiv="Content-Security-Policy" content="${csp}" />
  <link href="${mainCssUri}" rel="stylesheet" />
  <title>Work Items</title>
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <meta name="azure-devops-integration" content="minimal-webview" />
</head>
<body>
  <div id="svelte-root"></div>
  <script type="module" nonce="${nonce}" src="${scriptUri}"></script>
</body>
</html>`;
}

const activationLogger = createScopedLogger('activation', shouldLogDebug);
const verbose = activationLogger.debug;

// Self-test tracking (prove Svelte webview round-trip works)
// Self-test pending promise handlers (typed loosely to avoid unused param lint churn)
let selfTestPending:
  | { nonce: string; resolve: Function; reject: Function; timeout: NodeJS.Timeout }
  | undefined;

type TimerConnectionInfo = {
  id?: string;
  label?: string;
  organization?: string;
  project?: string;
};

let timerConnectionInfo: TimerConnectionInfo = {};

const DEFAULT_QUERY = 'My Activity';
const activeQueryByConnection = new Map<string, string>();

let nextAuthConnectionIndex = 0;
const INTERACTIVE_REAUTH_THROTTLE_MS = 5 * 60 * 1000;

function getApplicationActor():
  | { getSnapshot?: () => any; send?: (event: unknown) => void }
  | undefined {
  const actor = getApplicationStoreActor();
  if (!actor || typeof actor !== 'object') {
    return undefined;
  }
  return actor as { getSnapshot?: () => any; send?: (event: unknown) => void };
}

async function showEditDialog(item: any, client: any, provider: any): Promise<void> {
  // Define editable fields with their display names and current values
  const editableFields = [
    {
      id: 'System.Title',
      label: 'Title',
      value: item.fields?.['System.Title'] || '',
      type: 'text',
    },
    {
      id: 'System.State',
      label: 'State',
      value: item.fields?.['System.State'] || '',
      type: 'picklist',
      options: ['New', 'Active', 'Resolved', 'Closed', 'Removed'],
    },
    {
      id: 'System.AssignedTo',
      label: 'Assigned To',
      value:
        item.fields?.['System.AssignedTo']?.displayName || item.fields?.['System.AssignedTo'] || '',
      type: 'text',
    },
    {
      id: 'System.Tags',
      label: 'Tags',
      value: item.fields?.['System.Tags'] || '',
      type: 'text',
    },
    {
      id: 'System.Description',
      label: 'Description',
      value: item.fields?.['System.Description'] || '',
      type: 'multiline',
    },
  ];

  // Show field selection
  const fieldItems = editableFields.map((field) => ({
    label: field.label,
    description: `Current: ${field.value}`,
    field: field,
  }));

  const selectedField = await vscode.window.showQuickPick(fieldItems, {
    placeHolder: 'Select field to edit',
    title: `Edit Work Item #${item.id}`,
  });

  if (!selectedField) return;

  const field = selectedField.field;
  let newValue: string | undefined;

  if (field.type === 'picklist') {
    // Show picklist for state field
    const stateItems = field.options.map((option) => ({
      label: option,
      picked: option === field.value,
    }));

    const selectedState = await vscode.window.showQuickPick(stateItems, {
      placeHolder: 'Select new state',
      title: `Edit ${field.label}`,
    });

    if (!selectedState) return;
    newValue = selectedState.label;
  } else if (field.type === 'multiline') {
    // Show input box for multiline text
    newValue = await vscode.window.showInputBox({
      prompt: `Enter new ${field.label}`,
      value: field.value,
      ignoreFocusOut: true,
    });
  } else {
    // Show input box for single-line text
    newValue = await vscode.window.showInputBox({
      prompt: `Enter new ${field.label}`,
      value: field.value,
      ignoreFocusOut: true,
    });
  }

  if (newValue === undefined) return; // User cancelled

  // Update the work item
  try {
    const patches = [
      {
        op: 'replace' as const,
        path: `/fields/${field.id}`,
        value: newValue,
      },
    ];

    const updatedItem = await client.updateWorkItem(item.id, patches);

    if (updatedItem) {
      vscode.window.showInformationMessage(
        `Successfully updated ${field.label} for work item #${item.id}`
      );

      // Refresh the provider to show updated data
      provider.refresh?.(getStoredQueryForConnection(activeConnectionId));
    } else {
      vscode.window.showErrorMessage(`Failed to update ${field.label} for work item #${item.id}`);
    }
  } catch (error) {
    console.error('Error updating work item:', error);
    vscode.window.showErrorMessage(
      `Error updating work item: ${error instanceof Error ? error.message : String(error)}`
    );
  }
}

function dispatchApplicationEvent(event: unknown): void {
  // Route work item action events to legacy handleMessage which has implementations
  if (event && typeof event === 'object' && 'type' in event) {
    const evt = event as any;

    switch (evt.type) {
      case 'START_TIMER_INTERACTIVE':
        // Route to legacy handler with correct message format
        try {
          handleMessage({ type: 'startTimer', workItemId: evt.workItemId });
        } catch (error) {
          console.error('Error starting timer:', error);
          vscode.window.showErrorMessage(
            `Failed to start timer: ${error instanceof Error ? error.message : String(error)}`
          );
        }
        break;
      case 'STOP_TIMER':
        // Show comment dialog for time entry
        (async () => {
          try {
            const appActor = getApplicationStoreActor();
            const snapshot = appActor?.getSnapshot?.();
            const timerState = snapshot?.context?.timerState;

            if (!timerState?.workItemId || !timerState?.startTime) {
              vscode.window.showWarningMessage('No active timer to stop');
              return;
            }

            // Calculate elapsed time
            const elapsed = Math.floor((Date.now() - timerState.startTime) / 1000);
            const hours = (elapsed / 3600).toFixed(2);

            // Prompt for comment
            const comment = await vscode.window.showInputBox({
              prompt: `Add comment for ${hours} hours logged on work item #${timerState.workItemId}`,
              placeHolder: 'Optional: describe what you worked on...',
            });

            // Stop the timer (send to FSM)
            const timerActor = snapshot?.context?.timerActor;
            if (timerActor && typeof timerActor.send === 'function') {
              timerActor.send({ type: 'STOP' });
            }

            // Add time entry and comment if we have a client
            if (client && timerState.workItemId) {
              try {
                // Update completed/remaining work
                const wi = await client.getWorkItemById(timerState.workItemId);
                const completed = Number(
                  wi?.fields?.['Microsoft.VSTS.Scheduling.CompletedWork'] || 0
                );
                const remaining = Number(
                  wi?.fields?.['Microsoft.VSTS.Scheduling.RemainingWork'] || 0
                );
                const hoursDecimal = Number(hours);

                await client.updateWorkItem(timerState.workItemId, [
                  {
                    op: 'add',
                    path: '/fields/Microsoft.VSTS.Scheduling.CompletedWork',
                    value: completed + hoursDecimal,
                  },
                  {
                    op: 'add',
                    path: '/fields/Microsoft.VSTS.Scheduling.RemainingWork',
                    value: Math.max(remaining - hoursDecimal, 0),
                  },
                ]);

                // Add comment
                const finalComment = comment
                  ? `${comment} (Logged ${hours}h)`
                  : `Logged ${hours}h via timer stop.`;
                await client.addWorkItemComment(timerState.workItemId, finalComment);

                vscode.window.showInformationMessage(
                  `Timer stopped. Logged ${hours} hours to work item #${timerState.workItemId}`
                );
              } catch (error) {
                console.error('Error adding time entry:', error);
                vscode.window.showErrorMessage(
                  `Timer stopped but failed to log time: ${error instanceof Error ? error.message : String(error)}`
                );
              }
            } else {
              vscode.window.showInformationMessage(`Timer stopped. ${hours} hours elapsed.`);
            }

            // Clear persisted timer state
            if (snapshot?.context?.extensionContext) {
              await snapshot.context.extensionContext.globalState.update(
                'azureDevOpsInt.timer.state',
                undefined
              );
            }
          } catch (error) {
            console.error('Error stopping timer:', error);
            vscode.window.showErrorMessage(
              `Failed to stop timer: ${error instanceof Error ? error.message : String(error)}`
            );
          }
        })();
        break;
      case 'EDIT_WORK_ITEM':
        // Implement in-VSCode edit dialog
        try {
          if (evt.workItemId && client && provider) {
            const items = provider.getWorkItems?.() || [];
            const item = items.find((i: any) => i.id === evt.workItemId);
            if (item) {
              showEditDialog(item, client, provider);
            } else {
              vscode.window.showErrorMessage('Work item not found');
            }
          } else {
            vscode.window.showErrorMessage('Unable to edit work item: missing client or provider');
          }
        } catch (error) {
          console.error('Error editing work item:', error);
          vscode.window.showErrorMessage(
            `Failed to edit work item: ${error instanceof Error ? error.message : String(error)}`
          );
        }
        break;
      case 'OPEN_IN_BROWSER':
      case 'OPEN_WORK_ITEM':
        // Open work item in browser
        if (evt.workItemId && client) {
          const url = client.getBrowserUrl(`/_workitems/edit/${evt.workItemId}`);
          vscode.env.openExternal(vscode.Uri.parse(url));
        }
        break;
      case 'CREATE_BRANCH':
        // Show input for branch name then create and link to work item
        (async () => {
          try {
            if (evt.workItemId) {
              const items = provider?.getWorkItems?.() || [];
              const item = items.find((i: any) => i.id === evt.workItemId);

              if (item) {
                const title = item.fields?.['System.Title'] || '';
                const branchName = `feature/${evt.workItemId}-${title.toLowerCase().replace(/[^a-z0-9]+/g, '-')}`;
                const name = await vscode.window.showInputBox({
                  prompt: 'Enter branch name',
                  value: branchName,
                });

                if (name) {
                  try {
                    // Create the branch
                    await vscode.commands.executeCommand('git.branch', name);

                    // Add comment to work item linking the branch
                    if (client) {
                      const comment = `Created branch: ${name}`;
                      const success = await client.addWorkItemComment(evt.workItemId, comment);

                      if (success) {
                        vscode.window.showInformationMessage(
                          `Branch "${name}" created and linked to work item #${evt.workItemId}`
                        );
                      } else {
                        vscode.window.showWarningMessage(
                          `Branch "${name}" created but failed to link to work item #${evt.workItemId}`
                        );
                      }
                    } else {
                      vscode.window.showInformationMessage(`Branch "${name}" created`);
                    }
                  } catch (error) {
                    console.error('Error creating branch:', error);
                    vscode.window.showErrorMessage(
                      `Failed to create branch: ${error instanceof Error ? error.message : String(error)}`
                    );
                  }
                }
              } else {
                vscode.window.showErrorMessage('Work item not found');
              }
            } else {
              vscode.window.showErrorMessage('No work item ID provided for branch creation');
            }
          } catch (error) {
            console.error('Error in branch creation:', error);
            vscode.window.showErrorMessage(
              `Failed to create branch: ${error instanceof Error ? error.message : String(error)}`
            );
          }
        })();
        break;
    }
  }

  // Always send event to FSM for state management
  sendApplicationStoreEvent(event);
}

async function signInWithEntra(
  context: vscode.ExtensionContext,
  connectionId?: string,
  options: { showSuccessMessage?: boolean; forceInteractive?: boolean } = {}
): Promise<void> {
  await ensureConnectionsInitialized(context);
  const targetId = connectionId ?? activeConnectionId;

  if (!targetId) {
    vscode.window.showWarningMessage('No active connection to sign in with.');
    return;
  }

  dispatchApplicationEvent({
    type: 'SIGN_IN_ENTRA',
    connectionId: targetId,
    forceInteractive: options.forceInteractive,
  });
}

async function signOutEntra(
  context: vscode.ExtensionContext,
  connectionId?: string
): Promise<void> {
  await ensureConnectionsInitialized(context);
  const targetId = connectionId ?? activeConnectionId;

  if (!targetId) {
    vscode.window.showWarningMessage('No active connection to sign out from.');
    return;
  }

  dispatchApplicationEvent({ type: 'SIGN_OUT_ENTRA', connectionId: targetId });
}

function safeCommandHandler<Args extends unknown[], Result>(
  handler: (...args: Args) => Result
): (...args: Args) => void {
  return (...args: Args) => {
    if (isDeactivating) {
      console.log('[AzureDevOpsInt] [Command] Ignoring command execution during deactivation');
      return;
    }

    try {
      const result = handler(...args);
      const maybeThenable = result as
        | { catch?: (onRejected: (reason: unknown) => unknown) => unknown }
        | undefined;
      if (maybeThenable?.catch) {
        maybeThenable.catch((error) => {
          if (!isDeactivating) {
            console.error('[AzureDevOpsInt] [Command] Unhandled command error:', error);
          }
        });
      }
    } catch (error) {
      if (!isDeactivating) {
        console.error('[AzureDevOpsInt] [Command] Synchronous command error:', error);
      }
    }
  };
}

async function ensureSharedContextBridge(
  context: vscode.ExtensionContext
): Promise<ReturnType<typeof createSharedContextBridge>> {
  if (!sharedContextBridge) {
    if (!getApplicationActor()) {
      await import('./stores/applicationStore.js');
    }

    const actor = getApplicationActor();
    if (!actor) {
      throw new Error('Application store actor not available');
    }

    sharedContextBridge = createSharedContextBridge({
      actor: actor as any,
      logger: (message, meta) => {
        verbose(`[context-bridge] ${message}`, meta);
      },
    });

    context.subscriptions.push(sharedContextBridge);
    context.subscriptions.push(
      new vscode.Disposable(() => {
        sharedContextBridge = undefined;
      })
    );
  }

  return sharedContextBridge;
}

function getPendingAuthReminderMap(): Map<string, AuthReminderState> {
  const actor = getApplicationActor();
  if (!actor?.getSnapshot) {
    return new Map();
  }

  try {
    const snapshot = actor.getSnapshot?.();
    const pending = snapshot?.context?.pendingAuthReminders;

    if (pending instanceof Map) {
      return new Map(pending as Map<string, AuthReminderState>);
    }

    if (Array.isArray(pending)) {
      const normalized = pending
        .filter((entry): entry is AuthReminderState =>
          Boolean(
            entry && typeof entry.connectionId === 'string' && entry.connectionId.trim().length > 0
          )
        )
        .map((entry) => [entry.connectionId, entry] as const);

      return new Map(normalized);
    }
  } catch (error) {
    verbose('[authReminder] Failed to read pending reminders from actor', { error });
  }

  return new Map();
}

function getPendingAuthConnectionIds(): string[] {
  const pending = getPendingAuthReminderMap();
  const ordered: string[] = [];

  for (const connection of connections) {
    const reminder = pending.get(connection.id);
    if (reminder?.status === 'pending') {
      ordered.push(connection.id);
    }
  }

  for (const [connectionId, reminder] of pending.entries()) {
    if (reminder?.status === 'pending' && !ordered.includes(connectionId)) {
      ordered.push(connectionId);
    }
  }

  return ordered;
}

async function cycleAuthSignIn(context: vscode.ExtensionContext): Promise<void> {
  const pendingIds = getPendingAuthConnectionIds();

  if (pendingIds.length === 0) {
    if (activeConnectionId) {
      await signInWithEntra(context, activeConnectionId);
    } else {
      await signInWithEntra(context);
    }
    return;
  }

  if (nextAuthConnectionIndex >= pendingIds.length) {
    nextAuthConnectionIndex = 0;
  }

  const targetId = pendingIds[nextAuthConnectionIndex];
  nextAuthConnectionIndex = (nextAuthConnectionIndex + 1) % pendingIds.length;

  clearAuthReminder(targetId);
  await signInWithEntra(context, targetId, { showSuccessMessage: true });
}

function describeConnection(connection: ProjectConnection): string {
  if (connection.label && connection.label.trim().length > 0) {
    return connection.label;
  }

  const parts: string[] = [];
  if (connection.organization) parts.push(connection.organization);
  if (connection.project) parts.push(connection.project);

  if (parts.length > 0) {
    return parts.join('/');
  }

  return connection.id;
}

async function updateAuthStatusBar(): Promise<void> {
  if (!authStatusBarItem) return;

  if (!activeConnectionId) {
    clearAuthReminder(activeConnectionId);
    authStatusBarItem.hide();
    return;
  }

  const state = connectionStates.get(activeConnectionId);
  if (!state) {
    authStatusBarItem.hide();
    return;
  }

  // Show status bar for BOTH PAT and Entra connections
  const authMethod = state.authMethod || state.config?.authMethod || 'pat';

  authStatusBarItem.command = {
    title: 'Sign in with Microsoft Entra',
    command: 'azureDevOpsInt.signInWithEntra',
    arguments: [activeConnectionId],
  };

  try {
    const connectionLabel = describeConnection(state.config);

    // Get FSM connection state to determine actual auth status
    const actor = getApplicationActor();
    const snapshot = actor?.getSnapshot?.();
    const fsmConnectionStates = snapshot?.context?.connectionStates;
    const fsmConnectionState = fsmConnectionStates?.get(activeConnectionId);

    // Check multiple indicators of connection status
    const isConnected = Boolean(
      fsmConnectionState?.client ||
        fsmConnectionState?.provider ||
        (typeof fsmConnectionState?.isConnected === 'boolean' && fsmConnectionState.isConnected) ||
        // Also check legacy connectionStates map for connected clients/providers
        (state.client && state.provider)
    );

    const hasAuthFailure = Boolean(
      fsmConnectionState?.reauthInProgress ||
        fsmConnectionState?.lastError ||
        state.reauthInProgress
    );

    // Check if there's an active device code session for this connection
    const hasActiveDeviceCode = Boolean(
      snapshot?.context?.deviceCodeSession?.connectionId === activeConnectionId &&
        snapshot?.context?.deviceCodeSession?.expiresAt > Date.now()
    );

    // Only log status check if debug logging is enabled to prevent excessive logging
    if (shouldLogDebug()) {
      console.log('[AzureDevOpsInt][updateAuthStatusBar] Status check', {
        activeConnectionId,
        isConnected,
        hasAuthFailure,
        hasActiveDeviceCode,
        hasClient: !!state.client,
        hasProvider: !!state.provider,
        fsmIsConnected: fsmConnectionState?.isConnected,
        fsmClient: !!fsmConnectionState?.client,
        fsmProvider: !!fsmConnectionState?.provider,
      });
    }

    if (isConnected && !hasAuthFailure && !hasActiveDeviceCode) {
      // Show successful auth status
      if (authMethod === 'entra') {
        authStatusBarItem.text = '$(pass) Entra: Connected';
        authStatusBarItem.tooltip = `Microsoft Entra ID authentication active for ${connectionLabel}`;
        authStatusBarItem.command = undefined; // No action needed when connected
      } else {
        authStatusBarItem.text = '$(key) PAT: Connected';
        authStatusBarItem.tooltip = `Personal Access Token authentication active for ${connectionLabel}`;
        authStatusBarItem.command = 'azureDevOpsInt.setup'; // Allow managing connections
      }
      authStatusBarItem.backgroundColor = undefined; // Clear warning background
      authStatusBarItem.show();
    } else if (hasAuthFailure) {
      // Show auth failure status
      const authLabel = authMethod === 'entra' ? 'Entra' : 'PAT';
      authStatusBarItem.text = `$(error) ${authLabel}: Auth Failed`;
      authStatusBarItem.tooltip = `Authentication failed for ${connectionLabel}. Click to manage connections.`;
      authStatusBarItem.backgroundColor = new vscode.ThemeColor('statusBarItem.errorBackground');
      authStatusBarItem.command = 'azureDevOpsInt.setup';
      authStatusBarItem.show();
    } else if (hasActiveDeviceCode) {
      // Show device code flow in progress (Entra only)
      authStatusBarItem.text = '$(sync~spin) Entra: Device Code Active';
      authStatusBarItem.tooltip = `Device code authentication in progress for ${connectionLabel}`;
      authStatusBarItem.backgroundColor = new vscode.ThemeColor('statusBarItem.warningBackground');
      authStatusBarItem.command = 'azureDevOpsInt.signInWithEntra';
      authStatusBarItem.show();
    } else {
      // Show sign-in/auth required status
      if (authMethod === 'entra') {
        authStatusBarItem.text = '$(warning) Entra: Sign In Required';
        authStatusBarItem.tooltip = `Sign in required for ${connectionLabel}`;
        authStatusBarItem.command = 'azureDevOpsInt.signInWithEntra';
      } else {
        authStatusBarItem.text = '$(warning) PAT: Auth Required';
        authStatusBarItem.tooltip = `Personal Access Token required for ${connectionLabel}. Click to manage connections.`;
        authStatusBarItem.command = 'azureDevOpsInt.setup';
      }
      authStatusBarItem.backgroundColor = new vscode.ThemeColor('statusBarItem.warningBackground');
      authStatusBarItem.show();
    }
  } catch (error) {
    console.error('[AzureDevOpsInt] [updateAuthStatusBar] Error updating auth status:', error);
    authStatusBarItem.hide();
  }
}

function notifyConnectionsList(): void {
  const actor = getApplicationActor();
  const send = actor?.send;
  if (!send) {
    return;
  }

  send({
    type: 'CONNECTIONS_LOADED',
    connections: connections.slice(),
  });

  if (activeConnectionId) {
    send({ type: 'CONNECTION_SELECTED', connectionId: activeConnectionId });
  }
}

function ensureAuthReminder(
  connectionId: string,
  reason: AuthReminderReason,
  options: { detail?: string } = {}
): void {
  dispatchApplicationEvent({
    type: 'AUTH_REMINDER_REQUESTED',
    connectionId,
    reason,
    detail: options.detail,
  });
}

function triggerAuthReminderSignIn(
  connectionId: string,
  reason: AuthReminderReason,
  options: { detail?: string; force?: boolean; startInteractive?: boolean } = {}
): void {
  const state = connectionStates.get(connectionId);
  if (!state) return;

  const detail = options.detail;
  const startInteractive = options.startInteractive === true;

  if (!startInteractive || (state.authMethod ?? 'pat') !== 'entra') {
    ensureAuthReminder(connectionId, reason, detail ? { detail } : {});
    return;
  }

  const now = Date.now();
  if (state.reauthInProgress) {
    return;
  }

  if (
    !options.force &&
    state.lastInteractiveAuthAt &&
    now - state.lastInteractiveAuthAt < INTERACTIVE_REAUTH_THROTTLE_MS
  ) {
    return;
  }

  state.reauthInProgress = true;
  state.lastInteractiveAuthAt = now;
  state.accessToken = undefined;
  state.refreshFailureCount = 0;
  state.refreshBackoffUntil = undefined;

  const context = extensionContextRef;

  (async () => {
    try {
      if (context) {
        await signInWithEntra(context, connectionId, {
          showSuccessMessage: false,
          forceInteractive: true,
        });
      } else {
        await vscode.commands.executeCommand('azureDevOpsInt.signInWithEntra', connectionId);
      }
    } catch (error) {
      console.error(
        '[AzureDevOpsInt] [triggerAuthReminderSignIn] Interactive Entra sign-in failed',
        error
      );
      ensureAuthReminder(connectionId, reason, detail ? { detail } : {});
    } finally {
      state.reauthInProgress = false;
    }
  })().catch((error) => {
    console.error(
      '[AzureDevOpsInt] [triggerAuthReminderSignIn] Unexpected Entra sign-in error',
      error
    );
    state.reauthInProgress = false;
    ensureAuthReminder(connectionId, reason, detail ? { detail } : {});
  });
}

function clearAuthReminder(connectionId: string | undefined): void {
  if (!connectionId) {
    return;
  }

  dispatchApplicationEvent({ type: 'AUTH_REMINDER_CLEARED', connectionId });

  if (panel) {
    sendToWebview({
      type: 'authReminderClear',
      connectionId,
    });
  }

  if (getPendingAuthReminderMap().size === 0) {
    nextAuthConnectionIndex = 0;
  }
}

function normalizeQuery(value: unknown): string | undefined {
  if (typeof value !== 'string') return undefined;
  const trimmed = value.trim();
  return trimmed.length > 0 ? trimmed : undefined;
}

function getStoredQueryForConnection(connectionId?: string | null, fallback?: string): string {
  const resolved = fallback ?? getDefaultQuery(getConfig());
  const base = resolved && resolved.trim().length > 0 ? resolved : DEFAULT_QUERY;
  if (!connectionId) {
    return base;
  }
  const stored = activeQueryByConnection.get(connectionId);
  if (stored && stored.trim().length > 0) {
    return stored;
  }
  activeQueryByConnection.set(connectionId, base);
  return base;
}

function setStoredQueryForConnection(connectionId: string, query?: string): string {
  const resolvedDefault = getDefaultQuery(getConfig());
  const normalized = normalizeQuery(query) ?? resolvedDefault ?? DEFAULT_QUERY;
  activeQueryByConnection.set(connectionId, normalized);
  return normalized;
}

function getQueryForProvider(
  targetProvider?: WorkItemsProvider,
  connectionId?: string | null
): string {
  const cfg = getConfig();
  const fallback = getDefaultQuery(cfg);
  const providerConnectionId = connectionId
    ? connectionId
    : typeof targetProvider?.getConnectionId === 'function'
      ? targetProvider.getConnectionId()
      : undefined;
  return getStoredQueryForConnection(providerConnectionId, fallback);
}

function setTimerConnectionFrom(connection: ProjectConnection | undefined) {
  if (connection) {
    timerConnectionInfo = {
      id: connection.id,
      label: getConnectionLabel(connection),
      organization: connection.organization,
      project: connection.project,
    };
  } else {
    timerConnectionInfo = {};
  }
}

function getActiveTimerConnectionLabel(): string | undefined {
  const info = timerConnectionInfo;
  if (!info) {
    return undefined;
  }

  if (info.label && info.label.trim().length > 0) {
    return info.label;
  }

  if (info.id) {
    const byId = connections.find((connection) => connection.id === info.id);
    if (byId) {
      return getConnectionLabel(byId);
    }
  }

  if (info.organization && info.project) {
    const byCoordinates = connections.find(
      (connection) =>
        connection.organization === info.organization && connection.project === info.project
    );
    if (byCoordinates) {
      return getConnectionLabel(byCoordinates);
    }
  }

  return undefined;
}

type EnsureActiveConnectionOptions = {
  refresh?: boolean;
  notify?: boolean;
  interactive?: boolean;
};

async function ensureActiveConnection(
  context: vscode.ExtensionContext,
  connectionId?: string,
  options: EnsureActiveConnectionOptions = {}
): Promise<ConnectionState | undefined> {
  const prepared = await resolveActiveConnectionTarget(context, connectionId, options);
  if (!prepared) {
    return undefined;
  }

  const { connection } = prepared;
  const adapter = getConnectionAdapterInstance();

  const result = (await adapter.ensureActiveConnection(context, connection.id, options)) as
    | ConnectionState
    | undefined;

  if (result?.client && result?.provider) {
    (result as any).id = connection.id;
    result.config = connection;
    result.authMethod = connection.authMethod || 'pat';

    const settings = getConfig();
    await finalizeConnectionSuccess(connection, result, options, settings);
    return result;
  }

  verbose('[ensureActiveConnection] FSM connection did not produce a usable provider.', {
    connectionId: connection.id,
    hasResult: !!result,
    hasClient: !!result?.client,
    hasProvider: !!result?.provider,
  });

  return undefined;
}

function getConnectionAdapterInstance(): ConnectionAdapter {
  if (!connectionAdapterInstance) {
    const manager = getConnectionFSMManager();
    // The fallback function is no longer needed as we are fully on FSM.
    connectionAdapterInstance = new ConnectionAdapter(manager, async () => undefined, true);
    connectionAdapterInstance.setUseFSM(true);
  }
  return connectionAdapterInstance;
}

async function resolveActiveConnectionTarget(
  context: vscode.ExtensionContext,
  connectionId?: string,
  options: EnsureActiveConnectionOptions = {}
): Promise<{ connection: ProjectConnection; connectionId: string } | undefined> {
  await ensureConnectionsInitialized(context);

  const targetId = connectionId ?? activeConnectionId ?? connections[0]?.id;
  verbose('[ensureActiveConnection] evaluating target', {
    requested: connectionId,
    activeConnectionId,
    resolved: targetId,
    connectionCount: connections.length,
  });

  if (!targetId) {
    if (options.notify !== false) {
      notifyConnectionsList();
    }
    await vscode.commands.executeCommand('setContext', 'azureDevOpsInt.connected', false);
    provider = undefined;
    client = undefined;
    return undefined;
  }

  if (targetId !== activeConnectionId) {
    activeConnectionId = targetId;
    await context.globalState.update(ACTIVE_CONNECTION_STATE_KEY, targetId);
    if (options.notify !== false) {
      notifyConnectionsList();
    }
  }

  const connection = connections.find((item) => item.id === targetId);
  if (!connection) {
    console.warn('[azureDevOpsInt] Connection not found for id', targetId);
    return undefined;
  }

  verbose('[ensureActiveConnection] using connection', {
    id: connection.id,
    organization: connection.organization,
    project: connection.project,
    baseUrl: connection.baseUrl,
    apiBaseUrl: connection.apiBaseUrl,
    authMethod: connection.authMethod || 'pat',
    hasIdentityName: !!connection.identityName,
    identityName: connection.identityName,
  });

  return { connection, connectionId: targetId };
}

function configureProviderForConnection(
  connection: ProjectConnection,
  state: ConnectionState
): void {
  if (!state.provider) {
    return;
  }

  const providerLogger = createScopedLogger(`provider:${connection.id}`, shouldLogDebug);
  const branchSource = { id: connection.id, client: state.client };

  if (typeof state.provider.updateClient === 'function' && state.client) {
    state.provider.updateClient(state.client);
  }

  state.provider.setPostMessage?.((msg: unknown) => forwardProviderMessage(connection.id, msg));
  state.provider.setLogger?.(providerLogger);
  state.provider.setTransformWorkItems?.(createBranchAwareTransform(branchSource));
}

async function finalizeConnectionSuccess(
  connection: ProjectConnection,
  state: ConnectionState,
  options: EnsureActiveConnectionOptions,
  settings: vscode.WorkspaceConfiguration
): Promise<ConnectionState> {
  connectionStates.set(connection.id, state);
  configureProviderForConnection(connection, state);

  client = state.client;
  provider = state.provider;

  setTimerConnectionFrom(connection);

  await vscode.commands.executeCommand('setContext', 'azureDevOpsInt.connected', true);
  await updateAuthStatusBar();

  if (options.refresh !== false && state.provider) {
    const fallbackQuery = getDefaultQuery(settings);
    const selectedQuery = getStoredQueryForConnection(connection.id, fallbackQuery);

    if (!initialRefreshedConnections.has(connection.id)) {
      initialRefreshedConnections.add(connection.id);
    }

    verbose('[ensureActiveConnection] triggering provider refresh', {
      id: connection.id,
      query: selectedQuery,
    });

    state.provider.refresh(selectedQuery);
  }

  const hadReminder = getPendingAuthReminderMap().has(connection.id);
  clearAuthReminder(connection.id);
  if ((state.authMethod ?? 'pat') === 'entra' || hadReminder) {
    dispatchApplicationEvent({ type: 'AUTHENTICATION_SUCCESS', connectionId: connection.id });
  }

  dispatchApplicationEvent({
    type: 'CONNECTION_ESTABLISHED',
    connectionId: connection.id,
    connectionState: state,
  });

  return state;
}

function getClientForConnectionInfo(info?: TimerConnectionInfo): AzureDevOpsIntClient | undefined {
  if (!info) {
    return client;
  }

  if (info.id) {
    const stateById = connectionStates.get(info.id);
    if (stateById?.client) {
      return stateById.client;
    }
  }

  if (info.organization && info.project) {
    for (const state of connectionStates.values()) {
      if (
        state.config.organization === info.organization &&
        state.config.project === info.project &&
        state.client
      ) {
        return state.client;
      }
    }
  }

  if (info.label) {
    const byLabel = connections.find((connection) => getConnectionLabel(connection) === info.label);
    if (byLabel) {
      const stateByLabel = connectionStates.get(byLabel.id);
      if (stateByLabel?.client) {
        return stateByLabel.client;
      }
    }
  }

  if (activeConnectionId) {
    const activeState = connectionStates.get(activeConnectionId);
    if (activeState?.client) {
      return activeState.client;
    }
  }

  return client;
}

async function loadConnectionsFromConfig(
  context: vscode.ExtensionContext
): Promise<ProjectConnection[]> {
  const settings = getConfig();
  const rawConnections = settings.get<unknown[]>(CONNECTIONS_CONFIG_KEY) ?? [];

  const legacyOrganization = String(settings.get<string>('organization') ?? '').trim();
  const legacyProject = String(settings.get<string>('project') ?? '').trim();
  const legacyTeam = String(settings.get<string>('team') ?? '').trim();

  const legacyFallback =
    legacyOrganization && legacyProject
      ? {
          organization: legacyOrganization,
          project: legacyProject,
          team: legacyTeam || undefined,
          label: undefined,
        }
      : undefined;

  const {
    connections: normalized,
    requiresSave,
    summary,
  } = normalizeConnections(rawConnections, legacyFallback);
  connections = normalized;

  if (requiresSave) {
    try {
      await settings.update(
        CONNECTIONS_CONFIG_KEY,
        normalized.map((entry) => ({ ...entry })),
        vscode.ConfigurationTarget.Global
      );
      verbose('[connections] Saved migrated connections', summary);
    } catch (error) {
      console.warn('[AzureDevOpsInt] [connections] Failed to save migrated connections', error);
    }
  }

  // Migrate any existing global PAT into per-connection secret keys so
  // credentials are always connection-scoped (no global PAT sharing).
  if (connections.length > 0) {
    try {
      await migrateGlobalPATToConnections(context, connections);
    } catch (error) {
      console.warn('[azureDevOpsInt] migrateGlobalPATToConnections failed', error);
    }
  }

  verbose('[connections] Loaded connections from config', {
    count: connections.length,
    ids: connections.map((c) => c.id),
  });

  const validIds = new Set(connections.map((item) => item.id));

  for (const [id, state] of connectionStates.entries()) {
    if (!validIds.has(id)) {
      connectionStates.delete(id);
      initialRefreshedConnections.delete(id);
      clearConnectionCaches(id);
    } else {
      const updated = connections.find((item) => item.id === id);
      if (updated) {
        state.config = updated;
      }
    }
  }

  for (const key of Array.from(activeQueryByConnection.keys())) {
    if (!validIds.has(key)) {
      activeQueryByConnection.delete(key);
    }
  }

  const persistedActive = context.globalState.get<string>(ACTIVE_CONNECTION_STATE_KEY);
  const { activeConnectionId: resolvedActiveId, requiresPersistence } = resolveActiveConnectionId(
    persistedActive,
    connections
  );

  activeConnectionId = resolvedActiveId;

  if (requiresPersistence) {
    await context.globalState.update(ACTIVE_CONNECTION_STATE_KEY, resolvedActiveId);
  }

  // Set the readers for the bridge *after* connections are loaded
  setLoadedConnectionsReader(() => connections);
  setActiveConnectionIdReader(() => activeConnectionId);

  // Initialize PAT retrieval bridge function
  setGetSecretPAT(async (extensionContext: vscode.ExtensionContext, connectionId?: string) => {
    if (!connectionId) {
      return undefined;
    }

    // Find the connection to get its patKey
    const connection = connections.find((c) => c.id === connectionId);
    if (!connection || !connection.patKey) {
      return undefined;
    }

    // Retrieve PAT from secrets using the connection's patKey
    return await extensionContext.secrets.get(connection.patKey);
  });

  notifyConnectionsList();
  return connections;
}

async function saveConnectionsToConfig(
  context: vscode.ExtensionContext,
  nextConnections: ProjectConnection[]
): Promise<void> {
  const settings = getConfig();
  connections = nextConnections;
  const serialized = nextConnections.map((entry) => ({ ...entry }));
  await settings.update(CONNECTIONS_CONFIG_KEY, serialized, vscode.ConfigurationTarget.Global);

  const validIds = new Set(nextConnections.map((item) => item.id));
  for (const [id, state] of connectionStates.entries()) {
    if (!validIds.has(id)) {
      connectionStates.delete(id);
      initialRefreshedConnections.delete(id);
      clearConnectionCaches(id);
    } else {
      const updated = nextConnections.find((item) => item.id === id);
      if (updated) state.config = updated;
    }
  }

  if (activeConnectionId && !validIds.has(activeConnectionId)) {
    activeConnectionId = nextConnections[0]?.id;
    await context.globalState.update(ACTIVE_CONNECTION_STATE_KEY, activeConnectionId);
  }

  notifyConnectionsList();
}

async function ensureConnectionsInitialized(context: vscode.ExtensionContext) {
  if (connections.length === 0) await loadConnectionsFromConfig(context);
  return connections;
}

/**
 * LEGACY AUTH REMOVED - Device code callback no longer used in FSM architecture
 * Device code callback for Entra ID authentication
 * Shows VS Code notification with device code and verification URL
 */
const createDeviceCodeCallback =
  (_context: vscode.ExtensionContext, connection?: ProjectConnection): any =>
  async (_deviceCode: any, userCode: any, verificationUrl: any, expiresIn: any) => {
    verbose('[EntraAuth] Device code received', { userCode, verificationUrl, expiresIn });

    const connectionLabel = connection ? describeConnection(connection) : 'Microsoft Entra ID';
    const action = await vscode.window.showInformationMessage(
      `Sign in to ${connectionLabel} with Microsoft Entra ID. Selecting Open Browser copies the code to your clipboard automatically.\n\nGo to ${verificationUrl} and enter code:\n\n${userCode}\n\nCode expires in ${Math.floor(expiresIn / 60)} minutes.`,
      { modal: false },
      'Open Browser'
    );

    if (action === 'Open Browser') {
      try {
        await vscode.env.clipboard.writeText(userCode);
      } catch (error) {
        console.warn('[AzureDevOpsInt] [EntraAuth] Failed to copy device code to clipboard', error);
      }
      await vscode.env.openExternal(vscode.Uri.parse(verificationUrl));
      vscode.window.showInformationMessage(
        `Device code ${userCode} copied to clipboard. Paste it into the browser to finish signing in.`
      );
    }
  };

function ensureTimer(context: vscode.ExtensionContext) {
  if (timer) return timer;
  const config = getConfig();
  let pomodoroBreakTimeout: NodeJS.Timeout | undefined;
  timer = new WorkItemTimer({
    autoResumeOnActivity: config.get<boolean>('autoResumeOnActivity') ?? true,
    inactivityTimeoutSec: config.get<number>('timerInactivityTimeout') ?? 300,
    defaultElapsedLimitHours: config.get<number>('defaultElapsedLimitHours') ?? 3.5,
    pomodoroEnabled: config.get<boolean>('pomodoroEnabled') ?? false,
    breakPrompt: () => {
      const snap = timer?.snapshot?.();
      if (!snap || !snap.isPaused) return;
      vscode.window
        .showInformationMessage(
          'Time for a Pomodoro break?',
          { modal: true },
          'Start 5min break',
          'Skip'
        )
        .then((choice) => {
          if (!choice || choice === 'Skip') return;
          try {
            timer?.pause();
          } catch {
            /* ignore pause error */
          }
          if (pomodoroBreakTimeout) {
            try {
              clearTimeout(pomodoroBreakTimeout);
            } catch {
              /* ignore clear error */
            }
            pomodoroBreakTimeout = undefined;
          }
          pomodoroBreakTimeout = setTimeout(
            () => {
              try {
                timer?.resume();
              } catch {
                /* ignore resume error */
              }
              pomodoroBreakTimeout = undefined;
            },
            5 * 60 * 1000
          );
        })
        .then(
          () => {},
          (error) => {
            console.error(
              '[AzureDevOpsInt]  [TIMER] Failed to show Pomodoro break dialog:',
              error
            );
          }
        );
    },
    persist: (data: { state?: any; timeEntries?: any[]; updateLastSave?: boolean }) =>
      persistTimer(context, data),
    restorePersisted: () => restoreTimer(context),
    onState: (s: any) => {
      sendToWebview({
        type: 'timerUpdate',
        timer: s,
        connectionId: timerConnectionInfo.id,
        connectionLabel: timerConnectionInfo.label,
        connectionOrganization: timerConnectionInfo.organization,
        connectionProject: timerConnectionInfo.project,
      });
      updateTimerContext(s);
    },
    onInfo: (m: any) => verbose('[timer]', m),
    onWarn: (m: any) => console.warn('[AzureDevOpsInt] [timer]', m),
    onError: (m: any) => console.error('[AzureDevOpsInt] [timer]', m),
  });
  timer.loadFromPersisted();
  return timer;
}

function updateTimerContext(state: any) {
  vscode.commands.executeCommand('setContext', 'azureDevOpsInt.timerActive', state?.isActive);
  vscode.commands.executeCommand('setContext', 'azureDevOpsInt.timerPaused', state?.isPaused);
}

async function persistTimer(
  context: vscode.ExtensionContext,
  data: { state?: any; timeEntries?: any[]; updateLastSave?: boolean }
) {
  if (data.state) await context.globalState.update(STATE_TIMER, data.state);
  if (data.timeEntries) await context.globalState.update(STATE_TIME_ENTRIES, data.timeEntries);
  if (data.updateLastSave) await context.globalState.update(STATE_LAST_SAVE, Date.now());
}

function restoreTimer(context: vscode.ExtensionContext): {
  state: WorkItemTimerState | undefined;
  timeEntries: TimeEntry[] | undefined;
  lastSave: number | undefined;
  connection: TimerConnectionInfo | undefined;
} {
  return {
    state: context.globalState.get(STATE_TIMER),
    timeEntries: context.globalState.get(STATE_TIME_ENTRIES),
    lastSave: context.globalState.get(STATE_LAST_SAVE),
    connection: context.globalState.get(STATE_TIMER_CONNECTION),
  };
}

// Enable a minimal smoke-test mode for CI/integration tests to avoid heavy initialization
// Primary signal: VSCODE_INTEGRATION_SMOKE=1
// Fallback signal: VS Code test runner is present (extensionTestsPath arg) which indicates an integration test session.
function isVSCodeTestRun(): boolean {
  try {
    return (process.argv || []).some(
      (a) =>
        typeof a === 'string' &&
        (a.includes('--extensionTestsPath') || a.includes('integration-tests'))
    );
  } catch {
    return false;
  }
}
const IS_SMOKE = process.env.VSCODE_INTEGRATION_SMOKE === '1' || isVSCodeTestRun();

function getConfig() {
  return vscode.workspace.getConfiguration(CONFIG_NS);
}

type ConfigInspection<T> = {
  key: string;
  defaultValue?: T;
  globalValue?: T;
  workspaceValue?: T;
  workspaceFolderValue?: T;
  globalLanguageValue?: T;
  workspaceLanguageValue?: T;
  workspaceFolderLanguageValue?: T;
};

type SendToWebviewOptions = {
  panel?: vscode.WebviewView;
  logger?: LoggerFn;
};

type SendWorkItemsSnapshotOptions = Omit<PostWorkItemsSnapshotParams, 'panel' | 'logger'> &
  SendToWebviewOptions;

function sendToWebview(message: any): void {
  const messageType = message?.type;

  if (messageType === 'workItemsLoaded') {
    const items = Array.isArray(message.workItems) ? [...message.workItems] : [];
    console.log('[AzureDevOpsInt][sendToWebview] Processing workItemsLoaded:', {
      messageType,
      hasWorkItems: !!message.workItems,
      workItemsIsArray: Array.isArray(message.workItems),
      itemsCount: items.length,
      connectionId: message.connectionId,
    });

    dispatchApplicationEvent({
      type: 'WORK_ITEMS_LOADED',
      workItems: items,
      connectionId: typeof message.connectionId === 'string' ? message.connectionId : undefined,
      query: typeof message.query === 'string' ? message.query : undefined,
      kanbanView: !!message.kanbanView,
      types: Array.isArray(message.types) ? [...message.types] : undefined,
    });

    console.log('[AzureDevOpsInt][sendToWebview] Dispatched WORK_ITEMS_LOADED event to FSM');
  }

  if (!panel) {
    verbose?.('[sendToWebview] dropping message (no panel)', { type: messageType });
    return;
  }

  try {
    panel.webview.postMessage(message);
  } catch (error) {
    verbose?.(
      '[sendToWebview] failed to post message',
      error instanceof Error ? error.message : error
    );
  }
}

function forwardProviderMessage(connectionId: string, message: unknown) {
  // Forward provider messages directly, not wrapped in envelope
  // This allows sendToWebview to recognize workItemsLoaded and other message types
  console.log('[AzureDevOpsInt][forwardProviderMessage] Received from provider:', {
    connectionId,
    messageType: (message as any)?.type,
    hasWorkItems: !!(message as any)?.workItems,
    workItemsCount: Array.isArray((message as any)?.workItems)
      ? (message as any).workItems.length
      : 'n/a',
  });

  if (message && typeof message === 'object' && 'type' in message) {
    sendToWebview({
      ...(message as any),
      connectionId,
    });
  } else {
    // Fallback for messages without type
    sendToWebview({
      type: 'providerMessage',
      connectionId,
      message,
    });
  }
}

function resolveSnapshotTypes(
  providerRef: WorkItemsProvider | undefined,
  explicit: string[] | undefined,
  logger: LoggerFn | undefined
): string[] | undefined {
  if (Array.isArray(explicit)) {
    return [...explicit];
  }
  if (!providerRef || typeof (providerRef as any).getWorkItemTypeOptions !== 'function') {
    return undefined;
  }
  try {
    const raw = (providerRef as any).getWorkItemTypeOptions();
    return Array.isArray(raw) ? [...raw] : undefined;
  } catch (error) {
    logger?.(
      '[sendWorkItemsSnapshot] failed to read provider type options',
      error instanceof Error ? error.message : error
    );
    return undefined;
  }
}

function resolveBranchContextPayload(
  connectionId: string | undefined,
  explicit: PostWorkItemsSnapshotParams['branchContext']
): PostWorkItemsSnapshotParams['branchContext'] {
  if (explicit !== undefined) {
    return explicit;
  }
  if (!connectionId) {
    return null;
  }
  const enrichment = getBranchEnrichmentState(connectionId);
  return enrichment?.context ?? null;
}

function enrichWorkItems(
  items: any[],
  connectionId: string | undefined,
  branchContext: PostWorkItemsSnapshotParams['branchContext']
): any[] {
  if (!Array.isArray(items) || items.length === 0) {
    return [];
  }
  return items.map((item) => ({ ...item, connectionId, branchContext }));
}

function sendWorkItemsSnapshot(options: SendWorkItemsSnapshotOptions): void {
  const connectionId = options.connectionId ?? activeConnectionId;
  const items = Array.isArray(options.items) ? options.items : [];
  const branchContext = resolveBranchContextPayload(connectionId, options.branchContext);
  const types = resolveSnapshotTypes(options.provider, options.types, verbose);

  sendToWebview({
    type: 'workItemsLoaded',
    connectionId,
    workItems: items,
    kanbanView: options.kanbanView,
    provider: options.provider,
    types,
    query: options.query,
    branchContext,
  });

  const enrichedWorkItems = enrichWorkItems(items, connectionId, branchContext);
  dispatchApplicationEvent({
    type: 'WORK_ITEMS_LOADED',
    workItems: enrichedWorkItems,
    connectionId,
    query: options.query,
    kanbanView: !!options.kanbanView,
    types,
  });
}

function hasConfigOverride<T>(metadata: ConfigInspection<T> | undefined): boolean {
  if (!metadata) return false;
  const overrideValues = [
    metadata.globalValue,
    metadata.workspaceValue,
    metadata.workspaceFolderValue,
    metadata.globalLanguageValue,
    metadata.workspaceLanguageValue,
    metadata.workspaceFolderLanguageValue,
  ];
  return overrideValues.some((value) => value !== undefined);
}

export function resolveDefaultQuery(_config: vscode.WorkspaceConfiguration): string {
  const getValue = (key: string): string | undefined => {
    try {
      const raw = _config?.get<string | undefined>(key);
      if (typeof raw !== 'string') return undefined;
      const trimmed = raw.trim();
      return trimmed.length > 0 ? trimmed : undefined;
    } catch {
      return undefined;
    }
  };

  const explicitDefault = getValue('defaultQuery');
  if (explicitDefault) return explicitDefault;

  const workItemQuery = getValue('workItemQuery');
  if (workItemQuery) {
    const inspectFn =
      typeof _config?.inspect === 'function' ? _config.inspect.bind(_config) : undefined;
    if (!inspectFn) return workItemQuery;
    try {
      const metadata = inspectFn<string>('workItemQuery');
      if (hasConfigOverride(metadata)) return workItemQuery;
    } catch {
      // If inspect fails, assume the user intentionally set a custom query to avoid breaking legacy behavior.
      return workItemQuery;
    }
  }

  return DEFAULT_QUERY;
}

function getDefaultQuery(config?: vscode.WorkspaceConfiguration): string {
  const target = config ?? getConfig();
  return resolveDefaultQuery(target);
}

async function setOpenAIApiKey(context: vscode.ExtensionContext) {
  const apiKey = await vscode.window.showInputBox({
    prompt: 'Enter your OpenAI API Key',
    password: true,
  });
  if (apiKey) {
    await context.secrets.store(OPENAI_SECRET_KEY, apiKey);
    vscode.window.showInformationMessage('OpenAI API Key saved successfully.');
    openAiClient = new OpenAI({ apiKey });
  }
}

function getExtensionVersion(context: vscode.ExtensionContext): string {
  if (cachedExtensionVersion) return cachedExtensionVersion;
  try {
    const pkgPath = path.join(context.extensionPath, 'package.json');
    const pkgRaw = fs.readFileSync(pkgPath, 'utf8');
    const pkg = JSON.parse(pkgRaw);
    cachedExtensionVersion = String(pkg.version || 'dev');
  } catch {
    // Swallow errors and fall back to 'dev'
    cachedExtensionVersion = 'dev';
  }
  return cachedExtensionVersion;
}
async function migrateLegacyConfigIfNeeded() {
  try {
    const legacy = vscode.workspace.getConfiguration(LEGACY_CONFIG_NS);
    const target = getConfig();
    const legacyOrg = legacy.get<string>('organization');
    const legacyProj = legacy.get<string>('project');
    if (legacyOrg && !target.get('organization'))
      await target.update('organization', legacyOrg, vscode.ConfigurationTarget.Global);
    if (legacyProj && !target.get('project'))
      await target.update('project', legacyProj, vscode.ConfigurationTarget.Global);
  } catch (e) {
    console.warn('[azureDevOpsInt] migrateLegacyConfigIfNeeded failed', e);
  }
}

/**
 * Apply startup patches to fix user settings and configuration issues.
 * This system automatically resolves problems without requiring manual user intervention.
 *
 * Each patch has a version identifier to ensure it only runs once per user.
 * Add new patches here for future bug fixes that need to modify user settings.
 *
 * @param context VS Code extension context for accessing settings and state
 */
async function applyStartupPatches(context: vscode.ExtensionContext): Promise<void> {
  try {
    const patchState = context.globalState;
    const PATCH_VERSION_KEY = 'azureDevOpsInt.appliedPatches';
    const appliedPatches = patchState.get<string[]>(PATCH_VERSION_KEY, []);

    // Patch 1.0.0-clientid-removal: Remove clientId from connection configurations
    // This fixes authentication issues caused by storing incorrect client IDs per connection
    if (!appliedPatches.includes('1.0.0-clientid-removal')) {
      await applyClientIdRemovalPatch();
      appliedPatches.push('1.0.0-clientid-removal');
      await patchState.update(PATCH_VERSION_KEY, appliedPatches);
      console.log('[azureDevOpsInt] Applied patch: 1.0.0-clientid-removal');
    }

    // Future patches can be added here following the same pattern:
    // if (!appliedPatches.includes('1.1.0-some-other-fix')) {
    //   await applySomeOtherPatch();
    //   appliedPatches.push('1.1.0-some-other-fix');
    //   await patchState.update(PATCH_VERSION_KEY, appliedPatches);
    //   console.log('[azureDevOpsInt] Applied patch: 1.1.0-some-other-fix');
    // }
  } catch (error) {
    console.warn('[azureDevOpsInt] Failed to apply startup patches:', error);
    // Don't fail activation if patches fail - log and continue
  }
}

/**
 * Patch to remove clientId from connection configurations.
 *
 * PROBLEM: Earlier versions allowed users to configure clientId per connection,
 * which caused authentication failures when users stored incorrect client IDs.
 *
 * SOLUTION: Remove all clientId fields from stored connections. The extension
 * now hardcodes the correct Azure DevOps client ID (872cd9fa-d31f-45e0-9eab-6e460a02d1f1)
 * in the authentication code, preventing user configuration errors.
 */
async function applyClientIdRemovalPatch(): Promise<void> {
  try {
    const config = vscode.workspace.getConfiguration('azureDevOpsIntegration');
    const connections = config.get<ProjectConnection[]>('connections', []);

    let patchedCount = 0;
    const patchedConnections = connections.map((conn) => {
      if ('clientId' in conn) {
        patchedCount++;
        const { clientId, ...connWithoutClientId } = conn as any;
        return connWithoutClientId;
      }
      return conn;
    });

    if (patchedCount > 0) {
      await config.update('connections', patchedConnections, vscode.ConfigurationTarget.Global);
      console.log(
        `[azureDevOpsInt] Startup patch: Removed clientId from ${patchedCount} connection(s)`
      );
    }
  } catch (error) {
    console.warn('[azureDevOpsInt] Client ID removal patch failed:', error);
    throw error; // Re-throw to be caught by applyStartupPatches
  }
}

function getNonce() {
  let text = '';
  const possible = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  for (let i = 0; i < 32; i++) {
    text += possible.charAt(Math.floor(Math.random() * possible.length));
  }
  return text;
}

export async function activate(context: vscode.ExtensionContext) {
  // Store reference to our rejection handler for proper cleanup
  rejectionHandler = (reason: any, promise: Promise<any>) => {
    // Only handle rejections that originate from our extension
    const isFromOurExtension =
      reason &&
      typeof reason === 'object' &&
      'stack' in reason &&
      typeof reason.stack === 'string' &&
      (reason.stack.includes('azuredevops-integration-extension') ||
        reason.stack.includes('azureDevOpsInt') ||
        reason.stack.includes('activation.ts') ||
        reason.stack.includes('src\\fsm\\') ||
        reason.stack.includes('src/fsm/'));

    // Skip rejections from other extensions
    if (!isFromOurExtension) {
      return;
    }

    // Suppress cancellation errors which are normal during VS Code shutdown
    if (reason && typeof reason === 'object') {
      // Check for cancellation by name, message, or stack trace content
      const isCancellation =
        ('name' in reason && reason.name === 'Canceled') ||
        ('message' in reason && reason.message === 'Canceled') ||
        (reason.toString && reason.toString().includes('Canceled')) ||
        ('stack' in reason &&
          typeof reason.stack === 'string' &&
          reason.stack.includes('Canceled'));

      if (isCancellation) {
        verbose('[azureDevOpsInt] Promise cancelled during shutdown (normal)');
        return;
      }
    }
    console.error('[azureDevOpsInt] Unhandled Promise Rejection:', reason);
    console.error('[azureDevOpsInt] Promise:', promise);
  };

  // Add global unhandled promise rejection handler
  process.on('unhandledRejection', rejectionHandler);

  // Add global uncaught exception handler
  process.on('uncaughtException', (error) => {
    console.error('[azureDevOpsInt] Uncaught Exception:', error);
  });

  extensionContextRef = context;
  setExtensionContextRefBridge(context);

  // Apply startup patches to fix user settings and configuration issues
  await applyStartupPatches(context);

  // In smoke mode (integration tests), minimize activation work to avoid any potential stalls.
  if (IS_SMOKE) {
    try {
      vscode.commands.executeCommand('setContext', 'azureDevOpsInt.connected', false).then(
        () => {},
        () => {}
      );
    } catch {
      /* ignore */
    }
    // Do not register views/commands or initialize any domain objects in smoke mode.
    return undefined;
  }
  const cfg = getConfig();
  if (cfg.get<boolean>('debugLogging')) {
    const channel =
      getOutputChannel() ?? vscode.window.createOutputChannel('Azure DevOps Integration');
    setOutputChannel(channel);
    logLine('[activate] Debug logging enabled');
    // Bridge console logging to Output Channel for better debugging
    bridgeConsoleToOutputChannel();
  }
  // Status bar (hidden until connected or timer active)
  statusBarItem = vscode.window.createStatusBarItem(
    'azureDevOpsInt.timer',
    vscode.StatusBarAlignment.Left,
    100
  );
  statusBarItem.command = 'azureDevOpsInt.stopTimer';
  context.subscriptions.push(statusBarItem);

  // Auth status bar item (shows Entra ID token status)
  authStatusBarItem = vscode.window.createStatusBarItem(
    'azureDevOpsInt.authStatus',
    vscode.StatusBarAlignment.Left,
    99
  );
  authStatusBarItem.command = 'azureDevOpsInt.signInWithEntra';
  context.subscriptions.push(authStatusBarItem);
  authStatusBarItem.hide(); // Hidden by default, shown only for Entra ID connections

  // LEGACY AUTH REMOVED - EntraAuthenticationProvider replaced by FSM authentication
  const authenticationProviderOptions: vscode.AuthenticationProviderOptions & {
    supportsAccountManagement?: boolean;
  } = {
    supportsMultipleAccounts: true,
    supportsAccountManagement: true,
  };
  // LEGACY AUTH REMOVED - Authentication provider registration replaced by FSM authentication

  // Ensure applicationStore is initialized before registering webview provider
  // This prevents race conditions where the webview panel resolves before the FSM actor is available
  verbose('[activation] Ensuring application store is initialized before webview registration');
  await ensureSharedContextBridge(context);
  verbose('[activation] Application store initialized, FSM actor available');

  // Register the work items webview view resolver (guard against duplicate registration)
  if (!viewProviderRegistered) {
    verbose('[azureDevOpsInt] Registering webview view provider: azureDevOpsWorkItems');
    context.subscriptions.push(
      vscode.window.registerWebviewViewProvider(
        'azureDevOpsWorkItems',
        new AzureDevOpsIntViewProvider(context),
        { webviewOptions: { retainContextWhenHidden: true } }
      )
    );
    viewProviderRegistered = true;
  }
  // Register FSM trace commands for full replay capability
  registerTraceCommands(context);

  // Register quick debug commands for instant troubleshooting
  registerQuickDebugCommands(context);
  verbose('[ACTIVATION] Quick debug commands registered');

  // Register output channel reader for programmatic log access
  import('./fsm/commands/outputChannelReader.js')
    .then(({ registerOutputChannelReader }) => {
      registerOutputChannelReader(context);
      verbose('[ACTIVATION] Output channel reader registered for automated debugging');
    })
    .catch((error) => {
      console.error(
        '[AzureDevOpsInt]  [ACTIVATION] Failed to import output channel reader:',
        error
      );
    });

  // AUTO-START FSM TRACING AND SHOW OUTPUT FOR DEBUGGING
  verbose('[ACTIVATION] Starting FSM tracing session automatically...');

  // Import FSM tracing modules
  import('./fsm/logging/FSMTracer.js')
    .then(({ startTraceSession, fsmTracer }) => {
      try {
        const sessionId = startTraceSession('Extension Activation - Auto Debug Session');
        verbose(`[ACTIVATION] FSM tracing started: ${sessionId}`);

        // Show the FSM output channel immediately for visibility
        import('./fsm/logging/FSMLogger.js')
          .then(({ fsmLogger, FSMComponent }) => {
            fsmLogger.showOutputChannel();
            verbose('[ACTIVATION] FSM Output Channel opened for debugging visibility');

            // Log activation start
            fsmLogger.info(FSMComponent.APPLICATION, 'Extension activation started', {
              component: FSMComponent.APPLICATION,
              event: 'ACTIVATE',
              state: 'activating',
            });
          })
          .catch((error) => {
            console.error(
              '[AzureDevOpsInt]  [ACTIVATION] Failed to import FSM logger for activation:',
              error
            );
          });
      } catch (error) {
        console.error('[AzureDevOpsInt]  [ACTIVATION] Failed to start FSM tracing:', error);
      }
    })
    .catch((error) => {
      console.error(
        '[AzureDevOpsInt]  [ACTIVATION] Failed to import FSM tracing modules:',
        error
      );
    });

  // FSM and Bridge setup
  const fsmSetupService = new FSMSetupService(context);

  const appActor = getApplicationStoreActor();

  // Activate the FSM with extension context
  if (appActor && typeof (appActor as any).send === 'function') {
    verbose('[activation] Sending ACTIVATE event to FSM');
    (appActor as any).send({ type: 'ACTIVATE', context });
  }

  if (appActor && typeof (appActor as any).subscribe === 'function') {
    (appActor as any).subscribe((snapshot: any) => {
      if (panel && snapshot) {
        // Pre-compute all state matches since snapshot.matches() doesn't survive JSON serialization
        const matches = {
          // Top-level states
          inactive: snapshot.matches('inactive'),
          activating: snapshot.matches('activating'),
          activation_failed: snapshot.matches('activation_failed'),
          active: snapshot.matches('active'),
          error_recovery: snapshot.matches('error_recovery'),
          deactivating: snapshot.matches('deactivating'),

          // Active sub-states
          'active.setup': snapshot.matches({ active: 'setup' }),
          'active.setup.loading_connections': snapshot.matches({
            active: { setup: 'loading_connections' },
          }),
          'active.setup.waiting_for_panel': snapshot.matches({
            active: { setup: 'waiting_for_panel' },
          }),
          'active.setup.panel_ready': snapshot.matches({ active: { setup: 'panel_ready' } }),
          'active.setup.setup_error': snapshot.matches({ active: { setup: 'setup_error' } }),

          // Active.ready sub-states
          'active.ready': snapshot.matches({ active: 'ready' }),
          'active.ready.idle': snapshot.matches({ active: { ready: 'idle' } }),
          'active.ready.loadingData': snapshot.matches({ active: { ready: 'loadingData' } }),
          'active.ready.managingConnections': snapshot.matches({
            active: { ready: 'managingConnections' },
          }),
          'active.ready.error': snapshot.matches({ active: { ready: 'error' } }),
        };

        const serializableState = {
          fsmState: snapshot.value,
          context: getSerializableContext(snapshot.context),
          matches, // Include pre-computed state matches
        };

        // Reduce excessive logging - only log state changes if debug logging is enabled
        if (shouldLogDebug()) {
          console.log('[AzureDevOpsInt][activation] Sending state to webview:', {
            value: snapshot.value,
            matchesActive: matches.active,
            matchesActiveReady: matches['active.ready'],
            matchesActivating: matches.activating,
          });
        }

        panel.webview.postMessage({
          type: 'syncState',
          payload: serializableState,
        });

        // If activeQuery changed, persist and trigger provider refresh
        try {
          const snapCtx = snapshot.context;
          const newQuery: string | undefined = snapCtx?.activeQuery;
          const newConn: string | undefined = snapCtx?.activeConnectionId;
          if (newConn && typeof newQuery === 'string') {
            const changed =
              newConn !== lastQueriedActiveConnectionId || newQuery !== lastQueriedQuery;
            if (changed) {
              // Persist query in per-connection store
              setStoredQueryForConnection(newConn, newQuery);
              // Ensure provider exists before refresh
              if (!provider && extensionContextRef) {
                ensureActiveConnection(extensionContextRef, newConn, { refresh: false }).catch(
                  () => {}
                );
              }
              try {
                provider?.refresh(newQuery);
              } catch (e) {
                verbose('[activation] provider.refresh failed after query change', e);
              }
              lastQueriedActiveConnectionId = newConn;
              lastQueriedQuery = newQuery;
            }
          }
        } catch (e) {
          verbose('[activation] Failed handling activeQuery change', e);
        }
        // Update VS Code context key for debug view visibility so menus can react
        try {
          vscode.commands
            .executeCommand(
              'setContext',
              'azureDevOpsInt.debugViewVisible',
              !!snapshot.context?.debugViewVisible
            )
            .then(
              () => {},
              () => {}
            );
        } catch {
          /* ignore context key errors */
        }
      }
    });
  }

  // Core commands - using extracted commands module
  const commandContext = {
    context,
    panel,
    provider,
    timer,
    sessionTelemetry,
    client,
    statusBarItem,
    authStatusBarItem,
  };

  const commandDisposables = registerCommands(context, commandContext);
  context.subscriptions.push(...commandDisposables);

  // Set up listeners and handlers
  context.subscriptions.push(
    vscode.workspace.onDidChangeConfiguration(async (e) => {
      if (e.affectsConfiguration(CONFIG_NS)) {
        await loadConnectionsFromConfig(context);
        // Don't block on connection refresh during config changes
        ensureActiveConnection(context, activeConnectionId, { refresh: true }).catch((error) => {
          verbose('[onDidChangeConfiguration] Connection refresh failed:', error);
        });
      }
    })
  );

  // The webview will initialize immediately and show auth UI when ready
  ensureActiveConnection(context, activeConnectionId, { refresh: true }).catch((error) => {
    verbose('[activation] Initial connection failed:', error);
  });

  // Start periodic cache cleanup
  startCacheCleanup();

  // Start memory optimizer
  const memoryOptimizer = new MemoryOptimizer();

  // Start periodic token refresh
  if (tokenRefreshInterval) clearInterval(tokenRefreshInterval);
  tokenRefreshInterval = setInterval(
    () => {
      for (const connectionId of connectionStates.keys()) {
        dispatchApplicationEvent({ type: 'REFRESH_TOKEN', connectionId });
      }
    },
    15 * 60 * 1000
  ); // every 15 minutes

  // Start periodic build status refresh
  for (const connectionId of connectionStates.keys()) {
    updateBuildRefreshTimer(connectionId, connectionStates.get(connectionId)?.provider, true);
  }

  // Start periodic garbage collection if enabled
  if (global.gc) {
    if (gcInterval) clearInterval(gcInterval);
    gcInterval = setInterval(
      () => {
        global.gc?.();
        verbose('[GC] Periodic garbage collection triggered');
      },
      5 * 60 * 1000
    ); // every 5 minutes
  }
}

export function deactivate(): Thenable<void> {
  isDeactivating = true;
  verbose('[deactivate] starting');

  // Stop periodic tasks
  stopCacheCleanup();
  for (const connectionId of connectionStates.keys()) {
    updateBuildRefreshTimer(connectionId, undefined, false);
  }
  if (tokenRefreshInterval) {
    clearInterval(tokenRefreshInterval);
    tokenRefreshInterval = undefined;
  }
  if (gcInterval) {
    clearInterval(gcInterval);
    gcInterval = undefined;
  }

  // Clear all caches and state
  connectionStates.clear();
  activeQueryByConnection.clear();

  dispatchApplicationEvent({ type: 'EXTENSION_DEACTIVATED' });
  verbose('[deactivate] complete');

  return Promise.resolve();
}

// Helper function to extract only serializable properties from FSM context
function getSerializableContext(context: any): Record<string, any> {
  if (!context) {
    return {};
  }

  // Debug: log what we're serializing
  // Only log context serialization if debug logging is enabled
  if (shouldLogDebug()) {
    console.log('[AzureDevOpsInt][getSerializableContext] Original context:', {
      hasConnections: !!context.connections,
      connectionsType: typeof context.connections,
      connectionsIsArray: Array.isArray(context.connections),
      connectionsValue: context.connections,
      activeConnectionId: context.activeConnectionId,
      isActivated: context.isActivated,
    });
  }

  // Extract only serializable properties, excluding VS Code API objects and actors
  const serialized = {
    isActivated: context.isActivated,
    isDeactivating: context.isDeactivating,
    connections: context.connections || [],
    activeConnectionId: context.activeConnectionId,
    activeQuery: context.activeQuery,
    connectionStates: context.connectionStates ? Object.fromEntries(context.connectionStates) : {},
    pendingAuthReminders: context.pendingAuthReminders
      ? Object.fromEntries(context.pendingAuthReminders)
      : {},
    pendingWorkItems: context.pendingWorkItems,
    workItems: context.pendingWorkItems?.workItems || [],
    timerState: context.timerState,
    lastError: context.lastError
      ? { message: context.lastError.message, stack: context.lastError.stack }
      : undefined,
    errorRecoveryAttempts: context.errorRecoveryAttempts,
    viewMode: context.viewMode,
    kanbanColumns: context.kanbanColumns,
    deviceCodeSession: context.deviceCodeSession
      ? {
          connectionId: context.deviceCodeSession.connectionId,
          userCode: context.deviceCodeSession.userCode,
          verificationUri: context.deviceCodeSession.verificationUri,
          startedAt: context.deviceCodeSession.startedAt,
          expiresAt: context.deviceCodeSession.expiresAt,
          expiresInSeconds: context.deviceCodeSession.expiresInSeconds,
          remainingMs: Math.max(context.deviceCodeSession.expiresAt - Date.now(), 0),
        }
      : undefined,
  };

  // Only log serialization if debug logging is enabled to prevent log spam
  if (shouldLogDebug()) {
    console.log('[AzureDevOpsInt][getSerializableContext] Serialized context:', {
      connectionsLength: serialized.connections.length,
      activeConnectionId: serialized.activeConnectionId,
      hasDeviceCodeSession: !!serialized.deviceCodeSession,
      hasPendingWorkItems: !!serialized.pendingWorkItems,
      workItemsCount: serialized.workItems?.length || 0,
      viewMode: serialized.viewMode,
    });
  }

  return serialized;
}

class AzureDevOpsIntViewProvider implements vscode.WebviewViewProvider {
  public view?: vscode.WebviewView;
  private readonly extensionUri: vscode.Uri;
  private readonly fsm: ReturnType<typeof getApplicationActor>;

  constructor(private readonly context: vscode.ExtensionContext) {
    this.extensionUri = context.extensionUri;
    this.fsm = getApplicationActor();
  }

  public resolveWebviewView(
    webviewView: vscode.WebviewView,
    context: vscode.WebviewViewResolveContext,
    _token: vscode.CancellationToken
  ) {
    this.view = webviewView;
    panel = webviewView; // Store in global for snapshot subscription
    const webview = webviewView.webview;
    webview.options = {
      enableScripts: true,
      localResourceRoots: [vscode.Uri.joinPath(this.extensionUri, 'media')],
    };

    const nonce = getNonce();
    const scriptUri = webview.asWebviewUri(
      vscode.Uri.joinPath(this.extensionUri, 'media', 'webview', 'main.js')
    );
    // Link to main.css which contains Svelte component styles from esbuild
    const mainCssUri = webview.asWebviewUri(
      vscode.Uri.joinPath(this.extensionUri, 'media', 'webview', 'main.css')
    );

    // The 'unsafe-eval' is required for Svelte's dev mode.
    // TODO: Remove 'unsafe-eval' in production builds.
    const csp = `default-src 'none'; style-src ${webview.cspSource} 'unsafe-inline'; script-src 'nonce-${nonce}' 'unsafe-eval'; img-src ${webview.cspSource} data:; connect-src 'self';`;

    webview.html = `
      <!DOCTYPE html>
      <html lang="en">
      <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <meta http-equiv="Content-Security-Policy" content="${csp}">
        <link href="${mainCssUri}" rel="stylesheet">
        <title>Work Items</title>
      </head>
      <body>
        <div id="svelte-root"></div>
        <script type="module" nonce="${nonce}" src="${scriptUri}"></script>
      </body>
      </html>
    `;

    // Set up message handler to receive events from webview
    webview.onDidReceiveMessage((message) => {
      if (message.type === 'fsmEvent' && message.event) {
        // Forward webview events to the FSM
        console.log('[AzureDevOpsIntViewProvider] Received event from webview:', {
          eventType: message.event.type,
          event: message.event,
        });
        dispatchApplicationEvent(message.event);
      }
    });

    // Notify FSM that webview panel is ready
    this.fsm?.send?.({ type: 'UPDATE_WEBVIEW_PANEL', webviewPanel: webviewView });

    // Send initial FSM state to webview
    const appActor = getApplicationStoreActor();
    console.log('[AzureDevOpsIntViewProvider] Attempting to send initial state:', {
      hasActor: !!appActor,
      hasGetSnapshot: typeof (appActor as any)?.getSnapshot === 'function',
    });
    if (appActor && typeof (appActor as any).getSnapshot === 'function') {
      const snapshot = (appActor as any).getSnapshot();
      console.log('[AzureDevOpsIntViewProvider] Got snapshot:', {
        hasSnapshot: !!snapshot,
        value: snapshot?.value,
      });
      if (snapshot) {
        // Pre-compute state matches
        const matches = {
          inactive: snapshot.matches('inactive'),
          activating: snapshot.matches('activating'),
          activation_failed: snapshot.matches('activation_failed'),
          active: snapshot.matches('active'),
          error_recovery: snapshot.matches('error_recovery'),
          deactivating: snapshot.matches('deactivating'),
          'active.setup': snapshot.matches({ active: 'setup' }),
          'active.setup.loading_connections': snapshot.matches({
            active: { setup: 'loading_connections' },
          }),
          'active.setup.waiting_for_panel': snapshot.matches({
            active: { setup: 'waiting_for_panel' },
          }),
          'active.setup.panel_ready': snapshot.matches({ active: { setup: 'panel_ready' } }),
          'active.setup.setup_error': snapshot.matches({ active: { setup: 'setup_error' } }),
          'active.ready': snapshot.matches({ active: 'ready' }),
          'active.ready.idle': snapshot.matches({ active: { ready: 'idle' } }),
          'active.ready.loadingData': snapshot.matches({ active: { ready: 'loadingData' } }),
          'active.ready.managingConnections': snapshot.matches({
            active: { ready: 'managingConnections' },
          }),
          'active.ready.error': snapshot.matches({ active: { ready: 'error' } }),
        };

        const serializableState = {
          fsmState: snapshot.value,
          context: getSerializableContext(snapshot.context),
          matches,
        };
        console.log('[AzureDevOpsIntViewProvider] Posting initial syncState message with matches');
        webview.postMessage({
          type: 'syncState',
          payload: serializableState,
        });
      }
    }
  }
}

async function diagnoseWorkItemsIssue(context: vscode.ExtensionContext) {
  const log = (message: string) => getOutputChannel()?.appendLine(`[DIAGNOSTIC] ${message}`);
  log('Starting work items diagnostic...');

  const config = getConfig();
  const connections = config.get<ProjectConnection[]>('connections', []);
  const activeId = context.globalState.get<string>(ACTIVE_CONNECTION_STATE_KEY);

  log(`Found ${connections.length} connections.`);
  log(`Active connection ID: ${activeId}`);

  if (!activeId) {
    log('No active connection. Aborting.');
    getOutputChannel()?.show();
    return;
  }

  const activeConn = connections.find((c) => c.id === activeId);
  if (!activeConn) {
    log('Active connection not found in config. Aborting.');
    getOutputChannel()?.show();
    return;
  }

  log(`Active connection: ${activeConn.label} (${activeConn.organization}/${activeConn.project})`);

  const fsm = getApplicationActor();
  if (!fsm) {
    log('FSM actor not available. Aborting.');
    return;
  }

  const snapshot = fsm.getSnapshot?.();
  if (!snapshot) {
    log('FSM snapshot not available. Aborting.');
    return;
  }
  log('FSM state: ' + snapshot.value);

  const webviewReady = snapshot.context.flags.isWebviewReady;
  log('Webview ready state: ' + webviewReady);

  if (!webviewReady) {
    log('Webview is not ready. Cannot proceed with full diagnostic.');
  }

  log('Diagnostic complete.');
  getOutputChannel()?.show();
}

import { WorkItemTimerState, TimeEntry } from './types';

export interface TimerPersistPayload {
  state?: WorkItemTimerState;
  timeEntries?: TimeEntry[];
  updateLastSave?: boolean;
  defaultElapsedLimitHours?: number;
}
export interface TimerOptions {
  onState?: (_s: WorkItemTimerState | undefined) => void;
  onInfo?: (_m: string) => void;
  onWarn?: (_m: string) => void;
  onError?: (_m: string) => void;
  now?: () => number;
  persist?: (data: TimerPersistPayload) => void;
  restorePersisted?: () => { state?: WorkItemTimerState; timeEntries?: TimeEntry[] };
  autoResumeOnActivity?: boolean;
  inactivityTimeoutSec?: number;
  pomodoroEnabled?: boolean;
  breakPrompt?: (resumeCb: () => void) => void;
  inactivityCheckMs?: number; // optional: frequency for inactivity checks (testable)
  defaultElapsedLimitHours?: number; // cap elapsed time on stop if left running
}

export class WorkItemTimer {
  private onState: (_s: WorkItemTimerState | undefined) => void;
  private onInfo: (_m: string) => void;
  private onWarn: (_m: string) => void;
  private onError: (_m: string) => void;
  private now: () => number;
  private persist: (data: TimerPersistPayload) => void;
  private restorePersisted: () => { state?: WorkItemTimerState; timeEntries?: TimeEntry[] };
  private autoResumeOnActivity: boolean;
  private inactivityTimeoutSec: number;
  private pomodoroEnabled: boolean;
  private breakPrompt: (resumeCb: () => void) => void;

  private _tickIntervalMs = 1000;
  private _inactivityCheckMs = 10000;
  private defaultElapsedLimitHours = 3.5; // hours
  private _state: WorkItemTimerState | undefined;
  private _ticker: any;
  private _inactivityTicker: any;
  private _lastActivity: number;
  private _timeEntries: TimeEntry[] = [];

  constructor(options: TimerOptions = {}) {
    this.onState = options.onState || (() => {});
    this.onInfo = options.onInfo || (() => {});
    this.onWarn = options.onWarn || (() => {});
    this.onError = options.onError || (() => {});
    this.now = options.now || (() => Date.now());
    this.persist = options.persist || (() => {});
    this.restorePersisted = options.restorePersisted || (() => ({}));
    this.autoResumeOnActivity = options.autoResumeOnActivity ?? true;
    this.inactivityTimeoutSec = options.inactivityTimeoutSec ?? 300;
    // Allow tests to override how frequently inactivity is checked
    this._inactivityCheckMs = options.inactivityCheckMs ?? 10000;
    this.pomodoroEnabled = options.pomodoroEnabled ?? false;
    this.breakPrompt = options.breakPrompt || ((cb) => cb());
    this.defaultElapsedLimitHours = options.defaultElapsedLimitHours ?? 3.5;
    this._lastActivity = this.now();
  }

  loadFromPersisted() {
    const persisted = this.restorePersisted() || ({} as any);
    const state = persisted.state;
    const timeEntries = persisted.timeEntries;
    // If a cap value was persisted with the timer state, restore it so behavior is consistent across reloads
    const persistedCap =
      (persisted as any).defaultElapsedLimitHours ||
      (state && (state.__defaultElapsedLimitHours ?? undefined));
    if (typeof persistedCap === 'number') this.defaultElapsedLimitHours = persistedCap;
    if (state) {
      this._state = state;
      if (!this._state.isPaused) {
        const elapsedMs = this._state.elapsedSeconds * 1000;
        this._state.startTime = this.now() - elapsedMs;
      }
      this._timeEntries = timeEntries || [];
      this._startLoops();
      this.onInfo(`Restored timer for #${this._state.workItemId}: ${this._state.workItemTitle}`);
      this.onState(this.snapshot());
    }
  }

  snapshot() {
    return this._state ? { ...this._state } : undefined;
  }

  start(workItemId: number, workItemTitle: string) {
    if (this._state) {
      this.onWarn('Timer already running. Stop it first.');
      return false;
    }
    this._state = {
      workItemId,
      workItemTitle,
      startTime: this.now(),
      elapsedSeconds: 0,
      isPaused: false,
      isPomodoro: this.pomodoroEnabled,
      pomodoroCount: 0,
    };
    this._persist();
    this._startLoops();
    this.onInfo(`Timer started for #${workItemId}: ${workItemTitle}`);
    this.onState(this.snapshot());
    return true;
  }

  pause(manual = true) {
    if (!this._state || this._state.isPaused) {
      this.onInfo('No active timer to pause');
      return false;
    }
    this._applyElapsed();
    this._state.isPaused = true;
    (this._state as any).pausedTime = this.now();
    if (!manual) (this._state as any).pausedDueToInactivity = true;
    this._persist();
    this.onInfo('Timer paused');
    this.onState(this.snapshot());
    return true;
  }

  resume(fromActivity = false) {
    if (!this._state || !this._state.isPaused) {
      this.onInfo('No paused timer to resume');
      return false;
    }
    const anyState: any = this._state;
    if (anyState.pausedTime) {
      const pausedMs = Math.max(0, this.now() - anyState.pausedTime);
      const adj = Math.min(pausedMs, 24 * 60 * 60 * 1000);
      this._state.startTime += adj;
    }
    this._state.isPaused = false;
    delete anyState.pausedTime;
    if (fromActivity) delete anyState.pausedDueToInactivity;
    this._persist();
    this.onInfo('Timer resumed');
    this.onState(this.snapshot());
    return true;
  }

  activityPing() {
    this._lastActivity = this.now();
    const anyState: any = this._state;
    if (
      this._state &&
      this._state.isPaused &&
      anyState.pausedDueToInactivity &&
      this.autoResumeOnActivity
    ) {
      this.resume(true);
      this.onInfo('Timer resumed due to activity');
    }
  }

  stop(addTimeEntryCallback?: (workItemId: number, hoursDecimal: number) => Promise<any> | any) {
    if (!this._state) {
      this.onInfo('No timer running');
      return null;
    }
    this._applyElapsed();
    // Compute elapsed and enforce configured cap (for forgotten timers)
    const totalSecRaw = this._state.elapsedSeconds;
    const capSec = Math.max(0, Math.floor(this.defaultElapsedLimitHours * 3600));
    let usedSec = totalSecRaw;
    let capApplied = false;
    if (capSec > 0 && totalSecRaw > capSec) {
      usedSec = capSec;
      capApplied = true;
    }
    const hours = parseFloat((usedSec / 3600).toFixed(2));
    const entry: TimeEntry = {
      workItemId: this._state.workItemId,
      startTime: this._state.startTime,
      endTime: this._state.startTime + usedSec * 1000,
      duration: usedSec,
    };
    this._timeEntries.push(entry);
    this._persist();
    const finishedId = this._state.workItemId;
    this._clearState();
    if (addTimeEntryCallback)
      Promise.resolve(addTimeEntryCallback(finishedId, hours)).catch((err) =>
        this.onError((err && err.message) || String(err))
      );
    if (capApplied) {
      this.onWarn(
        `Timer stopped. Elapsed time exceeded configured cap; recorded ${hours.toFixed(
          2
        )} hours (cap: ${this.defaultElapsedLimitHours.toFixed(2)}h).`
      );
    } else {
      this.onInfo(`Timer stopped. Total time: ${hours.toFixed(2)} hours.`);
    }
    return {
      ...entry,
      hoursDecimal: hours,
      capApplied,
      capLimitHours: this.defaultElapsedLimitHours,
    } as any;
  }

  getTimeEntries() {
    return [...this._timeEntries];
  }

  timeReport(period: 'Today' | 'This Week' | 'This Month' | 'All Time') {
    // Base all calculations off the timer's notion of "now" for consistency and testability
    const now = this.now();
    const nowDate = new Date(now);
    let from: number;
    switch (period) {
      case 'Today': {
        const d = new Date(nowDate);
        d.setHours(0, 0, 0, 0);
        from = d.getTime();
        break;
      }
      case 'This Week': {
        const d = new Date(nowDate);
        // getDay(): 0 = Sunday ... 6 = Saturday
        d.setDate(d.getDate() - d.getDay());
        d.setHours(0, 0, 0, 0);
        from = d.getTime();
        break;
      }
      case 'This Month': {
        const d = new Date(nowDate.getFullYear(), nowDate.getMonth(), 1);
        d.setHours(0, 0, 0, 0);
        from = d.getTime();
        break;
      }
      case 'All Time':
      default:
        from = 0;
        break;
    }

    // Include entries that overlap the [from, now] window, not only those that started within it.
    // When aggregating, only count the overlapping portion of each entry to avoid over-attributing
    // time for entries that span across the boundary (e.g., cross midnight).
    const map = new Map<number, { total: number; entries: TimeEntry[] }>();
    for (const e of this._timeEntries) {
      const entryStart = e.startTime;
      const entryEnd =
        typeof (e as any).endTime === 'number'
          ? (e as any).endTime
          : e.startTime + e.duration * 1000;
      const overlapStart = Math.max(entryStart, from);
      const overlapEnd = Math.min(entryEnd, now);
      const overlapMs = Math.max(0, overlapEnd - overlapStart);
      if (overlapMs <= 0) continue; // no overlap with the window

      if (!map.has(e.workItemId)) map.set(e.workItemId, { total: 0, entries: [] });
      const bucket = map.get(e.workItemId)!;
      bucket.total += overlapMs / 1000; // store in seconds to align with previous semantics
      bucket.entries.push(e);
    }
    return { period, from, to: now, buckets: map };
  }

  private _applyElapsed() {
    if (!this._state || this._state.isPaused) return;
    const deltaSec = Math.floor((this.now() - this._state.startTime) / 1000);
    this._state.elapsedSeconds = Math.max(0, Math.min(deltaSec, 86400));
  }

  private _tick() {
    if (this._state && !this._state.isPaused) {
      this._applyElapsed();
      if (this._state.isPomodoro) {
        const mins = this._state.elapsedSeconds / 60;
        if (mins >= 25 && Math.floor(mins) % 30 === 25) {
          this.breakPrompt(() => this.activityPing());
          (this._state as any).pomodoroCount += 1;
        }
      }
      this.onState(this.snapshot());
      this._persist(false);
    }
  }

  private _checkInactivity() {
    if (!this._state || this._state.isPaused) return;
    const idleSec = (this.now() - this._lastActivity) / 1000;
    if (idleSec >= this.inactivityTimeoutSec) {
      this.pause(false);
      this.onWarn(`Timer paused after ${this.inactivityTimeoutSec} seconds of inactivity.`);
    }
  }

  private _startLoops() {
    this._stopLoops();
    this._ticker = setInterval(() => this._tick(), this._tickIntervalMs);
    this._inactivityTicker = setInterval(() => this._checkInactivity(), this._inactivityCheckMs);
  }
  private _stopLoops() {
    if (this._ticker) {
      clearInterval(this._ticker);
      this._ticker = undefined;
    }
    if (this._inactivityTicker) {
      clearInterval(this._inactivityTicker);
      this._inactivityTicker = undefined;
    }
  }
  private _clearState() {
    this._stopLoops();
    this._state = undefined;
    this._persist();
    this.onState(undefined);
  }
  private _persist(updateLastSave = true) {
    try {
      this.persist({
        state: this._state,
        timeEntries: this._timeEntries,
        updateLastSave,
        defaultElapsedLimitHours: this.defaultElapsedLimitHours,
      });
    } catch (e: any) {
      this.onError(e.message || e);
    }
  }

  // Allow updating the cap at runtime when settings change
  setDefaultElapsedLimitHours(hours: number) {
    if (typeof hours === 'number' && Number.isFinite(hours) && hours >= 0) {
      this.defaultElapsedLimitHours = hours;
      this.onInfo(`Default elapsed limit updated to ${hours} hours.`);
    }
  }
}

export default WorkItemTimer;

import * as vscode from 'vscode';
import type { AzureDevOpsIntClient } from '../../../azureClient.js';
import type {
  WorkItem,
  WorkItemBranchLink,
  WorkItemBranchMetadata,
  WorkItemBuildSummary,
} from '../../../types.js';

export type BranchContext = {
  branchName?: string;
  branchRef?: string;
  repositoryId?: string;
  repositoryName?: string;
  remoteUrl?: string;
  lastUpdated: number;
};

export type BranchEnrichmentState = {
  context: BranchContext | null;
  hasActiveBuild: boolean;
  lastUpdated: number;
};

export type ConnectionBranchSource = {
  id: string;
  client?: AzureDevOpsIntClient;
};

type RepositoryCacheEntry = {
  fetchedAt: number;
  repos: any[];
};

type BuildCacheEntry = {
  fetchedAt: number;
  builds: WorkItemBuildSummary[];
};

const branchStateByConnection = new Map<string, BranchEnrichmentState>();
const repositoryCacheByConnection = new Map<string, RepositoryCacheEntry>();
const buildCacheByKey = new Map<string, BuildCacheEntry>();
const buildRefreshTimers = new Map<string, NodeJS.Timeout>();

let cachedGitApi: any | undefined;

export const BRANCH_REFRESH_INTERVAL_MS = 30 * 1000;
export const REPOSITORY_CACHE_TTL_MS = 5 * 60 * 1000;
export const BUILD_CACHE_TTL_MS = 20 * 1000;

function getGitCacheKey(connectionId: string, repositoryId?: string, branchRef?: string): string {
  return `${connectionId}|${repositoryId ?? 'any'}|${branchRef ?? 'unknown'}`;
}

export function getBranchContext(connectionId: string): BranchContext | null {
  return branchStateByConnection.get(connectionId)?.context ?? null;
}

export function getBranchEnrichmentState(connectionId: string): BranchEnrichmentState | undefined {
  return branchStateByConnection.get(connectionId);
}

export function clearConnectionCaches(connectionId: string): void {
  branchStateByConnection.delete(connectionId);
  repositoryCacheByConnection.delete(connectionId);

  for (const key of Array.from(buildCacheByKey.keys())) {
    if (key.startsWith(`${connectionId}|`)) {
      buildCacheByKey.delete(key);
    }
  }

  const timer = buildRefreshTimers.get(connectionId);
  if (timer) {
    try {
      clearTimeout(timer);
    } catch {
      /* ignore */
    }
    buildRefreshTimers.delete(connectionId);
  }
}

export function updateBuildRefreshTimer(
  connectionId: string,
  provider: { refresh: (query?: string) => unknown } | undefined,
  shouldPoll: boolean
): void {
  const existing = buildRefreshTimers.get(connectionId);

  if (!shouldPoll) {
    if (existing) {
      try {
        clearTimeout(existing);
      } catch {
        /* ignore */
      }
      buildRefreshTimers.delete(connectionId);
    }
    return;
  }

  if (existing || !provider) {
    return;
  }

  const timer = setTimeout(() => {
    buildRefreshTimers.delete(connectionId);
    Promise.resolve(provider.refresh())
      .catch((error) => {
        console.warn('[AzureDevOpsInt] [branchEnrichment] Build polling refresh failed', error);
      })
      .then(() => {
        /* noop */
      });
  }, BRANCH_REFRESH_INTERVAL_MS);

  buildRefreshTimers.set(connectionId, timer);
}

type ParsedRemote = {
  organization?: string;
  project?: string;
  repositoryName?: string;
};

type ParsedBranchLink = {
  projectId?: string;
  repositoryId?: string;
  refName?: string;
  shortName?: string;
};

function formatMeta(meta: unknown): string | undefined {
  if (meta === undefined || meta === null) {
    return undefined;
  }
  if (typeof meta === 'string') {
    return meta;
  }
  try {
    return JSON.stringify(meta);
  } catch {
    return undefined;
  }
}

async function getGitApi(): Promise<any | undefined> {
  if (cachedGitApi) {
    return cachedGitApi;
  }

  try {
    const gitExt = vscode.extensions.getExtension('vscode.git');
    if (!gitExt) {
      return undefined;
    }

    const exports = gitExt.isActive ? gitExt.exports : await gitExt.activate();
    const api = exports?.getAPI?.(1);
    cachedGitApi = api;
    return api;
  } catch (error) {
    console.warn(
      '[AzureDevOpsInt] [branchEnrichment] Failed to acquire git API',
      formatMeta(error)
    );
    return undefined;
  }
}

function normalizeRepositoryName(name?: string | null): string | undefined {
  if (!name) {
    return undefined;
  }

  const trimmed = name.trim().replace(/\.git$/i, '');
  return trimmed.length > 0 ? trimmed : undefined;
}

function parseAzureRemote(remoteUrl?: string | null): ParsedRemote | undefined {
  if (!remoteUrl) {
    return undefined;
  }

  try {
    if (/^https?:/i.test(remoteUrl)) {
      const url = new URL(remoteUrl);
      const segments = url.pathname.split('/').filter(Boolean);
      const gitIndex = segments.findIndex((segment) => segment.toLowerCase() === '_git');

      if (gitIndex >= 0 && segments.length > gitIndex + 1) {
        return {
          organization: segments[0],
          project: segments[1],
          repositoryName: normalizeRepositoryName(segments[gitIndex + 1]),
        };
      }

      if (segments.length >= 3) {
        return {
          organization: segments[0],
          project: segments[1],
          repositoryName: normalizeRepositoryName(segments[segments.length - 1]),
        };
      }
    } else if (remoteUrl.includes('ssh.dev.azure.com')) {
      const marker = 'ssh.dev.azure.com:v3/';
      const idx = remoteUrl.indexOf(marker);

      if (idx >= 0) {
        const parts = remoteUrl
          .slice(idx + marker.length)
          .split(/[\\/]/)
          .filter(Boolean);

        if (parts.length >= 3) {
          return {
            organization: parts[0],
            project: parts[1],
            repositoryName: normalizeRepositoryName(parts[2]),
          };
        }
      }
    } else if (remoteUrl.includes('vs-ssh.visualstudio.com')) {
      const marker = 'vs-ssh.visualstudio.com:';
      const idx = remoteUrl.indexOf(marker);

      if (idx >= 0) {
        const after = remoteUrl.slice(idx + marker.length).replace(/^v[23]\//i, '');
        const parts = after.split(/[\\/]/).filter(Boolean);

        if (parts.length >= 3) {
          return {
            organization: parts[0],
            project: parts[1],
            repositoryName: normalizeRepositoryName(parts[2]),
          };
        }
      }
    }
  } catch (error) {
    console.warn(
      '[AzureDevOpsInt] [branchEnrichment] Failed to parse Azure remote URL',
      formatMeta({ remoteUrl, error })
    );
  }

  return undefined;
}

function normalizeBranchRef(ref?: string | null): { full: string; short: string } | null {
  if (!ref) {
    return null;
  }

  const trimmed = String(ref).trim();
  if (!trimmed) {
    return null;
  }

  if (trimmed.startsWith('refs/')) {
    const short = trimmed.startsWith('refs/heads/') ? trimmed.slice('refs/heads/'.length) : trimmed;
    return { full: trimmed, short };
  }

  return { full: `refs/heads/${trimmed}`, short: trimmed };
}

function parseBranchArtifactLink(url?: string | null): ParsedBranchLink | null {
  if (!url || typeof url !== 'string') {
    return null;
  }

  const prefix = 'vstfs:///Git/Ref/';
  if (!url.startsWith(prefix)) {
    return null;
  }

  try {
    const remainder = url.slice(prefix.length);
    const parts = remainder.split('%2F');
    if (parts.length < 3) {
      return null;
    }

    const projectId = decodeURIComponent(parts[0]);
    const repositoryId = decodeURIComponent(parts[1]);
    const encodedRef = parts.slice(2).join('%2F');
    const rawRef = decodeURIComponent(encodedRef);

    let refName = rawRef;
    let shortName: string | undefined;

    if (refName.startsWith('GB')) {
      shortName = refName.slice(2);
      if (shortName.startsWith('refs/')) {
        refName = shortName;
        shortName = refName.replace(/^refs\/heads\//, '');
      } else {
        refName = `refs/heads/${shortName}`;
      }
    } else if (refName.startsWith('GT')) {
      shortName = refName.slice(2);
      refName = `refs/tags/${shortName}`;
    } else if (refName.startsWith('refs/')) {
      shortName = refName.replace(/^refs\/heads\//, '');
    }

    return { projectId, repositoryId, refName, shortName };
  } catch (error) {
    console.warn(
      '[AzureDevOpsInt] [branchEnrichment] Failed to parse branch artifact link',
      formatMeta({ url, error })
    );
    return null;
  }
}

async function getAzureRepositoryByName(
  connectionId: string,
  client: AzureDevOpsIntClient,
  repoName?: string
): Promise<any | null> {
  if (!repoName) {
    return null;
  }

  const normalized = repoName.trim().toLowerCase();
  if (!normalized) {
    return null;
  }

  const cached = repositoryCacheByConnection.get(connectionId);
  const now = Date.now();
  let repos: any[] | null = null;

  if (cached && now - cached.fetchedAt < REPOSITORY_CACHE_TTL_MS) {
    repos = cached.repos;
  } else {
    try {
      const fetched = await client.getRepositories();
      if (Array.isArray(fetched)) {
        repos = fetched;
        repositoryCacheByConnection.set(connectionId, { fetchedAt: now, repos: fetched });
      }
    } catch (error) {
      console.warn(
        '[AzureDevOpsInt] [branchEnrichment] Failed to fetch repositories',
        formatMeta(error)
      );
      return null;
    }
  }

  if (!Array.isArray(repos)) {
    return null;
  }

  return (
    repos.find(
      (r: any) => typeof r?.name === 'string' && r.name.trim().toLowerCase() === normalized
    ) ||
    repos.find((r: any) => typeof r?.id === 'string' && r.id.trim().toLowerCase() === normalized) ||
    null
  );
}

async function resolveBranchContext(source: ConnectionBranchSource): Promise<BranchContext | null> {
  const api = await getGitApi();
  if (!api) {
    return null;
  }

  const repo = Array.isArray(api.repositories) ? api.repositories[0] : undefined;
  if (!repo) {
    return null;
  }

  const head = repo.state?.HEAD;
  const normalized = normalizeBranchRef(head?.name || head?.upstream?.name);
  if (!normalized) {
    return null;
  }

  const remotes: any[] = Array.isArray(repo.state?.remotes) ? repo.state.remotes : [];
  const remote =
    remotes.find((r: any) => !r?.isReadOnly && (r?.pushUrl || r?.fetchUrl)) || remotes[0];
  const remoteUrl = remote?.pushUrl || remote?.fetchUrl;
  const parsedRemote = parseAzureRemote(remoteUrl);

  let repositoryId: string | undefined;
  let repositoryName = parsedRemote?.repositoryName;

  if (repositoryName && source.client) {
    const repoMeta = await getAzureRepositoryByName(source.id, source.client, repositoryName);
    if (repoMeta) {
      if (typeof repoMeta.id === 'string') {
        repositoryId = repoMeta.id;
      }
      if (typeof repoMeta.name === 'string' && repoMeta.name.trim().length > 0) {
        repositoryName = repoMeta.name.trim();
      }
    }
  }

  return {
    branchName: normalized.short,
    branchRef: normalized.full,
    repositoryId,
    repositoryName,
    remoteUrl,
    lastUpdated: Date.now(),
  };
}

async function getBuildsForBranch(
  connectionId: string,
  client: AzureDevOpsIntClient,
  branchRef: string,
  repositoryId?: string
): Promise<WorkItemBuildSummary[]> {
  const key = getGitCacheKey(connectionId, repositoryId, branchRef);
  const cached = buildCacheByKey.get(key);
  const now = Date.now();

  if (cached && now - cached.fetchedAt < BUILD_CACHE_TTL_MS) {
    return cached.builds;
  }

  try {
    const builds = await client.getRecentBuilds({
      top: 5,
      branchName: branchRef,
      repositoryId,
    });

    if (Array.isArray(builds)) {
      buildCacheByKey.set(key, { fetchedAt: now, builds });
      return builds;
    }
  } catch (error) {
    console.warn(
      '[AzureDevOpsInt] [branchEnrichment] Failed to fetch builds for branch',
      formatMeta(error)
    );
  }

  buildCacheByKey.set(key, { fetchedAt: now, builds: [] });
  return [];
}

const FINISHED_BUILD_STATUSES = new Set(['completed', 'postponed']);
const FINISHED_BUILD_RESULTS = new Set([
  'succeeded',
  'failed',
  'canceled',
  'cancelled',
  'partiallysucceeded',
]);

function isBuildActive(summary?: WorkItemBuildSummary | null): boolean {
  if (!summary) {
    return false;
  }

  const status = typeof summary.status === 'string' ? summary.status.toLowerCase() : '';
  if (status) {
    return !FINISHED_BUILD_STATUSES.has(status);
  }

  const result = typeof summary.result === 'string' ? summary.result.toLowerCase() : '';
  if (!result) {
    return true;
  }

  return !FINISHED_BUILD_RESULTS.has(result);
}

type BranchMatchResult = {
  relation: WorkItemBranchLink;
  parsed: ParsedBranchLink;
  confidence: WorkItemBranchMetadata['matchConfidence'];
};

function findBestBranchMatch(
  relations: WorkItemBranchLink[],
  targetBranchRef: string,
  targetBranchName: string,
  targetRepositoryId?: string
): BranchMatchResult | null {
  if (!Array.isArray(relations) || relations.length === 0) {
    return null;
  }

  let best: { score: number; result: BranchMatchResult } | null = null;

  for (const relation of relations) {
    const parsed = parseBranchArtifactLink(relation.url);
    if (!parsed) {
      continue;
    }

    const normalized = normalizeBranchRef(parsed.refName || relation.refName);
    const relationRef = normalized?.full?.toLowerCase();
    const relationShort = (normalized?.short || parsed.shortName || '').toLowerCase();
    const desiredRef = targetBranchRef.toLowerCase();
    const desiredShort = targetBranchName.toLowerCase();
    const refMatches = !!relationRef && relationRef === desiredRef;
    const nameMatches = relationShort === desiredShort && desiredShort.length > 0;

    if (!refMatches && !nameMatches) {
      continue;
    }

    const repoMatch =
      targetRepositoryId && parsed.repositoryId
        ? parsed.repositoryId.toLowerCase() === targetRepositoryId.toLowerCase()
        : !targetRepositoryId;

    let score = 0;
    let confidence: WorkItemBranchMetadata['matchConfidence'] = 'name';

    if (refMatches) {
      score += 2;
      confidence = repoMatch ? 'exact' : 'refOnly';
    } else if (nameMatches) {
      score += 1;
      confidence = 'name';
    }

    if (repoMatch) {
      score += 1;
    }

    if (!best || score > best.score) {
      const result: BranchMatchResult = {
        relation,
        parsed: {
          projectId: parsed.projectId,
          repositoryId: parsed.repositoryId,
          refName: normalized?.full || parsed.refName,
          shortName: normalized?.short || parsed.shortName,
        },
        confidence,
      };

      best = { score, result };
    }
  }

  return best?.result ?? null;
}

export async function enrichWorkItemsForConnection(
  source: ConnectionBranchSource,
  payload: { items: WorkItem[]; connectionId: string }
): Promise<WorkItem[]> {
  const sourceItems = Array.isArray(payload.items) ? payload.items : [];
  const client = source.client;

  if (!client || sourceItems.length === 0) {
    branchStateByConnection.set(source.id, {
      context: null,
      hasActiveBuild: false,
      lastUpdated: Date.now(),
    });
    return [...sourceItems];
  }

  const branchContext = await resolveBranchContext(source);
  if (!branchContext) {
    branchStateByConnection.set(source.id, {
      context: null,
      hasActiveBuild: false,
      lastUpdated: Date.now(),
    });
    return sourceItems.map((item) => {
      const clone: WorkItem = { ...item };
      if ((clone as any).branchMetadata) {
        delete (clone as any).branchMetadata;
      }
      if (Array.isArray(item.relations)) {
        clone.relations = item.relations.map((rel) => ({ ...rel }));
      }
      return clone;
    });
  }

  const normalizedContext = normalizeBranchRef(branchContext.branchRef || branchContext.branchName);
  if (!normalizedContext) {
    branchStateByConnection.set(source.id, {
      context: null,
      hasActiveBuild: false,
      lastUpdated: Date.now(),
    });
    return sourceItems;
  }

  const matchedItems: WorkItem[] = [];
  const unmatchedItems: WorkItem[] = [];
  const matches: Array<{ item: WorkItem; repositoryId?: string; parsed: ParsedBranchLink }> = [];

  for (const original of sourceItems) {
    const clone: WorkItem = { ...original };
    const relations = Array.isArray(original.relations)
      ? original.relations.map((rel) => {
          const relClone: WorkItemBranchLink = { ...rel };
          const parsed = parseBranchArtifactLink(rel.url);
          if (parsed) {
            relClone.repositoryId = parsed.repositoryId;
            relClone.projectId = parsed.projectId;
            relClone.refName = parsed.refName;
          }
          return relClone;
        })
      : [];

    if (relations.length > 0) {
      clone.relations = relations;
    }

    const match = findBestBranchMatch(
      relations,
      normalizedContext.full,
      normalizedContext.short,
      branchContext.repositoryId
    );

    if (match) {
      const repositoryIdForMatch = match.parsed.repositoryId ?? branchContext.repositoryId;
      const metadata: WorkItemBranchMetadata = {
        isCurrentBranch: true,
        branchName: normalizedContext.short,
        refName: normalizedContext.full,
        repositoryId: repositoryIdForMatch,
        repositoryName: branchContext.repositoryName,
        matchConfidence: match.confidence,
        link: { ...match.relation },
        hasActiveBuild: false,
        lastUpdated: new Date().toISOString(),
      };

      clone.branchMetadata = metadata;
      matches.push({ item: clone, repositoryId: repositoryIdForMatch, parsed: match.parsed });
      matchedItems.push(clone);
    } else {
      if ((clone as any).branchMetadata) {
        delete (clone as any).branchMetadata;
      }
      unmatchedItems.push(clone);
    }
  }

  let hasActiveBuild = false;
  let resolvedRepositoryId = branchContext.repositoryId ?? matches[0]?.repositoryId;

  if (matches.length > 0) {
    const buildInfoByRepo = new Map<string, { build?: WorkItemBuildSummary; hasActive: boolean }>();
    const repoKeys = new Set<string>();

    for (const entry of matches) {
      repoKeys.add(entry.repositoryId ?? '__default__');
    }

    for (const repoKey of repoKeys) {
      const resolvedRepoId = repoKey === '__default__' ? branchContext.repositoryId : repoKey;
      const builds = await getBuildsForBranch(
        source.id,
        client,
        normalizedContext.full,
        resolvedRepoId
      );
      const latest = Array.isArray(builds) && builds.length > 0 ? builds[0] : undefined;
      const active = isBuildActive(latest);

      if (active) {
        hasActiveBuild = true;
      }

      buildInfoByRepo.set(repoKey, { build: latest, hasActive: active });
    }

    for (const entry of matches) {
      const info = buildInfoByRepo.get(entry.repositoryId ?? '__default__');
      if (info && entry.item.branchMetadata) {
        entry.item.branchMetadata.build = info.build;
        entry.item.branchMetadata.hasActiveBuild = info.hasActive;
        entry.item.branchMetadata.lastUpdated = new Date().toISOString();
      }
    }
  }

  let resolvedRepositoryName = branchContext.repositoryName;
  if (!resolvedRepositoryName && resolvedRepositoryId && source.client) {
    const repoMeta = await getAzureRepositoryByName(source.id, source.client, resolvedRepositoryId);
    if (repoMeta?.name && typeof repoMeta.name === 'string') {
      resolvedRepositoryName = repoMeta.name.trim();
    }
  }

  if (resolvedRepositoryName) {
    for (const entry of matches) {
      if (entry.item.branchMetadata) {
        entry.item.branchMetadata.repositoryName = resolvedRepositoryName;
      }
    }
  }

  if (resolvedRepositoryId) {
    for (const entry of matches) {
      if (entry.item.branchMetadata && !entry.item.branchMetadata.repositoryId) {
        entry.item.branchMetadata.repositoryId = resolvedRepositoryId;
      }
    }
  }

  const sortedItems = [...matchedItems, ...unmatchedItems];

  resolvedRepositoryId = branchContext.repositoryId ?? resolvedRepositoryId;
  branchStateByConnection.set(source.id, {
    context: {
      ...branchContext,
      branchName: normalizedContext.short,
      branchRef: normalizedContext.full,
      repositoryId: resolvedRepositoryId,
      repositoryName: resolvedRepositoryName ?? branchContext.repositoryName,
    },
    hasActiveBuild,
    lastUpdated: Date.now(),
  });

  return sortedItems;
}

import type { ProjectConnection } from '../../machines/applicationMachine.js';

/**
 * Derive a human-friendly label for a connection.
 * Order of preference:
 * 1. Explicit connection.label (trimmed)
 * 2. Project name if present
 * 3. Fallback: organization/project composite
 */
export function getConnectionLabel(connection: ProjectConnection): string {
  if (connection.label && connection.label.trim()) return connection.label.trim();
  if (connection.project && connection.project.trim()) return connection.project.trim();
  return `${connection.organization}/${connection.project}`;
}

import { WorkItemsProvider, type ProviderLogger } from '../../../provider.js';
import type { AzureDevOpsIntClient } from '../../../azureClient.js';
import type { WorkItem } from '../../../types.js';
import { enrichWorkItemsForConnection, type ConnectionBranchSource } from './branchEnrichment.js';

type WorkItemsTransformPayload = {
  items: WorkItem[];
  connectionId: string;
};

type ProviderFactoryOptions = {
  connectionId: string;
  client: AzureDevOpsIntClient;
  postMessage: (message: unknown) => void;
  logger?: ProviderLogger;
  debounceMs?: number;
};

export function createBranchAwareTransform(
  source: ConnectionBranchSource
): (payload: WorkItemsTransformPayload) => Promise<WorkItem[]> {
  return async (payload: WorkItemsTransformPayload) => {
    return enrichWorkItemsForConnection(source, payload);
  };
}

export function createConnectionProvider(options: ProviderFactoryOptions): WorkItemsProvider {
  const { connectionId, client, postMessage, logger, debounceMs } = options;

  const provider = new WorkItemsProvider(connectionId, client, postMessage, {
    logger,
    debounceMs,
    transformWorkItems: createBranchAwareTransform({ id: connectionId, client }),
  });

  return provider;
}

import { AzureDevOpsIntClient } from './azureClient.js';
import type { WorkItem } from './types.js';

export type PostMessageFn = (msg: any) => void;

export type ProviderLogger = {
  debug?: (message: string, meta?: any) => void;
  info?: (message: string, meta?: any) => void;
  warn?: (message: string, meta?: any) => void;
  error?: (message: string, meta?: any) => void;
};

type WorkItemTransform = (params: {
  items: WorkItem[];
  connectionId: string;
}) => Promise<WorkItem[]> | WorkItem[];

type ProviderOptions = {
  kanbanView?: boolean;
  currentFilters?: Record<string, any>;
  logger?: ProviderLogger;
  debounceMs?: number;
  transformWorkItems?: WorkItemTransform;
};

function isProviderOptions(value: any): value is ProviderOptions {
  return value && typeof value === 'object' && !Array.isArray(value);
}

const DEFAULT_QUERY = 'My Activity';

export class WorkItemsProvider {
  private connectionId: string;
  private client: AzureDevOpsIntClient | undefined;
  private postMessage: PostMessageFn;
  private logger: ProviderLogger | undefined;
  private debounceMs = 2000;
  private _workItems: WorkItem[] = [];
  private _selectedWorkItem: WorkItem | undefined;
  private _kanbanView = false;
  private _currentFilters: Record<string, any> = {};
  private _refreshInFlight = false;
  private _lastRefreshTs = 0;
  private _workItemTypes: string[] = [];
  private _currentQuery: string = DEFAULT_QUERY;
  private transformWorkItemsFn: WorkItemTransform | undefined;

  constructor(
    connectionOrClient: string | AzureDevOpsIntClient,
    clientOrPostMessage: AzureDevOpsIntClient | PostMessageFn,
    postMessageOrOptions?: PostMessageFn | ProviderOptions,
    maybeOptions?: ProviderOptions
  ) {
    if (typeof connectionOrClient === 'string') {
      this.connectionId = connectionOrClient;
      this.client = clientOrPostMessage as AzureDevOpsIntClient;
      this.postMessage =
        (typeof postMessageOrOptions === 'function' ? postMessageOrOptions : undefined) ||
        (() => {});
      this.applyOptions(maybeOptions ?? {});
    } else {
      // Legacy signature: (client, postMessage, options)
      this.connectionId = 'default';
      this.client = connectionOrClient;
      this.postMessage =
        (typeof clientOrPostMessage === 'function' ? clientOrPostMessage : undefined) || (() => {});
      this.applyOptions(isProviderOptions(postMessageOrOptions) ? postMessageOrOptions : {});
    }
  }

  private applyOptions(options: ProviderOptions) {
    this._kanbanView = options.kanbanView ?? this._kanbanView;
    this._currentFilters = options.currentFilters ?? this._currentFilters;
    this.logger = options.logger ?? this.logger;

    if (typeof options.debounceMs === 'number' && options.debounceMs >= 0) {
      this.debounceMs = options.debounceMs;
    }
    this.transformWorkItemsFn = options.transformWorkItems ?? this.transformWorkItemsFn;
  }

  updateClient(nextClient: AzureDevOpsIntClient | undefined) {
    this.client = nextClient;
    this._workItemTypes = [];
  }

  setPostMessage(nextPostMessage: PostMessageFn) {
    this.postMessage = nextPostMessage || (() => {});
  }

  setLogger(nextLogger: ProviderLogger | undefined) {
    this.logger = nextLogger;
  }

  setTransformWorkItems(nextTransform: WorkItemTransform | undefined) {
    this.transformWorkItemsFn = nextTransform;
  }

  getConnectionId() {
    return this.connectionId;
  }

  async refresh(query?: string) {
    if (!this.client || typeof (this.client as any).getWorkItems !== 'function') {
      this.log('warn', 'Cannot refresh work items; client missing getWorkItems');
      return;
    }
    const now = Date.now();
    // Debounce: skip if a refresh completed less than 2000ms ago
    if (now - this._lastRefreshTs < this.debounceMs) {
      return;
    }
    if (this._refreshInFlight) {
      // Skip overlapping refresh attempts
      return;
    }
    this._refreshInFlight = true;

    // Define normalizedQuery outside try block so it's accessible in catch
    const normalizedQuery =
      typeof query === 'string' && query.trim().length > 0
        ? query.trim()
        : this._currentQuery || DEFAULT_QUERY;
    this._currentQuery = normalizedQuery;

    // Notify webview that query is starting so it can show loading state
    this._post({
      type: 'workItemsLoading',
      query: normalizedQuery,
    });

    try {
      const shouldFetchTypes =
        this._workItemTypes.length === 0 &&
        typeof (this.client as any).getWorkItemTypes === 'function';
      const typePromise: Promise<any[] | null> | null = shouldFetchTypes
        ? Promise.resolve()
            .then(() => (this.client as any).getWorkItemTypes())
            .catch((err: any) => {
              this.log('warn', 'Failed to fetch work item types', {
                connectionId: this.connectionId,
                error: err?.message || String(err),
              });
              return null;
            })
        : null;

      this.log('debug', 'refresh(): starting fetch', {
        connectionId: this.connectionId,
        query: normalizedQuery,
      });
      const fetched = await this.client.getWorkItems(normalizedQuery);
      const processed = await this._applyTransform(fetched);
      this.log('info', 'refresh(): completed fetch', {
        connectionId: this.connectionId,
        count: Array.isArray(processed) ? processed.length : 'n/a',
      });

      if (typePromise) {
        try {
          const rawTypes = await typePromise;
          if (Array.isArray(rawTypes) && rawTypes.length > 0) {
            this._mergeWorkItemTypesFromNames(this._normalizeTypeNames(rawTypes));
          }
        } catch {
          // Errors already logged in catch above; swallow here to avoid aborting refresh
        }
      }

      // Always update the work items, even if empty
      this._workItems = processed;
      this._mergeWorkItemTypesFromItems(processed);
      // FSM will handle posting work items via syncDataToWebview
      this._notifyWorkItemsChanged();

      if (processed.length === 0) {
        this.log('debug', 'refresh(): no results for query', {
          connectionId: this.connectionId,
          query: normalizedQuery,
        });
      }
    } catch (err: any) {
      const errorMessage = err?.message || String(err);
      this.log('error', 'Failed to refresh work items', {
        connectionId: this.connectionId,
        query: normalizedQuery,
        error: errorMessage,
        stack: err?.stack,
      });

      // Show error to user in webview
      this._error(errorMessage);

      // Also log to console for Developer Tools
      console.error('[AzureDevOpsInt] [WorkItemsProvider] Refresh failed:', {
        connectionId: this.connectionId,
        query: normalizedQuery,
        error: errorMessage,
        fullError: err,
      });
    } finally {
      this._refreshInFlight = false;
      this._lastRefreshTs = Date.now();
    }
  }

  async getWorkItemById(id: number) {
    if (!this.client) return null;
    return this.client.getWorkItemById(id);
  }

  async createWorkItem(
    type: string,
    title: string,
    description?: string,
    assignedTo?: string,
    extraFields?: Record<string, unknown>
  ) {
    if (!this.client || typeof (this.client as any).createWorkItem !== 'function') {
      throw new Error('No client');
    }
    const created = await this.client.createWorkItem(
      type,
      title,
      description,
      assignedTo,
      extraFields
    );
    await this.refresh();
    return created;
  }

  async updateWorkItem(id: number, patchOps: any[]) {
    if (!this.client || typeof (this.client as any).updateWorkItem !== 'function') {
      throw new Error('No client');
    }
    const updated = await this.client.updateWorkItem(id, patchOps);
    await this.refresh();
    return updated;
  }

  async addWorkItemComment(id: number, text: string) {
    if (!this.client || typeof (this.client as any).addWorkItemComment !== 'function') {
      throw new Error('No client');
    }
    return this.client.addWorkItemComment(id, text);
  }

  async addTimeEntry(id: number, hours: number, note?: string) {
    if (!this.client || typeof (this.client as any).addTimeEntry !== 'function') {
      throw new Error('No client');
    }
    await this.client.addTimeEntry(id, hours, note);
    await this.refresh();
  }

  async search(term: string) {
    if (!this.client || typeof (this.client as any).searchWorkItems !== 'function') {
      this.log('warn', 'search() skipped; client missing searchWorkItems');
      return [];
    }
    const res = await this.client.searchWorkItems(term);
    await this.showWorkItems(res);
    return res;
  }

  async filter(filterObj: any) {
    if (!this.client || typeof (this.client as any).filterWorkItems !== 'function') {
      this.log('warn', 'filter() skipped; client missing filterWorkItems');
      return [];
    }
    const res = await this.client.filterWorkItems(filterObj);
    await this.showWorkItems(res);
    return res;
  }

  async runWIQL(wiql: string) {
    if (!this.client || typeof (this.client as any).runWIQL !== 'function') {
      this.log('warn', 'runWIQL() skipped; client missing runWIQL');
      return [];
    }
    return this.client.runWIQL(wiql);
  }

  async showWorkItems(items: WorkItem[]) {
    if (!Array.isArray(items)) return;
    const processed = await this._applyTransform(items);
    const shouldIgnoreEmpty = processed.length === 0 && this._workItems.length > 0;
    if (!shouldIgnoreEmpty) {
      this._workItems = processed;
    }
    this._mergeWorkItemTypesFromItems(shouldIgnoreEmpty ? this._workItems : processed);
    // FSM will handle posting work items via syncDataToWebview
    this._notifyWorkItemsChanged();
  }

  selectWorkItem(item: WorkItem) {
    this._selectedWorkItem = item;
    this._post({ type: 'workItemSelected', workItem: item });
  }

  getSelectedWorkItem() {
    return this._selectedWorkItem;
  }
  getWorkItems() {
    return [...this._workItems];
  }

  dispose() {
    this._workItems = [];
    this._selectedWorkItem = undefined;
    this._currentFilters = {};
    this._workItemTypes = [];
  }

  private _notifyWorkItemsChanged() {
    // CRITICAL: Send workItemsLoaded message so FSM can receive the data via forwardProviderMessage
    this._post({
      type: 'workItemsLoaded',
      workItems: [...this._workItems],
      connectionId: this.connectionId,
      query: this._currentQuery,
      kanbanView: this._kanbanView,
      types: [...this._workItemTypes],
    });

    // Send supplementary data
    this._postWorkItemTypeOptions();
    if (Object.keys(this._currentFilters).length > 0)
      this._post({ type: 'restoreFilters', filters: this._currentFilters });
  }
  private _post(msg: any) {
    if (!this.postMessage) return;
    if (msg && typeof msg === 'object' && !Array.isArray(msg)) {
      this.postMessage({ connectionId: this.connectionId, ...msg });
    } else {
      this.postMessage(msg);
    }
  }
  private _error(message: string) {
    this._post({ type: 'workItemsError', error: message });
  }

  private log(level: 'debug' | 'info' | 'warn' | 'error', message: string, meta?: any) {
    if (!this.logger) return;
    const fn = this.logger[level];
    if (typeof fn === 'function') {
      try {
        fn(message, meta);
      } catch {
        // ignore logger failures
      }
    }
  }

  getWorkItemTypeOptions() {
    return [...this._workItemTypes];
  }

  private _normalizeTypeNames(rawTypes: any[]): string[] {
    if (!Array.isArray(rawTypes)) return [];
    return rawTypes
      .map((entry: any) => {
        if (!entry) return '';
        if (typeof entry === 'string') return entry.trim();
        if (typeof entry.name === 'string') return entry.name.trim();
        if (typeof entry.text === 'string') return entry.text.trim();
        if (typeof entry.referenceName === 'string') return entry.referenceName.trim();
        return '';
      })
      .filter((name: string) => name.length > 0);
  }

  private _mergeWorkItemTypesFromNames(names: string[]) {
    if (!Array.isArray(names) || names.length === 0) return;
    const current = new Set(this._workItemTypes);
    let changed = false;
    for (const raw of names) {
      const value = typeof raw === 'string' ? raw.trim() : '';
      if (!value) continue;
      if (!current.has(value)) {
        current.add(value);
        changed = true;
      }
    }
    if (changed) {
      this._workItemTypes = Array.from(current).sort((a, b) => a.localeCompare(b));
    }
  }

  private _mergeWorkItemTypesFromItems(items: WorkItem[]) {
    if (!Array.isArray(items) || items.length === 0) return;
    const names = items
      .map((item: any) => {
        const fromFlattened = typeof item?.type === 'string' ? item.type : undefined;
        const fromFields =
          typeof item?.fields?.['System.WorkItemType'] === 'string'
            ? item.fields['System.WorkItemType']
            : undefined;
        const value = fromFlattened || fromFields;
        return typeof value === 'string' ? value.trim() : '';
      })
      .filter((name: string) => name.length > 0);
    this._mergeWorkItemTypesFromNames(names);
  }

  private _postWorkItemTypeOptions() {
    this._post({ type: 'workItemTypeOptions', types: [...this._workItemTypes] });
  }

  private async _applyTransform(items: WorkItem[]): Promise<WorkItem[]> {
    if (!Array.isArray(items)) return [];
    if (!this.transformWorkItemsFn) return [...items];
    try {
      const result = await this.transformWorkItemsFn({
        items: [...items],
        connectionId: this.connectionId,
      });
      if (Array.isArray(result)) {
        return result;
      }
    } catch (error: any) {
      this.log('warn', 'transformWorkItems failed', {
        connectionId: this.connectionId,
        error: error?.message || String(error),
      });
    }
    return [...items];
  }
}

export default WorkItemsProvider;

import type { Disposable, Webview } from 'vscode';
import type { ActorRefFrom, StateFrom } from 'xstate';
import type { ApplicationContext, ProjectConnection } from '../fsm/machines/applicationMachine.js';
import { applicationMachine } from '../fsm/machines/applicationMachine.js';
import {
  deriveTabViewModel,
  deriveTimerViewModel,
  normalizeWorkItems,
  type TabWorkItemViewModel,
  type TabViewModel,
} from '../fsm/functions/tabViewModelFunctions.js';
import {
  getLoadedConnections as readLegacyConnections,
  getActiveConnectionId as readLegacyActiveConnectionId,
} from '../fsm/services/extensionHostBridge.js';

export type Logger = (message: string, meta?: Record<string, unknown>) => void;

export type SharedContextBridgeOptions = {
  actor: ActorRefFrom<typeof applicationMachine>;
  logger?: Logger;
  contextSelector?: (context: ApplicationContext) => SharedContextPayload;
};

export interface SharedContextBridge extends Disposable {
  attachWebview(webview: Webview): void;
  detachWebview(): void;
  handleWebviewMessage(message: unknown): boolean;
  sync(): void;
}

interface SharedConnectionView {
  id: string;
  label: string;
  organization?: string;
  project?: string;
  authMethod?: string;
  lastUsed?: string;
}

interface SharedWorkItemView {
  id: number;
  title: string;
  type?: string;
  state?: string;
  assignedTo?: string;
}

interface SharedTimerView {
  isActive: boolean;
  isRunning: boolean;
  workItemId: number | null;
  elapsed: number;
}

export interface SharedContextPayload {
  activeConnectionId: string | null;
  connections: SharedConnectionView[];
  workItems: SharedWorkItemView[];
  timer: SharedTimerView;
  isLoading: boolean;
  authReminders: Array<{
    connectionId: string;
    reason: string;
    detail?: string;
    message?: string;
    label?: string;
    authMethod?: 'pat' | 'entra';
  }>;
  connectionStateSummaries: Array<{
    id: string;
    isConnected: boolean;
    hasClient: boolean;
    hasProvider: boolean;
    reauthInProgress: boolean;
  }>;
  lastError?: {
    message: string;
    name?: string;
  } | null;
  tab: TabViewModel;
}

type ApplicationActor = ActorRefFrom<typeof applicationMachine>;
type ApplicationState = StateFrom<typeof applicationMachine>;

export function createSharedContextBridge({
  actor,
  logger,
  contextSelector,
}: SharedContextBridgeOptions): SharedContextBridge {
  const applicationActor = actor as ApplicationActor;
  let currentWebview: Webview | undefined;
  let disposed = false;
  let lastSignature = '';

  const subscription = applicationActor.subscribe((state) =>
    maybePostContext(state as ApplicationState, 'state-change')
  );

  function log(message: string, meta?: Record<string, unknown>) {
    try {
      logger?.(message, meta);
    } catch (error) {
      console.warn('[AzureDevOpsInt] [SharedContextBridge] Logger failed', {
        message,
        meta,
        error,
      });
    }
  }

  function buildPayload(context: ApplicationContext): SharedContextPayload {
    if (contextSelector) {
      return contextSelector(context);
    }

    const fsmConnections = Array.isArray(context.connections) ? context.connections : [];
    const legacyConnections = normalizeLegacyProjectConnections(readLegacyConnections());
    const resolvedConnections: ProjectConnection[] =
      fsmConnections.length > 0 ? fsmConnections : legacyConnections;

    const resolvedActiveConnectionId =
      context.activeConnectionId ?? readLegacyActiveConnectionId() ?? null;

    const augmentedContext =
      resolvedConnections === fsmConnections
        ? context
        : ({
            ...context,
            connections: resolvedConnections,
            activeConnectionId: resolvedActiveConnectionId,
          } as ApplicationContext);

    const safeConnections: SharedConnectionView[] = resolvedConnections.map((connection) => ({
      id: connection.id,
      label:
        typeof connection.label === 'string' && connection.label.trim().length > 0
          ? connection.label.trim()
          : connection.project || connection.id,
      organization: connection.organization,
      project: connection.project,
      authMethod: connection.authMethod,
      lastUsed: connectionStatesSafeTimestamp(augmentedContext, connection.id),
    }));

    const connectionLabelLookup = new Map(
      resolvedConnections.map((connection) => {
        const resolvedLabel =
          typeof connection.label === 'string' && connection.label.trim().length > 0
            ? connection.label.trim()
            : connection.project || connection.id;
        return [connection.id, resolvedLabel] as const;
      })
    );

    const normalizeReminder = (
      connectionId: string,
      reminder: {
        reason?: string;
        detail?: string;
        message?: string;
        label?: string;
        authMethod?: 'pat' | 'entra' | undefined;
      }
    ) => {
      const normalizedId = typeof connectionId === 'string' ? connectionId.trim() : '';
      if (!normalizedId) {
        return null;
      }

      const resolvedLabel =
        typeof reminder?.label === 'string' && reminder.label.trim().length > 0
          ? reminder.label.trim()
          : (connectionLabelLookup.get(normalizedId) ?? normalizedId);

      return {
        connectionId: normalizedId,
        reason: reminder?.reason ?? 'authFailed',
        detail: reminder?.detail,
        message: reminder?.message,
        label: resolvedLabel,
        authMethod: reminder?.authMethod,
      };
    };

    const normalizedWorkItems = normalizeWorkItems(
      Array.isArray(augmentedContext.pendingWorkItems?.workItems)
        ? augmentedContext.pendingWorkItems!.workItems
        : []
    );

    const workItems: SharedWorkItemView[] = normalizedWorkItems.map(
      ({ id, title, type, state, assignedTo }: TabWorkItemViewModel) => ({
        id,
        title,
        type,
        state,
        assignedTo,
      })
    );

    const timerSnapshot = deriveTimerViewModel(augmentedContext);
    const tabView = deriveTabViewModel(
      augmentedContext,
      augmentedContext.activeConnectionId ?? null
    );

    const connectionStateSummaries = normalizeConnectionStateSummaries(
      augmentedContext.connectionStates
    );

    const authReminders = Array.isArray(context.pendingAuthReminders)
      ? context.pendingAuthReminders
          .filter(
            (reminder) =>
              reminder?.status !== 'dismissed' &&
              typeof reminder?.connectionId === 'string' &&
              reminder.connectionId.trim().length > 0
          )
          .map((reminder) =>
            normalizeReminder(reminder.connectionId, {
              reason: reminder?.reason,
              detail: reminder?.detail,
              message: reminder?.message,
              label: reminder?.label,
              authMethod: reminder?.authMethod,
            })
          )
          .filter((reminder): reminder is NonNullable<typeof reminder> => reminder !== null)
      : context.pendingAuthReminders instanceof Map
        ? Array.from(context.pendingAuthReminders.entries())
            .filter(([, reminder]) => reminder?.status !== 'dismissed')
            .map(([connectionId, reminder]) =>
              normalizeReminder(connectionId, {
                reason: reminder?.reason,
                detail: reminder?.detail,
                message: reminder?.message,
                label: reminder?.label,
                authMethod: reminder?.authMethod,
              })
            )
            .filter((reminder): reminder is NonNullable<typeof reminder> => reminder !== null)
        : [];

    const lastError = augmentedContext.lastError
      ? {
          message: augmentedContext.lastError.message,
          name: augmentedContext.lastError.name,
        }
      : null;

    const isLoading = Boolean(
      augmentedContext.pendingWorkItems?.workItems?.length &&
        !augmentedContext.pendingWorkItems?.connectionId
    );

    return {
      activeConnectionId: resolvedActiveConnectionId,
      connections: safeConnections,
      workItems,
      timer: timerSnapshot,
      isLoading,
      authReminders,
      connectionStateSummaries,
      lastError,
      tab: tabView,
    };
  }

  function connectionStatesSafeTimestamp(
    context: ApplicationContext,
    connectionId: string
  ): string | undefined {
    try {
      const rawState = context.connectionStates?.get(connectionId);
      if (!rawState) {
        return undefined;
      }
      const inferred = (rawState as Record<string, unknown>).lastUsed;
      return typeof inferred === 'string' ? inferred : undefined;
    } catch (error) {
      log('connectionStateTimestampExtractionFailed', {
        connectionId,
        error: serializeError(error),
      });
      return undefined;
    }
  }

  function maybePostContext(
    state: StateFrom<typeof applicationMachine> | undefined,
    reason: string
  ) {
    if (!currentWebview || !state) {
      return;
    }

    try {
      const payload = buildPayload(state.context);
      const signature = JSON.stringify(payload);
      if (signature === lastSignature) {
        return;
      }
      lastSignature = signature;

      const message = {
        type: 'contextUpdate',
        context: payload,
        meta: {
          reason,
          state: state.value,
        },
      };

      const maybeThenable = currentWebview.postMessage(message);
      if (typeof (maybeThenable as Thenable<boolean> | undefined)?.then === 'function') {
        (maybeThenable as Thenable<boolean>).then(undefined, (error) => {
          log('postMessageFailed', { error: serializeError(error) });
        });
      }
    } catch (error) {
      log('contextSerializationFailed', { error: serializeError(error) });
    }
  }

  function synchronizeImmediately(reason: string) {
    const snapshot = applicationActor.getSnapshot?.();
    if (!snapshot) {
      log('snapshotUnavailable', { reason });
      return;
    }
    maybePostContext(snapshot, reason);
  }

  function normalizeConnectionStateSummaries(
    rawStates: ApplicationContext['connectionStates'] | unknown
  ): SharedContextPayload['connectionStateSummaries'] {
    if (rawStates instanceof Map) {
      return Array.from(rawStates.entries()).map(([id, state]) =>
        createConnectionSummary(id, state)
      );
    }

    if (isRecord(rawStates)) {
      return Object.entries(rawStates).map(([id, state]) => createConnectionSummary(id, state));
    }

    return [];
  }

  function createConnectionSummary(
    id: string,
    rawState: unknown
  ): SharedContextPayload['connectionStateSummaries'][number] {
    const baseRecord = pickSummarySource(rawState);
    const explicitConnected =
      typeof baseRecord.isConnected === 'boolean' ? baseRecord.isConnected : undefined;
    const explicitReauth =
      typeof baseRecord.reauthInProgress === 'boolean' ? baseRecord.reauthInProgress : undefined;

    const stateToken = toLowerCaseString(baseRecord.state);
    const statusToken = toLowerCaseString(baseRecord.status);

    const connectedToken = stateToken.includes('connected') || statusToken.includes('connected');
    const readyToken = stateToken.includes('ready') || statusToken.includes('ready');
    const authToken =
      stateToken.includes('reauth') ||
      stateToken.includes('authenticating') ||
      statusToken.includes('reauth') ||
      statusToken.includes('authenticating');

    const clientCandidate =
      baseRecord.client ??
      baseRecord.azureClient ??
      baseRecord.connectionClient ??
      baseRecord.hasClient ??
      baseRecord.isConnected ??
      (connectedToken || readyToken);

    const providerCandidate =
      baseRecord.provider ?? baseRecord.hasProvider ?? baseRecord.workItemsProvider;

    const reauthCandidate =
      explicitReauth ?? baseRecord.isAuthenticating ?? baseRecord.authInProgress ?? authToken;

    const hasClient = Boolean(clientCandidate);
    const hasProvider = Boolean(providerCandidate);
    const isConnected =
      explicitConnected ?? Boolean(hasClient || hasProvider || connectedToken || readyToken);

    return {
      id,
      isConnected,
      hasClient: hasClient || isConnected,
      hasProvider,
      reauthInProgress: Boolean(reauthCandidate),
    };
  }

  function pickSummarySource(rawState: unknown): Record<string, any> {
    if (!isRecord(rawState)) {
      return {};
    }

    const nestedContext = rawState.context;
    if (isRecord(nestedContext)) {
      return { ...nestedContext };
    }

    return { ...rawState };
  }

  function isRecord(value: unknown): value is Record<string, any> {
    return typeof value === 'object' && value !== null;
  }

  function toLowerCaseString(value: unknown): string {
    return typeof value === 'string' ? value.toLowerCase() : '';
  }

  function normalizeLegacyProjectConnections(value: unknown): ProjectConnection[] {
    if (!Array.isArray(value)) {
      return [];
    }

    return value
      .map((entry) => (isProjectConnection(entry) ? entry : coerceProjectConnection(entry)))
      .filter((entry): entry is ProjectConnection => Boolean(entry));
  }

  function isProjectConnection(value: unknown): value is ProjectConnection {
    if (!isRecord(value)) {
      return false;
    }

    return (
      typeof value.id === 'string' &&
      value.id.length > 0 &&
      typeof value.organization === 'string' &&
      value.organization.length > 0 &&
      typeof value.project === 'string' &&
      value.project.length > 0
    );
  }

  function coerceProjectConnection(value: unknown): ProjectConnection | null {
    if (!isRecord(value)) {
      return null;
    }

    const id = readString(value.id) ?? readString(value.connectionId) ?? null;
    const organization = readString(value.organization) ?? readString(value.org) ?? null;
    const project = readString(value.project) ?? readString(value.teamProject) ?? null;

    if (!id || !organization || !project) {
      return null;
    }

    const coerced: ProjectConnection = {
      id,
      organization,
      project,
      team: readString(value.team) ?? undefined,
      authMethod:
        value.authMethod === 'entra' ? 'entra' : value.authMethod === 'pat' ? 'pat' : undefined,
      baseUrl: readString(value.baseUrl) ?? undefined,
      apiBaseUrl: readString(value.apiBaseUrl) ?? undefined,
      identityName: readString(value.identityName) ?? undefined,
      tenantId: readString(value.tenantId) ?? undefined,
      clientId: readString(value.clientId) ?? undefined,
    };

    return coerced;
  }

  function readString(value: unknown): string | null {
    return typeof value === 'string' && value.trim().length > 0 ? value.trim() : null;
  }

  function handleWebviewMessage(message: unknown): boolean {
    if (!message || typeof message !== 'object') {
      return false;
    }
    const msg = message as { type?: unknown };
    if (msg.type === 'getContext') {
      synchronizeImmediately('explicit-request');
      return true;
    }
    return false;
  }

  return {
    attachWebview(webview: Webview) {
      currentWebview = webview;
      synchronizeImmediately('webview-attached');
    },
    detachWebview() {
      currentWebview = undefined;
    },
    handleWebviewMessage,
    sync() {
      synchronizeImmediately('manual-sync');
    },
    dispose() {
      if (disposed) {
        return;
      }
      disposed = true;
      try {
        subscription.unsubscribe();
      } catch (error) {
        log('subscriptionDisposalFailed', { error: serializeError(error) });
      }
      currentWebview = undefined;
    },
  };
}

function serializeError(error: unknown): Record<string, unknown> {
  if (error instanceof Error) {
    return {
      message: error.message,
      name: error.name,
      stack: error.stack,
    };
  }
  if (typeof error === 'object' && error) {
    return { ...error } as Record<string, unknown>;
  }
  return { value: String(error) };
}

import type {
  ApplicationContext,
  AuthReminderState,
  ConnectionState,
  ProjectConnection,
} from '../machines/applicationMachine.js';

export interface TabWorkItemViewModel {
  id: number;
  title: string;
  type?: string;
  state?: string;
  assignedTo?: string;
}

export interface TabTimerViewModel {
  isActive: boolean;
  isRunning: boolean;
  workItemId: number | null;
  elapsed: number;
}

export interface TabAuthReminderViewModel {
  reason: string;
  detail?: string;
  message?: string;
  label?: string;
  authMethod?: 'pat' | 'entra';
}

export interface TabStatusViewModel {
  isLoading: boolean;
  lastError?: string | null;
}

export interface TabViewModel {
  connectionId: string | null;
  label: string;
  organization?: string;
  project?: string;
  authMethod?: 'pat' | 'entra';
  isActive: boolean;
  workItems: TabWorkItemViewModel[];
  rawWorkItems: unknown[];
  timer: TabTimerViewModel;
  status: TabStatusViewModel;
  authReminder: TabAuthReminderViewModel | null;
}

export function deriveTabViewModel(
  context: ApplicationContext,
  rawConnectionId: string | null | undefined
): TabViewModel {
  const normalizedId =
    normalizeConnectionId(rawConnectionId) ?? normalizeConnectionId(context.activeConnectionId);
  const connection = selectConnection(context.connections, normalizedId);
  const connectionId = connection?.id ?? normalizedId ?? null;
  const connectionState = connectionId ? context.connectionStates.get(connectionId) : undefined;

  return {
    connectionId,
    label: deriveConnectionLabel(connection, connectionId),
    organization: connection?.organization,
    project: connection?.project,
    authMethod: connection?.authMethod,
    isActive: Boolean(connectionId && context.activeConnectionId === connectionId),
    rawWorkItems: deriveRawWorkItems(context, connectionId),
    workItems: deriveWorkItems(context, connectionId),
    timer: deriveTimerViewModel(context),
    status: deriveStatus(context, connectionId, connectionState),
    authReminder: deriveAuthReminder(context.pendingAuthReminders, connectionId),
  };
}

export function deriveTimerViewModel(context: ApplicationContext): TabTimerViewModel {
  const timerActor = context.timerActor as { getSnapshot?: () => unknown } | undefined;
  if (!timerActor?.getSnapshot) {
    return { isActive: false, isRunning: false, workItemId: null, elapsed: 0 };
  }

  try {
    const snapshot = timerActor.getSnapshot() as {
      context?: Record<string, unknown>;
      matches?: (value: string) => boolean;
      value?: unknown;
    } | null;
    const snapshotContext = snapshot?.context ?? {};
    const isRunning =
      typeof snapshot?.matches === 'function'
        ? snapshot.matches('running')
        : snapshot?.value === 'running';

    return {
      isActive: Boolean(snapshot),
      isRunning: Boolean(isRunning),
      workItemId: readNumeric(snapshotContext.workItemId),
      elapsed: readElapsed(snapshotContext.elapsed),
    };
  } catch {
    return { isActive: false, isRunning: false, workItemId: null, elapsed: 0 };
  }
}

export function normalizeWorkItems(items: unknown[]): TabWorkItemViewModel[] {
  return items
    .map((item) => normalizeWorkItem(item))
    .filter((item): item is TabWorkItemViewModel => item !== null && item.id !== null);
}

function deriveWorkItems(
  context: ApplicationContext,
  connectionId: string | null
): TabWorkItemViewModel[] {
  return normalizeWorkItems(deriveRawWorkItems(context, connectionId));
}

function deriveRawWorkItems(context: ApplicationContext, connectionId: string | null): unknown[] {
  if (!connectionId) {
    return [];
  }

  const pending = context.pendingWorkItems;
  if (!pending || !Array.isArray(pending.workItems)) {
    return [];
  }

  const targetMatches =
    typeof pending.connectionId === 'string' ? pending.connectionId === connectionId : true;
  if (!targetMatches) {
    return [];
  }

  return pending.workItems;
}

function deriveStatus(
  context: ApplicationContext,
  connectionId: string | null,
  connectionState: ConnectionState | undefined
): TabStatusViewModel {
  if (!connectionId) {
    return { isLoading: false, lastError: null };
  }

  const pending = context.pendingWorkItems;
  const pendingMatches =
    pending && (!pending.connectionId || pending.connectionId === connectionId);
  const waitingForData = Boolean(
    pendingMatches && (!Array.isArray(pending?.workItems) || pending.workItems.length === 0)
  );
  const connectionReady = Boolean(connectionState?.client && connectionState?.provider);
  const lastError = context.lastError?.message ?? null;

  return {
    isLoading: !connectionReady || waitingForData,
    lastError,
  };
}

function deriveAuthReminder(
  reminders: Map<string, AuthReminderState> | undefined,
  connectionId: string | null
): TabAuthReminderViewModel | null {
  if (!connectionId || !reminders?.size) {
    return null;
  }

  const reminder = reminders.get(connectionId);
  if (!reminder || reminder.status !== 'pending') {
    return null;
  }

  return {
    reason: reminder.reason,
    detail: reminder.detail,
    message: reminder.message,
    label: reminder.label ?? connectionId ?? undefined,
    authMethod: reminder.authMethod,
  };
}

function normalizeConnectionId(connectionId: string | null | undefined): string | null {
  if (typeof connectionId !== 'string') {
    return null;
  }
  const trimmed = connectionId.trim();
  return trimmed.length > 0 ? trimmed : null;
}

function selectConnection(
  connections: ProjectConnection[] | undefined,
  connectionId: string | null
): ProjectConnection | undefined {
  if (!connectionId || !Array.isArray(connections)) {
    return undefined;
  }
  return connections.find((connection) => connection.id === connectionId);
}

function deriveConnectionLabel(
  connection: ProjectConnection | undefined,
  connectionId: string | null
): string {
  if (connection?.project) {
    return connection.project;
  }
  if (connection?.id) {
    return connection.id;
  }
  return connectionId ?? 'unknown-connection';
}

function normalizeWorkItem(item: unknown): TabWorkItemViewModel | null {
  if (!item || typeof item !== 'object') {
    return null;
  }

  const record = item as Record<string, unknown>;
  const id = readNumeric(record.id ?? record['System.Id']);
  if (id === null) {
    return null;
  }

  const fields = readRecord(record.fields);
  const title = readString(record.title ?? fields?.['System.Title']) ?? String(id);
  const type = readString(record.type ?? fields?.['System.WorkItemType']);
  const state = readString(record.state ?? fields?.['System.State']);
  const assignedTo = readAssignedTo(record.assignedTo, fields?.['System.AssignedTo']);

  return { id, title, type, state, assignedTo };
}

function readNumeric(value: unknown): number | null {
  if (typeof value === 'number' && Number.isFinite(value)) {
    return value;
  }
  if (typeof value === 'string') {
    const parsed = Number.parseInt(value, 10);
    return Number.isFinite(parsed) ? parsed : null;
  }
  return null;
}

function readElapsed(value: unknown): number {
  const numeric = readNumeric(value);
  return numeric !== null ? numeric : 0;
}

function readString(value: unknown): string | undefined {
  if (typeof value === 'string') {
    const trimmed = value.trim();
    return trimmed.length > 0 ? trimmed : undefined;
  }
  return undefined;
}

function readRecord(value: unknown): Record<string, unknown> | undefined {
  return value && typeof value === 'object' ? (value as Record<string, unknown>) : undefined;
}

function readAssignedTo(fieldValue: unknown, fieldRecord: unknown): string | undefined {
  if (typeof fieldValue === 'string') {
    return fieldValue;
  }
  if (fieldRecord && typeof fieldRecord === 'object') {
    const record = fieldRecord as Record<string, unknown>;
    const candidate = record.displayName ?? record.uniqueName;
    return readString(candidate);
  }
  return undefined;
}

import type * as vscode from 'vscode';

