import * as vscode from 'vscode';

const LOG_BUFFER_MAX = 5000;
let outputChannel: vscode.OutputChannel | undefined;
const logBuffer: string[] = [];

type ScopedLogFunction = (message: string, meta?: unknown) => void;

export type ScopedLogger = {
  debug: ScopedLogFunction;
  info: ScopedLogFunction;
  warn: ScopedLogFunction;
  error: ScopedLogFunction;
};

export function setOutputChannel(channel: vscode.OutputChannel | undefined) {
  outputChannel = channel;
}

export function getOutputChannel(): vscode.OutputChannel | undefined {
  return outputChannel;
}

export function clearOutputChannel() {
  outputChannel = undefined;
}

export function logLine(text: string) {
  try {
    outputChannel?.appendLine(text);
  } catch {
    /* ignore channel errors */
  }
  try {
    logBuffer.push(text);
    if (logBuffer.length > LOG_BUFFER_MAX) {
      logBuffer.splice(0, logBuffer.length - LOG_BUFFER_MAX);
    }
  } catch {
    /* ignore buffer errors */
  }
}

function formatLogMeta(meta: unknown): string | undefined {
  if (meta === undefined || meta === null) return undefined;
  if (typeof meta === 'string') return meta;
  try {
    return JSON.stringify(meta);
  } catch {
    return '[unserializable meta]';
  }
}

export function createScopedLogger(
  scope: string,
  shouldLogDebug: () => boolean = () => false
): ScopedLogger {
  const write = (level: string, message: string, meta?: unknown) => {
    const metaText = formatLogMeta(meta);
    const suffix = metaText ? ` ${metaText}` : '';
    const line = `${new Date().toISOString()} [${scope}] ${level} ${message}${suffix}`;
    logLine(line);
  };

  return {
    debug: (message, meta) => {
      if (!shouldLogDebug()) return;
      write('DEBUG', message, meta);
    },
    info: (message, meta) => {
      if (!shouldLogDebug()) return;
      write('INFO', message, meta);
    },
    warn: (message, meta) => {
      write('WARN', message, meta);
    },
    error: (message, meta) => {
      write('ERROR', message, meta);
    },
  };
}

export function getLogBufferSnapshot(): string[] {
  return [...logBuffer];
}

export function getLogBufferLength(): number {
  return logBuffer.length;
}

export function clearLogBuffer() {
  logBuffer.splice(0, logBuffer.length);
}

/**
 * Bridges console.log and console.error to the Output Channel
 * This ensures all console logging appears in the extension logs
 */
export function bridgeConsoleToOutputChannel() {
  const originalConsoleLog = console.log;
  const originalConsoleError = console.error;
  const originalConsoleWarn = console.warn;

  console.log = function (...args: any[]) {
    try {
      const message = args
        .map((arg) => {
          if (typeof arg === 'string') return arg;
          try {
            return JSON.stringify(arg);
          } catch {
            return String(arg);
          }
        })
        .join(' ');
      logLine(`[console.log] ${message}`);
    } catch {
      /* ignore */
    }
    return originalConsoleLog.apply(console, args);
  };

  console.error = function (...args: any[]) {
    try {
      const message = args
        .map((arg) => {
          if (typeof arg === 'string') return arg;
          if (arg instanceof Error) return `${arg.message}\n${arg.stack}`;
          try {
            return JSON.stringify(arg);
          } catch {
            return String(arg);
          }
        })
        .join(' ');
      logLine(`[console.error] ${message}`);
    } catch {
      /* ignore */
    }
    return originalConsoleError.apply(console, args);
  };

  console.warn = function (...args: any[]) {
    try {
      const message = args
        .map((arg) => {
          if (typeof arg === 'string') return arg;
          try {
            return JSON.stringify(arg);
          } catch {
            return String(arg);
          }
        })
        .join(' ');
      logLine(`[console.warn] ${message}`);
    } catch {
      /* ignore */
    }
    return originalConsoleWarn.apply(console, args);
  };
}

/**
 * Intelligent caching system for Azure DevOps Integration
 * Provides memory-efficient caching with TTL, LRU eviction, and performance metrics
 */

export interface CacheEntry<T> {
  data: T;
  timestamp: number;
  ttl: number;
  accessCount: number;
  lastAccessed: number;
}

export interface CacheMetrics {
  hits: number;
  misses: number;
  evictions: number;
  size: number;
  maxSize: number;
  hitRate: number;
}

export interface CacheOptions {
  maxSize?: number;
  defaultTtl?: number;
  enableMetrics?: boolean;
  evictionPolicy?: 'lru' | 'ttl' | 'both';
}

export class IntelligentCache<T = any> {
  private cache = new Map<string, CacheEntry<T>>();
  private accessOrder: string[] = [];
  private metrics: CacheMetrics;
  private options: Required<CacheOptions>;

  constructor(options: CacheOptions = {}) {
    this.options = {
      maxSize: options.maxSize ?? 1000,
      defaultTtl: options.defaultTtl ?? 300000, // 5 minutes
      enableMetrics: options.enableMetrics ?? true,
      evictionPolicy: options.evictionPolicy ?? 'both',
    };

    this.metrics = {
      hits: 0,
      misses: 0,
      evictions: 0,
      size: 0,
      maxSize: this.options.maxSize,
      hitRate: 0,
    };
  }

  /**
   * Get a value from the cache
   */
  get(key: string): T | null {
    const entry = this.cache.get(key);

    if (!entry) {
      this.metrics.misses++;
      this.updateHitRate();
      return null;
    }

    // Check if expired
    if (this.isExpired(entry)) {
      this.delete(key);
      this.metrics.misses++;
      this.updateHitRate();
      return null;
    }

    // Update access tracking
    entry.accessCount++;
    entry.lastAccessed = Date.now();
    this.updateAccessOrder(key);

    this.metrics.hits++;
    this.updateHitRate();
    return entry.data;
  }

  /**
   * Set a value in the cache
   */
  set(key: string, value: T, ttl?: number): void {
    const now = Date.now();
    const entry: CacheEntry<T> = {
      data: value,
      timestamp: now,
      ttl: ttl ?? this.options.defaultTtl,
      accessCount: 0,
      lastAccessed: now,
    };

    // Check if we need to evict
    if (this.cache.size >= this.options.maxSize && !this.cache.has(key)) {
      this.evict();
    }

    this.cache.set(key, entry);
    this.updateAccessOrder(key);
    this.metrics.size = this.cache.size;
  }

  /**
   * Check if a key exists and is not expired
   */
  has(key: string): boolean {
    const entry = this.cache.get(key);
    if (!entry) return false;

    if (this.isExpired(entry)) {
      this.delete(key);
      return false;
    }

    return true;
  }

  /**
   * Delete a key from the cache
   */
  delete(key: string): boolean {
    const deleted = this.cache.delete(key);
    if (deleted) {
      const index = this.accessOrder.indexOf(key);
      if (index > -1) {
        this.accessOrder.splice(index, 1);
      }
      this.metrics.size = this.cache.size;
    }
    return deleted;
  }

  /**
   * Clear all entries from the cache
   */
  clear(): void {
    this.cache.clear();
    this.accessOrder = [];
    this.metrics.size = 0;
  }

  /**
   * Get cache metrics
   */
  getMetrics(): CacheMetrics {
    return { ...this.metrics };
  }

  /**
   * Get cache size
   */
  size(): number {
    return this.cache.size;
  }

  /**
   * Clean up expired entries
   */
  cleanup(): number {
    let cleaned = 0;

    for (const [key, entry] of this.cache.entries()) {
      if (this.isExpired(entry)) {
        this.delete(key);
        cleaned++;
      }
    }

    return cleaned;
  }

  /**
   * Get all keys in the cache
   */
  keys(): string[] {
    return Array.from(this.cache.keys());
  }

  /**
   * Get cache statistics for debugging
   */
  getStats(): {
    metrics: CacheMetrics;
    topKeys: Array<{ key: string; accessCount: number; lastAccessed: number }>;
    memoryUsage: number;
  } {
    const topKeys = Array.from(this.cache.entries())
      .map(([key, entry]) => ({
        key,
        accessCount: entry.accessCount,
        lastAccessed: entry.lastAccessed,
      }))
      .sort((a, b) => b.accessCount - a.accessCount)
      .slice(0, 10);

    // Rough memory usage estimation
    const memoryUsage = this.cache.size * 100; // Rough estimate

    return {
      metrics: this.getMetrics(),
      topKeys,
      memoryUsage,
    };
  }

  private isExpired(entry: CacheEntry<T>): boolean {
    return Date.now() - entry.timestamp > entry.ttl;
  }

  private updateAccessOrder(key: string): void {
    // Remove from current position
    const index = this.accessOrder.indexOf(key);
    if (index > -1) {
      this.accessOrder.splice(index, 1);
    }

    // Add to end (most recently accessed)
    this.accessOrder.push(key);
  }

  private evict(): void {
    if (this.cache.size === 0) return;

    let keyToEvict: string | null = null;

    switch (this.options.evictionPolicy) {
      case 'lru':
        keyToEvict = this.accessOrder[0]; // Least recently used
        break;
      case 'ttl': {
        // Find the oldest entry
        let oldestTime = Date.now();
        for (const [key, entry] of this.cache.entries()) {
          if (entry.timestamp < oldestTime) {
            oldestTime = entry.timestamp;
            keyToEvict = key;
          }
        }
        break;
      }
      case 'both': {
        // Use LRU for frequently accessed items, TTL for old items
        const now = Date.now();
        const oldThreshold = now - this.options.defaultTtl * 2;

        // First try to evict old items
        for (const [key, entry] of this.cache.entries()) {
          if (entry.timestamp < oldThreshold) {
            keyToEvict = key;
            break;
          }
        }

        // If no old items, use LRU
        if (!keyToEvict) {
          keyToEvict = this.accessOrder[0];
        }
        break;
      }
    }

    if (keyToEvict) {
      this.delete(keyToEvict);
      this.metrics.evictions++;
    }
  }

  private updateHitRate(): void {
    const total = this.metrics.hits + this.metrics.misses;
    this.metrics.hitRate = total > 0 ? this.metrics.hits / total : 0;
  }
}

/**
 * Specialized cache for work items with optimized serialization
 */
export class WorkItemCache extends IntelligentCache<any> {
  private static readonly WORK_ITEM_TTL = 300000; // 5 minutes
  private static readonly QUERY_TTL = 600000; // 10 minutes
  private static readonly METADATA_TTL = 1800000; // 30 minutes

  constructor() {
    super({
      maxSize: 2000,
      defaultTtl: WorkItemCache.WORK_ITEM_TTL,
      enableMetrics: true,
      evictionPolicy: 'both',
    });
  }

  /**
   * Cache work items with appropriate TTL
   */
  setWorkItems(key: string, workItems: any[]): void {
    this.set(key, workItems, WorkItemCache.WORK_ITEM_TTL);
  }

  /**
   * Cache query results with longer TTL
   */
  setQueryResult(key: string, result: any): void {
    this.set(key, result, WorkItemCache.QUERY_TTL);
  }

  /**
   * Cache metadata with longest TTL
   */
  setMetadata(key: string, metadata: any): void {
    this.set(key, metadata, WorkItemCache.METADATA_TTL);
  }

  /**
   * Generate cache key for work items query
   */
  static generateWorkItemsKey(connectionId: string, query: string, filters: any): string {
    const filterStr = JSON.stringify(filters);
    return `workitems:${connectionId}:${Buffer.from(query + filterStr).toString('base64')}`;
  }

  /**
   * Generate cache key for query
   */
  static generateQueryKey(connectionId: string, query: string): string {
    return `query:${connectionId}:${Buffer.from(query).toString('base64')}`;
  }

  /**
   * Generate cache key for metadata
   */
  static generateMetadataKey(connectionId: string, type: string): string {
    return `metadata:${connectionId}:${type}`;
  }
}

/**
 * Global cache instances
 */
export const workItemCache = new WorkItemCache();
export const apiCache = new IntelligentCache({ maxSize: 500, defaultTtl: 300000 });
export const metadataCache = new IntelligentCache({ maxSize: 100, defaultTtl: 1800000 });

/**
 * Cache cleanup interval (5 minutes)
 */
let cleanupInterval: NodeJS.Timeout | null = null;

export function startCacheCleanup(): void {
  if (cleanupInterval) return;

  cleanupInterval = setInterval(() => {
    workItemCache.cleanup();
    apiCache.cleanup();
    metadataCache.cleanup();
  }, 300000); // 5 minutes
}

export function stopCacheCleanup(): void {
  if (cleanupInterval) {
    clearInterval(cleanupInterval);
    cleanupInterval = null;
  }
}

/**
 * Get comprehensive cache statistics
 */
export function getCacheStats(): {
  workItems: ReturnType<WorkItemCache['getStats']>;
  api: ReturnType<IntelligentCache['getStats']>;
  metadata: ReturnType<IntelligentCache['getStats']>;
  totalMemoryUsage: number;
} {
  const workItemsStats = workItemCache.getStats();
  const apiStats = apiCache.getStats();
  const metadataStats = metadataCache.getStats();

  return {
    workItems: workItemsStats,
    api: apiStats,
    metadata: metadataStats,
    totalMemoryUsage: workItemsStats.memoryUsage + apiStats.memoryUsage + metadataStats.memoryUsage,
  };
}

/**
 * Performance monitoring and optimization utilities
 * Tracks metrics, provides performance insights, and optimizes operations
 */

import { performance } from 'perf_hooks';
import { getCacheStats } from './cache.js';

export interface PerformanceMetrics {
  operation: string;
  duration: number;
  timestamp: number;
  memoryUsage?: number;
  cacheHit?: boolean;
  error?: string;
}

export interface PerformanceStats {
  totalOperations: number;
  averageDuration: number;
  minDuration: number;
  maxDuration: number;
  errorRate: number;
  cacheHitRate: number;
  memoryUsage: {
    current: number;
    peak: number;
    average: number;
  };
}

export interface MemoryUsage {
  rss: number;
  heapTotal: number;
  heapUsed: number;
  external: number;
  arrayBuffers: number;
}

class PerformanceMonitor {
  private metrics: PerformanceMetrics[] = [];
  private maxMetrics = 1000;
  private memoryPeak = 0;
  private isEnabled = true;

  /**
   * Start timing an operation
   */
  startTiming(operation: string): () => PerformanceMetrics {
    const startTime = performance.now();
    const startMemory = this.getMemoryUsage();

    return (error?: string, cacheHit?: boolean): PerformanceMetrics => {
      const endTime = performance.now();
      const endMemory = this.getMemoryUsage();

      const metric: PerformanceMetrics = {
        operation,
        duration: endTime - startTime,
        timestamp: Date.now(),
        memoryUsage: endMemory.heapUsed - startMemory.heapUsed,
        cacheHit,
        error,
      };

      this.recordMetric(metric);
      return metric;
    };
  }

  /**
   * Record a performance metric
   */
  recordMetric(metric: PerformanceMetrics): void {
    if (!this.isEnabled) return;

    this.metrics.push(metric);

    // Keep only the most recent metrics
    if (this.metrics.length > this.maxMetrics) {
      this.metrics = this.metrics.slice(-this.maxMetrics);
    }

    // Update memory peak
    if (metric.memoryUsage && metric.memoryUsage > this.memoryPeak) {
      this.memoryPeak = metric.memoryUsage;
    }
  }

  /**
   * Get performance statistics
   */
  getStats(): PerformanceStats {
    if (this.metrics.length === 0) {
      return {
        totalOperations: 0,
        averageDuration: 0,
        minDuration: 0,
        maxDuration: 0,
        errorRate: 0,
        cacheHitRate: 0,
        memoryUsage: {
          current: this.getMemoryUsage().heapUsed,
          peak: this.memoryPeak,
          average: 0,
        },
      };
    }

    const durations = this.metrics.map((m) => m.duration);
    const errors = this.metrics.filter((m) => m.error).length;
    const cacheHits = this.metrics.filter((m) => m.cacheHit === true).length;
    const memoryUsages = this.metrics
      .filter((m) => m.memoryUsage !== undefined)
      .map((m) => m.memoryUsage!);

    return {
      totalOperations: this.metrics.length,
      averageDuration: durations.reduce((a, b) => a + b, 0) / durations.length,
      minDuration: Math.min(...durations),
      maxDuration: Math.max(...durations),
      errorRate: errors / this.metrics.length,
      cacheHitRate: cacheHits / this.metrics.length,
      memoryUsage: {
        current: this.getMemoryUsage().heapUsed,
        peak: this.memoryPeak,
        average:
          memoryUsages.length > 0
            ? memoryUsages.reduce((a, b) => a + b, 0) / memoryUsages.length
            : 0,
      },
    };
  }

  /**
   * Get metrics for a specific operation
   */
  getOperationStats(operation: string): PerformanceStats {
    const operationMetrics = this.metrics.filter((m) => m.operation === operation);

    if (operationMetrics.length === 0) {
      return this.getStats();
    }

    const durations = operationMetrics.map((m) => m.duration);
    const errors = operationMetrics.filter((m) => m.error).length;
    const cacheHits = operationMetrics.filter((m) => m.cacheHit === true).length;

    return {
      totalOperations: operationMetrics.length,
      averageDuration: durations.reduce((a, b) => a + b, 0) / durations.length,
      minDuration: Math.min(...durations),
      maxDuration: Math.max(...durations),
      errorRate: errors / operationMetrics.length,
      cacheHitRate: cacheHits / operationMetrics.length,
      memoryUsage: {
        current: this.getMemoryUsage().heapUsed,
        peak: this.memoryPeak,
        average: 0,
      },
    };
  }

  /**
   * Get recent metrics
   */
  getRecentMetrics(count: number = 50): PerformanceMetrics[] {
    return this.metrics.slice(-count);
  }

  /**
   * Clear all metrics
   */
  clear(): void {
    this.metrics = [];
    this.memoryPeak = 0;
  }

  /**
   * Enable or disable monitoring
   */
  setEnabled(enabled: boolean): void {
    this.isEnabled = enabled;
  }

  /**
   * Get current memory usage
   */
  getMemoryUsage(): MemoryUsage {
    return process.memoryUsage();
  }

  /**
   * Get comprehensive performance report
   */
  getPerformanceReport(): {
    stats: PerformanceStats;
    cacheStats: ReturnType<typeof getCacheStats>;
    memoryUsage: MemoryUsage;
    recommendations: string[];
  } {
    const stats = this.getStats();
    const cacheStats = getCacheStats();
    const memoryUsage = this.getMemoryUsage();
    const recommendations: string[] = [];

    // Performance recommendations
    if (stats.averageDuration > 1000) {
      recommendations.push('Consider optimizing slow operations (>1s average)');
    }

    if (stats.errorRate > 0.1) {
      recommendations.push('High error rate detected - investigate error handling');
    }

    if (stats.cacheHitRate < 0.5) {
      recommendations.push('Low cache hit rate - consider improving caching strategy');
    }

    if (memoryUsage.heapUsed > 100 * 1024 * 1024) {
      // 100MB
      recommendations.push('High memory usage - consider memory optimization');
    }

    if (cacheStats.totalMemoryUsage > 50 * 1024 * 1024) {
      // 50MB
      recommendations.push('Cache memory usage is high - consider reducing cache size');
    }

    return {
      stats,
      cacheStats,
      memoryUsage,
      recommendations,
    };
  }
}

/**
 * Global performance monitor instance
 */
export const performanceMonitor = new PerformanceMonitor();

/**
 * Performance decorator for methods
 */
export function measurePerformance(operationName: string) {
  return function (target: any, propertyName: string, descriptor: PropertyDescriptor) {
    const method = descriptor.value;

    descriptor.value = async function (...args: any[]) {
      const endTiming = performanceMonitor.startTiming(`${operationName}.${propertyName}`);

      try {
        const result = await method.apply(this, args);
        endTiming();
        return result;
      } catch (error) {
        endTiming(error instanceof Error ? error.message : 'Unknown error');
        throw error;
      }
    };
  };
}

/**
 * Utility function to measure async operations
 */
export async function measureAsync<T>(
  operation: string,
  fn: () => Promise<T>,
  cacheHit?: boolean
): Promise<T> {
  const endTiming = performanceMonitor.startTiming(operation);

  try {
    const result = await fn();
    endTiming(undefined, cacheHit);
    return result;
  } catch (error) {
    endTiming(error instanceof Error ? error.message : 'Unknown error', cacheHit);
    throw error;
  }
}

/**
 * Utility function to measure sync operations
 */
export function measureSync<T>(operation: string, fn: () => T, cacheHit?: boolean): T {
  const endTiming = performanceMonitor.startTiming(operation);

  try {
    const result = fn();
    endTiming(undefined, cacheHit);
    return result;
  } catch (error) {
    endTiming(error instanceof Error ? error.message : 'Unknown error', cacheHit);
    throw error;
  }
}

/**
 * Debounce function with performance tracking
 */
export function debounce<T extends (...args: any[]) => any>(
  func: T,
  wait: number,
  operation: string = 'debounced'
): (...args: Parameters<T>) => void {
  let timeout: NodeJS.Timeout;

  return (...args: Parameters<T>) => {
    clearTimeout(timeout);
    timeout = setTimeout(() => {
      measureSync(operation, () => func(...args));
    }, wait);
  };
}

/**
 * Throttle function with performance tracking
 */
export function throttle<T extends (...args: any[]) => any>(
  func: T,
  limit: number,
  operation: string = 'throttled'
): (...args: Parameters<T>) => void {
  let inThrottle: boolean;

  return (...args: Parameters<T>) => {
    if (!inThrottle) {
      measureSync(operation, () => func(...args));
      inThrottle = true;
      setTimeout(() => (inThrottle = false), limit);
    }
  };
}

/**
 * Memory usage utilities
 */
export class MemoryOptimizer {
  private static readonly GC_THRESHOLD = 50 * 1024 * 1024; // 50MB
  private static lastGC = 0;
  private static readonly GC_INTERVAL = 30000; // 30 seconds

  /**
   * Force garbage collection if needed
   */
  static forceGCIfNeeded(): boolean {
    const memoryUsage = performanceMonitor.getMemoryUsage();
    const now = Date.now();

    if (memoryUsage.heapUsed > this.GC_THRESHOLD && now - this.lastGC > this.GC_INTERVAL) {
      if (global.gc) {
        global.gc();
        this.lastGC = now;
        return true;
      }
    }

    return false;
  }

  /**
   * Get memory usage summary
   */
  static getMemorySummary(): {
    current: MemoryUsage;
    peak: number;
    recommendations: string[];
  } {
    const current = performanceMonitor.getMemoryUsage();
    const peak = performanceMonitor.getStats().memoryUsage.peak;
    const recommendations: string[] = [];

    if (current.heapUsed > 100 * 1024 * 1024) {
      // 100MB
      recommendations.push('Consider reducing memory usage');
    }

    if (current.external > 50 * 1024 * 1024) {
      // 50MB
      recommendations.push('High external memory usage - check for memory leaks');
    }

    return {
      current,
      peak,
      recommendations,
    };
  }
}

/**
 * Performance optimization utilities
 */
export class PerformanceOptimizer {
  /**
   * Optimize work item loading with batching
   */
  static async batchWorkItems<T>(
    items: T[],
    batchSize: number = 50,
    processor: (batch: T[]) => Promise<any[]>
  ): Promise<any[]> {
    const results: any[] = [];

    for (let i = 0; i < items.length; i += batchSize) {
      const batch = items.slice(i, i + batchSize);
      const batchResults = await measureAsync(`batchWorkItems.${batchSize}`, () =>
        processor(batch)
      );
      results.push(...batchResults);
    }

    return results;
  }

  /**
   * Optimize API calls with request batching
   */
  static async batchApiCalls<T>(
    calls: (() => Promise<T>)[],
    concurrency: number = 5
  ): Promise<T[]> {
    const results: T[] = [];

    for (let i = 0; i < calls.length; i += concurrency) {
      const batch = calls.slice(i, i + concurrency);
      const batchResults = await Promise.all(
        batch.map((call) => measureAsync('batchApiCall', call))
      );
      results.push(...batchResults);
    }

    return results;
  }

  /**
   * Optimize data processing with chunking
   */
  static processInChunks<T, R>(
    data: T[],
    chunkSize: number = 1000,
    processor: (chunk: T[]) => R[]
  ): R[] {
    const results: R[] = [];

    for (let i = 0; i < data.length; i += chunkSize) {
      const chunk = data.slice(i, i + chunkSize);
      const chunkResults = measureSync(`processInChunks.${chunkSize}`, () => processor(chunk));
      results.push(...chunkResults);
    }

    return results;
  }
}

import { randomUUID } from 'crypto';
import type { ProjectConnection } from '../../machines/applicationMachine.js';

export interface LegacyConnectionFallback {
  organization?: string;
  project?: string;
  team?: string;
  label?: string;
}

export interface NormalizationDependencies {
  generateId?: () => string;
  createPatKey?: (connectionId: string) => string;
  computeDefaultBaseUrl?: (organization: string) => string;
  computeApiBaseUrl?: (baseUrl: string, organization: string, project: string) => string;
}

export interface NormalizationSummary {
  generatedIds: string[];
  addedPatKeys: string[];
  addedBaseUrls: string[];
  addedApiBaseUrls: string[];
  removedInvalidEntries: number;
}

export interface NormalizeConnectionsResult {
  connections: ProjectConnection[];
  requiresSave: boolean;
  summary: NormalizationSummary;
}

const defaultDependencies: Required<NormalizationDependencies> = {
  generateId: () =>
    typeof globalThis.crypto?.randomUUID === 'function'
      ? globalThis.crypto.randomUUID()
      : randomUUID(),
  createPatKey: (connectionId: string) => `azureDevOpsInt.pat.${connectionId}`,
  computeDefaultBaseUrl: (organization: string) => `https://dev.azure.com/${organization}`,
  computeApiBaseUrl: (baseUrl: string, organization: string, project: string) => {
    const trimmedBase = baseUrl.replace(/\/$/, '');
    const lower = trimmedBase.toLowerCase();
    if (lower.includes('dev.azure.com')) {
      return `${trimmedBase}/${organization}/${project}/_apis`;
    }
    if (lower.includes('visualstudio.com')) {
      return `${trimmedBase}/${project}/_apis`;
    }
    return `${trimmedBase}/${organization}/${project}/_apis`;
  },
};

export function normalizeConnections(
  rawConnections: unknown[],
  legacyFallback: LegacyConnectionFallback | undefined,
  dependencies: NormalizationDependencies = {}
): NormalizeConnectionsResult {
  const deps = { ...defaultDependencies, ...filterUndefined(dependencies) };
  const normalized: ProjectConnection[] = [];
  const generatedIds: string[] = [];
  const addedPatKeys: string[] = [];
  const addedBaseUrls: string[] = [];
  const addedApiBaseUrls: string[] = [];
  let removedInvalidEntries = 0;

  for (const raw of rawConnections) {
    const result = sanitizeConnection(raw, deps);
    if (!result.connection) {
      if (result.removed) {
        removedInvalidEntries += 1;
      }
      continue;
    }

    if (result.generatedId) {
      generatedIds.push(result.connection.id);
    }
    if (result.addedPatKey) {
      addedPatKeys.push(result.connection.id);
    }
    if (result.addedBaseUrl) {
      addedBaseUrls.push(result.connection.id);
    }
    if (result.addedApiBaseUrl) {
      addedApiBaseUrls.push(result.connection.id);
    }

    normalized.push(result.connection);
  }

  if (
    normalized.length === 0 &&
    legacyFallback &&
    legacyFallback.organization &&
    legacyFallback.project
  ) {
    const fallbackId = deps.generateId();
    normalized.push({
      id: fallbackId,
      organization: legacyFallback.organization,
      project: legacyFallback.project,
      team: legacyFallback.team,
      label: legacyFallback.label,
      authMethod: undefined,
      patKey: deps.createPatKey(fallbackId),
      baseUrl: deps.computeDefaultBaseUrl(legacyFallback.organization),
      apiBaseUrl: deps.computeApiBaseUrl(
        deps.computeDefaultBaseUrl(legacyFallback.organization),
        legacyFallback.organization,
        legacyFallback.project
      ),
    });
    generatedIds.push(fallbackId);
    addedPatKeys.push(fallbackId);
    addedBaseUrls.push(fallbackId);
    addedApiBaseUrls.push(fallbackId);
  }

  const requiresSave =
    generatedIds.length > 0 ||
    addedPatKeys.length > 0 ||
    addedBaseUrls.length > 0 ||
    addedApiBaseUrls.length > 0;

  return {
    connections: normalized,
    requiresSave,
    summary: {
      generatedIds,
      addedPatKeys,
      addedBaseUrls,
      addedApiBaseUrls,
      removedInvalidEntries,
    },
  };
}

export interface ResolveActiveConnectionResult {
  activeConnectionId?: string;
  requiresPersistence: boolean;
  reason: 'persisted' | 'defaulted-first' | 'cleared';
}

export function resolveActiveConnectionId(
  persistedActiveId: string | undefined,
  connections: ProjectConnection[]
): ResolveActiveConnectionResult {
  if (connections.length === 0) {
    return {
      activeConnectionId: undefined,
      requiresPersistence: Boolean(persistedActiveId),
      reason: 'cleared',
    };
  }

  if (persistedActiveId) {
    const exists = connections.some((connection) => connection.id === persistedActiveId);
    if (exists) {
      return {
        activeConnectionId: persistedActiveId,
        requiresPersistence: false,
        reason: 'persisted',
      };
    }
  }

  return {
    activeConnectionId: connections[0]?.id,
    requiresPersistence: true,
    reason: 'defaulted-first',
  };
}

interface SanitizeResult {
  connection: ProjectConnection | null;
  generatedId: boolean;
  addedPatKey: boolean;
  addedBaseUrl: boolean;
  addedApiBaseUrl: boolean;
  removed: boolean;
}

function sanitizeConnection(
  raw: unknown,
  deps: Required<NormalizationDependencies>
): SanitizeResult {
  if (!raw || typeof raw !== 'object') {
    return emptyResult(true);
  }

  const record = raw as Record<string, unknown>;
  const organization = readString(record.organization);
  const project = readString(record.project);
  if (!organization || !project) {
    return emptyResult(true);
  }

  const id = readString(record.id) ?? deps.generateId();
  const generatedId = !readString(record.id);

  const label = readOptionalString(record.label);
  const team = readOptionalString(record.team);
  const tenantId = readOptionalString(record.tenantId);
  const identityName = readOptionalString(record.identityName);

  const authMethodRaw = readOptionalString(record.authMethod);
  const authMethod = deriveAuthMethod(authMethodRaw, tenantId);

  let patKey = readOptionalString(record.patKey);
  let addedPatKey = false;
  if (!patKey && authMethod !== 'entra') {
    patKey = deps.createPatKey(id);
    addedPatKey = true;
  }

  let baseUrl = readOptionalString(record.baseUrl);
  let addedBaseUrl = false;
  if (!baseUrl) {
    baseUrl = deps.computeDefaultBaseUrl(organization);
    addedBaseUrl = true;
  }

  let apiBaseUrl = readOptionalString(record.apiBaseUrl);
  let addedApiBaseUrl = false;
  if (!apiBaseUrl && baseUrl) {
    apiBaseUrl = deps.computeApiBaseUrl(baseUrl, organization, project);
    addedApiBaseUrl = true;
  }

  const connection: ProjectConnection = {
    id,
    organization,
    project,
    team: team ?? undefined,
    label: label ?? undefined,
    authMethod: authMethod ?? undefined,
    patKey: patKey ?? undefined,
    baseUrl: baseUrl ?? undefined,
    apiBaseUrl: apiBaseUrl ?? undefined,
    tenantId: tenantId ?? undefined,
    identityName: identityName ?? undefined,
  };

  return {
    connection,
    generatedId,
    addedPatKey,
    addedBaseUrl,
    addedApiBaseUrl,
    removed: false,
  };
}

function deriveAuthMethod(
  authMethod: string | undefined,
  tenantId: string | undefined
): 'pat' | 'entra' | undefined {
  if (authMethod === 'entra' || authMethod === 'pat') {
    return authMethod;
  }
  if (tenantId) {
    return 'entra';
  }
  return undefined;
}

function readString(value: unknown): string | null {
  if (typeof value !== 'string') {
    return null;
  }
  const trimmed = value.trim();
  return trimmed.length > 0 ? trimmed : null;
}

function readOptionalString(value: unknown): string | undefined {
  const result = readString(value);
  return result === null ? undefined : result;
}

function emptyResult(removed: boolean): SanitizeResult {
  return {
    connection: null,
    generatedId: false,
    addedPatKey: false,
    addedBaseUrl: false,
    addedApiBaseUrl: false,
    removed,
  };
}

function filterUndefined<T extends object>(input: T): Partial<T> {
  const result: Partial<T> = {};
  for (const [key, value] of Object.entries(input)) {
    if (value !== undefined) {
      (result as Record<string, unknown>)[key] = value as unknown;
    }
  }
  return result;
}

