/**
 * FSM-based Setup UI Integration
 * Handles VS Code UI interactions for the setup machine
 */

import * as vscode from 'vscode';
import { createActor } from 'xstate';
import {
  setupMachine,
  type FSMSetupResult,
  type SetupMachineContext,
} from '../machines/setupMachine.js';

// Define the connection type locally since it's defined in activation.ts
type ProjectConnection = {
  id: string;
  organization: string;
  project: string;
  label?: string;
  team?: string;
  authMethod?: 'pat' | 'entra';
  patKey?: string;
  baseUrl?: string;
  identityName?: string;
  tenantId?: string;
  clientId?: string;
  apiBaseUrl?: string;
};

export class FSMSetupService {
  private context: vscode.ExtensionContext;

  constructor(context: vscode.ExtensionContext) {
    this.context = context;
  }

  async startSetup(options?: { skipInitialChoice?: boolean }): Promise<FSMSetupResult> {
    // Load connections from workspace configuration (not globalState!)
    const settings = vscode.workspace.getConfiguration('azureDevOpsIntegration');
    const existingConnections = settings.get<ProjectConnection[]>('connections', []);
    const activeConnectionId = this.context.globalState.get<string>(
      'azureDevOpsInt.activeConnectionId'
    );

    console.log('[FSMSetupService] Starting setup with:', {
      connectionsCount: existingConnections.length,
      activeConnectionId,
      connectionAuthMethods: existingConnections.map((c) => ({
        id: c.id,
        authMethod: c.authMethod,
      })),
    });

    // Create and start the setup machine
    const actor = createActor(setupMachine, {
      input: {
        extensionContext: this.context,
        existingConnections,
        activeConnectionId,
      } as any,
    });

    actor.start();

    // Send start event
    actor.send({ type: 'START', skipInitialChoice: options?.skipInitialChoice });

    return new Promise<FSMSetupResult>((resolve) => {
      // Subscribe to state changes and handle UI interactions
      actor.subscribe((state) => {
        this.handleStateChange(state, actor, resolve);
      });
    });
  }

  private async handleStateChange(
    state: any,
    actor: any,
    resolve: (result: FSMSetupResult) => void
  ): Promise<void> {
    const context = state.context as SetupMachineContext;

    switch (state.value) {
      case 'collectingUrl':
        await this.showUrlInput(actor);
        break;

      case 'urlError':
        await this.showUrlError(context.error!, actor);
        break;

      case 'selectingAuth':
        await this.showAuthMethodSelection(actor);
        break;

      case 'collectingCredentials':
        await this.showCredentialsInput(context, actor);
        break;

      case 'connectionError':
        await this.showConnectionError(context.error!, actor);
        break;

      case 'saveError':
        await this.showSaveError(context.error!, actor);
        break;

      case 'managingExisting':
        await this.showExistingConnections(context, actor);
        break;

      case 'removeError':
        await this.showRemoveError(context.error!, actor);
        break;

      case 'completed':
        resolve({
          status: 'success',
          connectionId: context.connectionId,
        });
        break;

      case 'removed':
        resolve({
          status: 'removed',
          removedConnectionId: context.removedConnectionId,
        });
        break;

      case 'cancelled':
        resolve({ status: 'cancelled' });
        break;

      case 'testingConnection':
        vscode.window.showInformationMessage('Testing connection...');
        break;

      case 'testingExistingConnection':
        vscode.window.showInformationMessage('Testing connection...');
        break;

      case 'testingExistingResult':
        await this.showExistingTestResult(context, actor);
        break;

      case 'savingConnection':
        vscode.window.showInformationMessage('Saving connection...');
        break;

      case 'removingConnection':
        vscode.window.showInformationMessage('Removing connection...');
        break;
    }
  }

  private async showUrlInput(actor: any): Promise<void> {
    const url = await vscode.window.showInputBox({
      prompt: 'Enter an Azure DevOps work item URL to auto-configure your connection',
      placeHolder: 'https://dev.azure.com/myorg/myproject/_workitems/edit/123',
      validateInput: (value) => {
        if (!value) return 'URL is required';
        if (!value.includes('_workitems')) return 'Please provide a work item URL';
        return null;
      },
    });

    if (url) {
      actor.send({ type: 'URL_PROVIDED', url });
    } else {
      // Check if user wants to manage existing connections
      const manageExisting = await vscode.window.showQuickPick(
        ['Cancel', 'Manage Existing Connections'],
        { placeHolder: 'What would you like to do?' }
      );

      if (manageExisting === 'Manage Existing Connections') {
        actor.send({ type: 'MANAGE_EXISTING' });
      } else {
        actor.send({ type: 'CANCEL' });
      }
    }
  }

  private async showUrlError(error: string, actor: any): Promise<void> {
    const action = await vscode.window.showErrorMessage(
      `Invalid URL: ${error}`,
      'Try Again',
      'Cancel'
    );

    if (action === 'Try Again') {
      actor.send({ type: 'RETRY' });
    } else {
      actor.send({ type: 'CANCEL' });
    }
  }

  private async showAuthMethodSelection(actor: any): Promise<void> {
    const authMethod = await vscode.window.showQuickPick(
      [
        {
          label: 'Personal Access Token (PAT)',
          description: 'Use a Personal Access Token for authentication',
          detail: 'Recommended for most users. Generate a PAT from Azure DevOps settings.',
          value: 'pat',
        },
        {
          label: 'Microsoft Entra ID (OAuth)',
          description: 'Use your Microsoft account to sign in',
          detail: 'Enterprise authentication using your organizational account.',
          value: 'entra',
        },
      ],
      {
        placeHolder: 'Choose authentication method',
        matchOnDescription: true,
        matchOnDetail: true,
      }
    );

    if (authMethod) {
      actor.send({ type: 'AUTH_METHOD_SELECTED', method: authMethod.value as 'pat' | 'entra' });
    } else {
      actor.send({ type: 'CANCEL' });
    }
  }

  private async showCredentialsInput(context: SetupMachineContext, actor: any): Promise<void> {
    if (context.authMethod === 'pat') {
      await this.showPATInput(actor);
    } else if (context.authMethod === 'entra') {
      await this.showEntraConfig(actor);
    }
  }

  private async showPATInput(actor: any): Promise<void> {
    const token = await vscode.window.showInputBox({
      prompt: 'Enter your Personal Access Token',
      password: true,
      placeHolder: 'Your PAT from Azure DevOps',
      validateInput: (value) => {
        if (!value) return 'PAT is required';
        if (value.length < 20) return 'PAT seems too short';
        return null;
      },
    });

    if (token) {
      actor.send({ type: 'PAT_PROVIDED', token });
    } else {
      actor.send({ type: 'CANCEL' });
    }
  }

  private async showEntraConfig(actor: any): Promise<void> {
    // For Entra ID, we need tenant ID and client ID
    const tenantId = await vscode.window.showInputBox({
      prompt: 'Enter your Tenant ID (Directory ID)',
      placeHolder: 'xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx',
      validateInput: (value) => {
        if (!value) return 'Tenant ID is required';
        // Basic UUID format validation
        if (!/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(value)) {
          return 'Invalid Tenant ID format';
        }
        return null;
      },
    });

    if (!tenantId) {
      actor.send({ type: 'CANCEL' });
      return;
    }

    const clientId = await vscode.window.showInputBox({
      prompt: 'Enter your Application (Client) ID',
      placeHolder: 'xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx',
      validateInput: (value) => {
        if (!value) return 'Client ID is required';
        // Basic UUID format validation
        if (!/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(value)) {
          return 'Invalid Client ID format';
        }
        return null;
      },
    });

    if (clientId) {
      actor.send({ type: 'ENTRA_CONFIG', tenantId, clientId });
    } else {
      actor.send({ type: 'CANCEL' });
    }
  }

  private async showConnectionError(error: string, actor: any): Promise<void> {
    const action = await vscode.window.showErrorMessage(
      `Connection test failed: ${error}`,
      'Try Again',
      'Go Back',
      'Cancel'
    );

    switch (action) {
      case 'Try Again':
        actor.send({ type: 'RETRY' });
        break;
      case 'Go Back':
        actor.send({ type: 'BACK' });
        break;
      default:
        actor.send({ type: 'CANCEL' });
        break;
    }
  }

  private async showSaveError(error: string, actor: any): Promise<void> {
    const action = await vscode.window.showErrorMessage(
      `Failed to save connection: ${error}`,
      'Try Again',
      'Cancel'
    );

    if (action === 'Try Again') {
      actor.send({ type: 'RETRY' });
    } else {
      actor.send({ type: 'CANCEL' });
    }
  }

  private async showExistingConnections(context: SetupMachineContext, actor: any): Promise<void> {
    const connections = this.context.globalState.get<ProjectConnection[]>(
      'azureDevOpsInt.connections',
      []
    );

    if (connections.length === 0) {
      vscode.window.showInformationMessage('No existing connections found.');
      actor.send({ type: 'CANCEL' });
      return;
    }

    const items = connections.map((conn) => ({
      label: conn.label || `${conn.organization}/${conn.project}`,
      description: conn.authMethod === 'pat' ? 'Personal Access Token' : 'Microsoft Entra ID',
      detail: conn.baseUrl,
      connection: conn,
    }));

    items.push({
      label: '$(add) Add New Connection',
      description: 'Create a new Azure DevOps connection',
      detail: 'Set up a new connection to Azure DevOps',
      connection: null as any,
    });

    const selected = await vscode.window.showQuickPick(items, {
      placeHolder: 'Select a connection to manage or add a new one',
      matchOnDescription: true,
      matchOnDetail: true,
    });

    if (!selected) {
      actor.send({ type: 'CANCEL' });
      return;
    }

    if (!selected.connection) {
      // Add new connection - restart the flow
      actor.send({ type: 'CANCEL' });
      // Start a new setup flow
      const newSetup = new FSMSetupService(this.context);
      await newSetup.startSetup();
      return;
    }

    // Show connection management options
    const action = await vscode.window.showQuickPick(
      [
        { label: 'Remove Connection', value: 'remove', description: 'Delete this connection' },
        {
          label: 'Test Connection',
          value: 'test',
          description: 'Test if this connection is working',
        },
      ],
      {
        placeHolder: `Manage connection: ${selected.label}`,
      }
    );

    if (action?.value === 'remove') {
      const confirm = await vscode.window.showWarningMessage(
        `Are you sure you want to remove the connection "${selected.label}"?`,
        { modal: true },
        'Yes, Remove'
      );

      if (confirm === 'Yes, Remove') {
        actor.send({ type: 'REMOVE_CONNECTION', connectionId: selected.connection.id });
      } else {
        actor.send({ type: 'CANCEL' });
      }
    } else if (action?.value === 'test') {
      // TODO: Implement connection test
      vscode.window.showInformationMessage('Connection test not yet implemented in FSM setup');
      actor.send({ type: 'CANCEL' });
    } else {
      actor.send({ type: 'CANCEL' });
    }
  }

  private async showExistingTestResult(context: SetupMachineContext, actor: any): Promise<void> {
    const result = context.lastExistingTestResult;
    const connection = context.testingExistingConnection;

    if (!result || !connection) {
      actor.send({ type: 'CONTINUE_MANAGING' });
      return;
    }

    const connectionLabel =
      connection.label ||
      (connection.organization && connection.project
        ? `${connection.organization}/${connection.project}`
        : connection.id || 'connection');

    if (result.success) {
      await vscode.window.showInformationMessage(
        `Connection "${connectionLabel}" succeeded. ${result.message}`
      );
      actor.send({ type: 'CONTINUE_MANAGING' });
      return;
    }

    const choice = await vscode.window.showErrorMessage(
      `Connection test failed for "${connectionLabel}". ${result.message}`,
      'Retry',
      'Back'
    );

    if (choice === 'Retry') {
      actor.send({ type: 'RETRY' });
    } else {
      actor.send({ type: 'CONTINUE_MANAGING' });
    }
  }

  private async showRemoveError(error: string, actor: any): Promise<void> {
    const action = await vscode.window.showErrorMessage(
      `Failed to remove connection: ${error}`,
      'Try Again',
      'Cancel'
    );

    if (action === 'Try Again') {
      actor.send({ type: 'RETRY' });
    } else {
      actor.send({ type: 'CANCEL' });
    }
  }
}

/**
 * FSM-based Connection Setup Machine
 * Replaces legacy SetupWizard with pure FSM architecture
 */

import { createMachine, assign, fromPromise } from 'xstate';
import { showSetupMenu, SetupAction } from '../functions/setup/ui.js';
import { createScopedLogger } from '../../logging.js';
import { addOrEditConnection } from '../functions/setup/connection.js';
import { manageConnections } from '../functions/setup/manage.js';
import { switchConnection } from '../functions/setup/switch.js';
import { convertConnectionToEntra } from '../functions/setup/convert.js';
import { signInWithEntra, signOutEntra } from '../functions/setup/auth.js';
import type { ProjectConnection } from './applicationMachine.js';
import type * as vscode from 'vscode';

const fsmLogger = createScopedLogger('setupMachine');

enum _FSMComponent {
  SETUP = 'SETUP',
}

interface SetupMachineContext {
  // Existing action selection (menu-based)
  selectedAction?: SetupAction;
  // VS Code extension context provided via machine input
  extensionContext: vscode.ExtensionContext;
  // Existing connections (not used by current tests but required by broader setup flow)
  connections: ProjectConnection[];
  saveConnections: (connections: ProjectConnection[]) => Promise<void>;
  ensureActiveConnection: (
    context: vscode.ExtensionContext,
    connectionId?: string,
    options?: { refresh?: boolean }
  ) => Promise<any>;
  // Fields needed for existing connection testing flow (tests expect these)
  testingExistingConnection?: ProjectConnection;
  lastExistingTestResult?: { success: boolean; message: string };
  error?: string;
}

export const setupMachine = createMachine(
  {
    id: 'setup',
    initial: 'idle',
    // Initialize context from machine input to satisfy tests supplying extensionContext only
    context: ({ input }): SetupMachineContext => ({
      extensionContext: (input as any)?.extensionContext,
      connections: (input as any)?.existingConnections || [],
      activeConnectionId: (input as any)?.activeConnectionId,
      saveConnections: async () => {},
      ensureActiveConnection: async () => {},
    }),
    states: {
      idle: {
        on: {
          SETUP_REQUESTED: 'showingMenu',
          START: [
            { target: 'managingExisting', guard: 'skipInitialChoice' },
            { target: 'showingMenu' },
          ],
        },
      },
      // Original menu-driven flow (unchanged)
      showingMenu: {
        invoke: {
          id: 'showSetupMenu',
          src: 'showSetupMenu',
          input: ({ context }) => ({
            connections: context.connections,
            activeConnectionId: context.activeConnectionId,
          }),
          onDone: {
            target: 'handlingAction',
            actions: assign({
              selectedAction: ({ event }) => event.output,
            }),
          },
          onError: {
            target: 'idle',
            actions: 'logError',
          },
        },
      },
      handlingAction: {
        always: [
          { target: 'idle', guard: 'isAdd', actions: ['navigateToAdd'] },
          { target: 'idle', guard: 'isManage', actions: ['navigateToManage'] },
          { target: 'idle', guard: 'isSwitch', actions: ['navigateToSwitch'] },
          { target: 'idle', guard: 'isEntraSignIn', actions: ['signInWithEntra'] },
          { target: 'idle', guard: 'isEntraSignOut', actions: ['signOutEntra'] },
          { target: 'idle', guard: 'isConvertToEntra', actions: ['convertToEntra'] },
          { target: 'idle' }, // Default
        ],
      },
      // New state expected by tests when managing existing connections directly
      managingExisting: {
        on: {
          TEST_EXISTING_CONNECTION: {
            target: 'testingExistingConnection',
            actions: assign({
              testingExistingConnection: ({ event }) => event.connection,
            }),
          },
          CANCEL: 'idle',
        },
      },
      testingExistingConnection: {
        invoke: {
          id: 'testExistingConnection',
          src: fromPromise(({ input }) => {
            const { connection, extensionContext } = input as {
              connection?: ProjectConnection;
              extensionContext: vscode.ExtensionContext;
            };
            return (async () => {
              if (!connection) {
                return { success: false, message: 'No connection provided' };
              }
              try {
                const pat = await extensionContext.secrets.get('azureDevOpsInt.pat');
                if (!pat) {
                  return { success: false, message: 'Personal Access Token missing' };
                }
                const base = connection.apiBaseUrl || connection.baseUrl || '';
                const testUrl = base.replace(/\/$/, '') + '/wit/workitemtypes/Task?api-version=7.0';
                const res = await fetch(testUrl);
                if (res.status >= 200 && res.status < 300) {
                  return { success: true, message: 'Connection test succeeded' };
                }
                return { success: false, message: `Connection test failed (status ${res.status})` };
              } catch (err: any) {
                return { success: false, message: err?.message || 'Connection test exception' };
              }
            })();
          }),
          input: ({ context }) => ({
            connection: context.testingExistingConnection,
            extensionContext: context.extensionContext,
          }),
          onDone: {
            target: 'testingExistingResult',
            actions: assign({
              lastExistingTestResult: ({ event }) => event.output,
            }),
          },
          onError: {
            target: 'testingExistingResult',
            actions: assign({
              lastExistingTestResult: ({ event }) => ({
                success: false,
                message: (event as any).error?.message || 'Connection test failed',
              }),
            }),
          },
        },
      },
      testingExistingResult: {
        on: {
          RETRY: 'testingExistingConnection',
          CONTINUE_MANAGING: {
            target: 'managingExisting',
            actions: assign({ testingExistingConnection: () => undefined }),
          },
        },
      },
    },
  },
  {
    actions: {
      logError: ({ event }) => {
        fsmLogger.error('Error in setup machine', {
          error: event.data,
        });
      },
      navigateToAdd: async ({ context }) => {
        await addOrEditConnection(
          context.extensionContext,
          context.connections,
          context.saveConnections,
          context.ensureActiveConnection
        );
      },
      navigateToManage: async ({ context }) => {
        await manageConnections(
          context.extensionContext,
          context.connections,
          context.saveConnections,
          context.ensureActiveConnection
        );
      },
      navigateToSwitch: async ({ context }) => {
        await switchConnection(
          context.extensionContext,
          context.connections,
          context.ensureActiveConnection
        );
      },
      signInWithEntra: async ({ context }) => {
        await signInWithEntra(
          context.connections,
          context.extensionContext.globalState.get('azureDevOpsInt.activeConnectionId')
        );
      },
      signOutEntra: async ({ context }) => {
        await signOutEntra(
          context.connections,
          context.extensionContext.globalState.get('azureDevOpsInt.activeConnectionId')
        );
      },
      convertToEntra: async ({ context }) => {
        await convertConnectionToEntra(
          context.extensionContext,
          context.connections,
          context.saveConnections,
          context.ensureActiveConnection
        );
      },
    },
    actors: {
      showSetupMenu: fromPromise(async ({ input }: { input: any }) => {
        return showSetupMenu({
          connections: input?.connections || [],
          activeConnectionId: input?.activeConnectionId,
        });
      }),
    },
    guards: {
      isAdd: ({ context }) => context.selectedAction === 'add',
      isManage: ({ context }) => context.selectedAction === 'manage',
      isSwitch: ({ context }) => context.selectedAction === 'switch',
      isEntraSignIn: ({ context }) => context.selectedAction === 'entraSignIn',
      isEntraSignOut: ({ context }) => context.selectedAction === 'entraSignOut',
      isConvertToEntra: ({ context }) => context.selectedAction === 'convertToEntra',
      skipInitialChoice: ({ event }) => !!(event as any).skipInitialChoice,
    },
  }
);

import * as vscode from 'vscode';
import type { ProjectConnection } from '../../machines/applicationMachine.js';

export type SetupAction =
  | 'add'
  | 'manage'
  | 'switch'
  | 'entraSignIn'
  | 'entraSignOut'
  | 'convertToEntra';

export interface ShowSetupMenuOptions {
  connections?: ProjectConnection[];
  activeConnectionId?: string;
}

export async function showSetupMenu(
  options: ShowSetupMenuOptions = {}
): Promise<SetupAction | undefined> {
  const { connections = [], activeConnectionId } = options;

  // Determine active connection's auth method
  const activeConnection = connections.find((c) => c.id === activeConnectionId);
  const activeAuthMethod = activeConnection?.authMethod || 'pat';
  const hasPatConnections = connections.some((c) => c.authMethod !== 'entra');
  const _hasEntraConnections = connections.some((c) => c.authMethod === 'entra');

  // Build menu items based on context
  const menuItems: Array<{ label: string; action: SetupAction }> = [
    { label: 'Add new connection', action: 'add' as SetupAction },
    { label: 'Manage existing connections', action: 'manage' as SetupAction },
    { label: 'Switch active connection', action: 'switch' as SetupAction },
  ];

  // Add Entra-specific options only if active connection is Entra
  if (activeAuthMethod === 'entra') {
    menuItems.push({
      label: '$(sign-in) Sign in with Microsoft Entra ID',
      action: 'entraSignIn' as SetupAction,
    });
    menuItems.push({
      label: '$(sign-out) Sign out from Microsoft Entra ID',
      action: 'entraSignOut' as SetupAction,
    });
  }

  // Add convert option only if there are PAT connections
  if (hasPatConnections) {
    menuItems.push({
      label: 'Convert PAT connection to Entra ID',
      action: 'convertToEntra' as SetupAction,
    });
  }

  const action = await vscode.window.showQuickPick(menuItems, {
    placeHolder: 'What would you like to do?',
  });

  return action?.action;
}

import * as vscode from 'vscode';
import { randomUUID } from 'crypto';
import { parseAzureDevOpsUrl } from '../../../azureDevOpsUrlParser.js';
import type { ProjectConnection } from '../../machines/applicationMachine.js';
import { AzureDevOpsIntClient } from '../../../azureClient.js';

async function testPATConnection(
  organization: string,
  project: string,
  pat: string,
  baseUrl?: string
): Promise<{ success: boolean; message?: string }> {
  try {
    const client = new AzureDevOpsIntClient(organization, project, pat, { baseUrl });
    const userId = await client.getAuthenticatedUserId();
    if (userId) {
      return { success: true };
    } else {
      return {
        success: false,
        message: 'Authentication failed. Could not retrieve user identity.',
      };
    }
  } catch (error: any) {
    return { success: false, message: error.message };
  }
}

export async function addOrEditConnection(
  context: vscode.ExtensionContext,
  connections: ProjectConnection[],
  saveFn: (connections: ProjectConnection[]) => Promise<void>,
  ensureActiveFn: (
    context: vscode.ExtensionContext,
    connectionId?: string,
    options?: { refresh?: boolean }
  ) => Promise<any>,
  connectionToEdit?: ProjectConnection
): Promise<void> {
  const isEditing = !!connectionToEdit;
  const urlValue =
    connectionToEdit && connectionToEdit.organization && connectionToEdit.project
      ? `https://dev.azure.com/${connectionToEdit.organization}/${connectionToEdit.project}`
      : '';

  const url = await vscode.window.showInputBox({
    prompt: isEditing
      ? `Editing connection: ${connectionToEdit.label || connectionToEdit.id}`
      : 'Enter an Azure DevOps work item URL to auto-configure your connection',
    value: urlValue,
    placeHolder: 'https://dev.azure.com/myorg/myproject/_workitems/edit/123',
  });

  if (!url) return;

  const parsed = parseAzureDevOpsUrl(url);
  if (!parsed) {
    vscode.window.showErrorMessage('Invalid Azure DevOps URL');
    return;
  }

  const { organization, project, baseUrl } = parsed;

  const authMethod = (await vscode.window.showQuickPick(
    ['Personal Access Token (PAT)', 'Microsoft Entra ID'],
    {
      placeHolder: 'Choose authentication method',
    }
  )) as 'Personal Access Token (PAT)' | 'Microsoft Entra ID' | undefined;

  if (!authMethod) return;

  const newOrUpdatedConnection: ProjectConnection = {
    id: connectionToEdit?.id || randomUUID(),
    organization,
    project,
    baseUrl,
    authMethod: authMethod === 'Personal Access Token (PAT)' ? 'pat' : 'entra',
    label: `${organization}/${project}`,
  };

  if (newOrUpdatedConnection.authMethod === 'pat') {
    const pat = await vscode.window.showInputBox({
      prompt: 'Enter your Personal Access Token',
      password: true,
    });
    if (!pat) return;

    const testResult = await testPATConnection(organization, project, pat, baseUrl);
    if (!testResult.success) {
      vscode.window.showErrorMessage(`Connection test failed: ${testResult.message}`);
      return;
    }

    const patKey = `azureDevOpsInt.pat:${newOrUpdatedConnection.id}`;
    newOrUpdatedConnection.patKey = patKey; // CRITICAL: Add patKey to connection object
    await context.secrets.store(patKey, pat);
  }

  const newConnections = [...connections];
  const existingIndex = newConnections.findIndex((c) => c.id === newOrUpdatedConnection.id);

  if (existingIndex > -1) {
    newConnections[existingIndex] = newOrUpdatedConnection;
  } else {
    newConnections.push(newOrUpdatedConnection);
  }

  await saveFn(newConnections);
  await ensureActiveFn(context, newOrUpdatedConnection.id, { refresh: true });

  vscode.window.showInformationMessage(
    `Connection ${isEditing ? 'updated' : 'added'} successfully.`
  );
}

export interface ParsedAzureDevOpsUrl {
  organization: string;
  project: string;
  baseUrl: string;
  apiBaseUrl: string;
  workItemId?: number;
  isValid: boolean;
  error?: string;
}

const UNSUPPORTED_URL_ERROR =
  'Unsupported URL format. Provide an Azure DevOps URL that includes the organization and project.';

function trimSegment(segment: string | undefined): string {
  return typeof segment === 'string' ? segment.trim() : '';
}

function buildApiBaseUrl(
  baseUrl: string,
  organization: string,
  project: string,
  isSimplifiedOnPrem: boolean = false
): string {
  const trimmedBase = baseUrl.replace(/\/$/, '');
  const lowerBase = trimmedBase.toLowerCase();

  if (
    lowerBase.includes('visualstudio.com') ||
    lowerBase.endsWith('.dev.azure.com') ||
    lowerBase.includes('dev.azure.com/') ||
    lowerBase === 'https://dev.azure.com'
  ) {
    return `https://dev.azure.com/${organization}/${project}/_apis`;
  }

  if (lowerBase.includes('dev.azure.com')) {
    return `https://dev.azure.com/${organization}/${project}/_apis`;
  }

  const apiUrl = isSimplifiedOnPrem
    ? `${trimmedBase}/${project}/_apis`
    : `${trimmedBase}/${organization}/${project}/_apis`;
  return apiUrl;
}

function extractWorkItemId(segments: string[], searchParams: URLSearchParams): number | undefined {
  const workItemsIndex = segments.findIndex((segment) => segment.toLowerCase() === '_workitems');
  if (workItemsIndex >= 0) {
    const next = segments[workItemsIndex + 1]?.toLowerCase();
    if (next === 'edit' || next === 'view') {
      const idSegment = segments[workItemsIndex + 2];
      const parsed = Number(idSegment);
      if (!Number.isNaN(parsed)) {
        return parsed;
      }
    }
  }

  const queryId = searchParams.get('id');
  if (queryId) {
    const parsed = Number(queryId);
    if (!Number.isNaN(parsed)) {
      return parsed;
    }
  }

  return undefined;
}

function makeInvalid(error: string): ParsedAzureDevOpsUrl {
  return {
    organization: '',
    project: '',
    baseUrl: '',
    apiBaseUrl: '',
    isValid: false,
    error,
  };
}

function splitPathSegments(parsed: URL): {
  segments: string[];
  pathSegments: string[];
  trailingSegments: string[];
} {
  const segments = parsed.pathname
    .split('/')
    .map((segment) => segment.trim())
    .filter((segment) => segment.length > 0);

  const specialIndex = segments.findIndex((segment) => segment.startsWith('_'));
  if (specialIndex < 0) {
    return { segments, pathSegments: segments, trailingSegments: [] };
  }

  return {
    segments,
    pathSegments: segments.slice(0, specialIndex),
    trailingSegments: segments.slice(specialIndex),
  };
}

function resolveCloudRoot(
  parsed: URL,
  pathSegments: string[]
): { organization: string; project: string; baseUrl: string } | null {
  if (pathSegments.length < 2) {
    return null;
  }

  const organization = trimSegment(pathSegments[0]);
  const project = trimSegment(pathSegments[1]);
  if (!organization || !project) {
    return null;
  }

  return {
    organization,
    project,
    baseUrl: `${parsed.protocol}//${parsed.host}/${organization}`,
  };
}

function resolveCloudSubdomain(
  parsed: URL,
  pathSegments: string[]
): { organization: string; project: string; baseUrl: string } | null {
  const parts = parsed.hostname.split('.');
  const organization = trimSegment(parts[0]);
  const project = trimSegment(pathSegments[0]);
  if (!organization || !project) {
    return null;
  }

  return {
    organization,
    project,
    baseUrl: `${parsed.protocol}//${parsed.host}`,
  };
}

function resolveVisualStudio(
  parsed: URL,
  pathSegments: string[]
): { organization: string; project: string; baseUrl: string } | null {
  const parts = parsed.hostname.split('.');
  const organization = trimSegment(parts[0]);
  const project = trimSegment(pathSegments[0]);
  if (!organization || !project) {
    return null;
  }

  const canonicalHost = parsed.hostname.includes('.vsrm.')
    ? parsed.hostname.replace('.vsrm.', '.')
    : parsed.hostname;

  return {
    organization,
    project,
    baseUrl: `${parsed.protocol}//${canonicalHost}`,
  };
}

function resolveOnPrem(
  parsed: URL,
  pathSegments: string[]
): {
  organization: string;
  project: string;
  baseUrl: string;
  isSimplified: boolean;
} | null {
  if (pathSegments.length < 2) {
    return null;
  }

  const collection = trimSegment(pathSegments[0]);
  const project = trimSegment(pathSegments[pathSegments.length - 1]);
  if (!collection || !project) {
    return null;
  }

  const hasSeparateOrg = pathSegments.length >= 3;
  const organization = hasSeparateOrg
    ? trimSegment(pathSegments[pathSegments.length - 2])
    : collection;
  if (!organization) {
    return null;
  }

  return {
    organization,
    project,
    baseUrl: `${parsed.protocol}//${parsed.host}/${collection}`,
    isSimplified: !hasSeparateOrg,
  };
}

export function parseAzureDevOpsUrl(url: string): ParsedAzureDevOpsUrl {
  if (!url || typeof url !== 'string' || !url.trim()) {
    return makeInvalid('Invalid URL: URL must be a non-empty string');
  }

  let parsed: URL;
  try {
    parsed = new URL(url.trim());
  } catch {
    return makeInvalid('Invalid URL: URL must include a valid protocol (http or https)');
  }

  const { segments, pathSegments, trailingSegments } = splitPathSegments(parsed);
  if (trailingSegments.length === 0) {
    return makeInvalid(UNSUPPORTED_URL_ERROR);
  }

  const firstSpecial = trailingSegments[0].toLowerCase();
  const isWorkItemPath = firstSpecial === '_workitems';
  const isBoardsPath = firstSpecial === '_boards';
  if (!isWorkItemPath && !isBoardsPath) {
    return makeInvalid(UNSUPPORTED_URL_ERROR);
  }

  const host = parsed.hostname.toLowerCase();
  const isCloudRoot = host === 'dev.azure.com' || host === 'vsrm.dev.azure.com';
  const isCloudSubdomain = host.endsWith('.dev.azure.com');
  const isVisualStudio =
    host.endsWith('.visualstudio.com') || host.endsWith('.vsrm.visualstudio.com');

  let organization = '';
  let project = '';
  let baseUrl = '';
  let isSimplifiedOnPrem = false;

  if (isCloudRoot) {
    const resolved = resolveCloudRoot(parsed, pathSegments);
    if (!resolved) {
      return makeInvalid(UNSUPPORTED_URL_ERROR);
    }
    organization = resolved.organization;
    project = resolved.project;
    baseUrl = resolved.baseUrl;
  } else if (isCloudSubdomain) {
    const resolved = resolveCloudSubdomain(parsed, pathSegments);
    if (!resolved) {
      return makeInvalid(UNSUPPORTED_URL_ERROR);
    }
    organization = resolved.organization;
    project = resolved.project;
    baseUrl = resolved.baseUrl;
  } else if (isVisualStudio) {
    const resolved = resolveVisualStudio(parsed, pathSegments);
    if (!resolved) {
      return makeInvalid(UNSUPPORTED_URL_ERROR);
    }
    organization = resolved.organization;
    project = resolved.project;
    baseUrl = resolved.baseUrl;
  } else {
    const resolved = resolveOnPrem(parsed, pathSegments);
    if (!resolved) {
      return makeInvalid(UNSUPPORTED_URL_ERROR);
    }
    organization = resolved.organization;
    project = resolved.project;
    baseUrl = resolved.baseUrl;
    isSimplifiedOnPrem = resolved.isSimplified;
  }

  const workItemId = extractWorkItemId(segments, parsed.searchParams);
  const mode = trailingSegments[1]?.toLowerCase();
  const requiresId = isWorkItemPath && (mode === 'edit' || mode === 'view');
  if (requiresId && !workItemId) {
    return makeInvalid(UNSUPPORTED_URL_ERROR);
  }

  const apiBaseUrl = buildApiBaseUrl(baseUrl, organization, project, isSimplifiedOnPrem);

  return {
    organization,
    project,
    baseUrl,
    apiBaseUrl,
    workItemId,
    isValid: true,
  };
}

export function isAzureDevOpsWorkItemUrl(url: string): boolean {
  const parsed = parseAzureDevOpsUrl(url);
  if (!parsed.isValid) {
    return false;
  }
  return typeof parsed.workItemId === 'number' && !Number.isNaN(parsed.workItemId);
}

/**
 * Generates a PAT creation URL for the given organization.
 *
 * @param organization - The Azure DevOps organization name
 * @param baseUrl - The base URL for the organization
 * @returns The PAT creation URL
 */
export function generatePatCreationUrl(organization: string, baseUrl: string): string {
  if (baseUrl.includes('dev.azure.com')) {
    return `https://dev.azure.com/${organization}/_usersSettings/tokens`;
  } else if (baseUrl.includes('visualstudio.com')) {
    return `https://${organization}.visualstudio.com/_usersSettings/tokens`;
  } else {
    // For on-premises servers, use the base URL structure
    const trimmedBase = baseUrl.replace(/\/$/, '');
    return `${trimmedBase}/_usersSettings/tokens`;
  }
}

/**
 * Generates a work item URL for the given organization, project, and work item ID.
 *
 * @param organization - The Azure DevOps organization name
 * @param project - The project name
 * @param workItemId - The work item ID
 * @param baseUrl - The base URL for the organization
 * @returns The work item URL
 */
export function generateWorkItemUrl(
  organization: string,
  project: string,
  workItemId: number,
  baseUrl: string
): string {
  if (baseUrl.includes('dev.azure.com')) {
    return `https://dev.azure.com/${organization}/${project}/_workitems/edit/${workItemId}`;
  } else if (baseUrl.includes('visualstudio.com')) {
    return `https://${organization}.visualstudio.com/${project}/_workitems/edit/${workItemId}`;
  } else {
    // For on-premises servers, use the base URL structure
    const trimmedBase = baseUrl.replace(/\/$/, '');
    return `${trimmedBase}/${project}/_workitems/edit/${workItemId}`;
  }
}

/**
 * Tests the connection to Azure DevOps using the parsed URL information.
 *
 * @param parsedUrl - The parsed URL information
 * @param pat - The Personal Access Token
 * @returns Promise that resolves to true if connection is successful
 */
export async function testAzureDevOpsConnection(
  parsedUrl: ParsedAzureDevOpsUrl,
  pat: string
): Promise<{ success: boolean; error?: string }> {
  if (!parsedUrl.isValid) {
    return { success: false, error: 'Invalid URL configuration' };
  }

  if (!pat || !pat.trim()) {
    return { success: false, error: 'Personal Access Token is required' };
  }

  try {
    // Test the connection by making a simple API call
    const response = await fetch(`${parsedUrl.apiBaseUrl}/wit/workitemtypes?api-version=7.0`, {
      method: 'GET',
      headers: {
        Authorization: `Basic ${Buffer.from(`:${pat}`).toString('base64')}`,
        'Content-Type': 'application/json',
      },
    });

    if (response.ok) {
      return { success: true };
    } else if (response.status === 401) {
      return {
        success: false,
        error: 'Invalid Personal Access Token. Please check your token and try again.',
      };
    } else if (response.status === 403) {
      return {
        success: false,
        error: 'Access denied. Please ensure your token has the required permissions.',
      };
    } else {
      return { success: false, error: `Connection failed with status ${response.status}` };
    }
  } catch (error) {
    return {
      success: false,
      error: `Network error: ${error instanceof Error ? error.message : 'Unknown error'}`,
    };
  }
}

import axios, { AxiosInstance } from 'axios';
import { WorkItem, WorkItemBuildSummary } from './types.js';
import { RateLimiter } from './rateLimiter.js';
import { workItemCache, WorkItemCache } from './cache.js';
import { measureAsync } from './performance.js';

const escapeRegExp = (value: string) => value.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');

const stripProjectSegment = (url: string, encodedProject: string, rawProject?: string): string => {
  const candidates = new Set<string>();
  if (encodedProject) {
    candidates.add(encodedProject);
    const doubleEncoded = encodeURIComponent(encodedProject);
    if (doubleEncoded && doubleEncoded !== encodedProject) {
      candidates.add(doubleEncoded);
    }
  }

  if (rawProject?.trim()) {
    candidates.add(rawProject.trim());
  }

  try {
    const decoded = decodeURIComponent(encodedProject);
    if (decoded) {
      candidates.add(decoded);
      const fullyDecoded = decodeURIComponent(decoded);
      if (fullyDecoded) {
        candidates.add(fullyDecoded);
      }
    }
  } catch {
    // ignore decode issues
  }

  return Array.from(candidates).reduce((current, candidate) => {
    if (!candidate) {
      return current;
    }

    const pattern = new RegExp(`/${escapeRegExp(candidate)}$`);
    return current.replace(pattern, '');
  }, url);
};

type AuthType = 'pat' | 'bearer';

interface ClientOptions {
  ratePerSecond?: number;
  burst?: number;
  team?: string; // Optional team context for iteration APIs and @CurrentIteration resolution
  // If true (default), attempt to use [System.StateCategory] in WIQL filters until proven unsupported.
  wiqlPreferStateCategory?: boolean;
  baseUrl?: string; // Custom base URL for different Azure DevOps instances
  apiBaseUrl?: string; // Manual API base URL override (takes precedence over derived URL)
  authType?: AuthType; // 'pat' (default) or 'bearer' for Entra ID tokens
  identityName?: string; // Optional identity name for on-prem servers where @Me doesn't resolve
  onAuthFailure?: (error: Error) => void;
}

export class AzureDevOpsIntClient {
  public organization: string;
  public project: string;
  public encodedOrganization: string;
  public encodedProject: string;
  public axios: AxiosInstance;
  private _repoCache: any[] | undefined;
  private credential: string; // PAT or access token
  private authType: AuthType;
  private limiter: RateLimiter;
  private onAuthFailure?: (error: Error) => void;
  public team: string | undefined;
  public encodedTeam: string | undefined;
  // Capability cache: prefer using [System.StateCategory] unless Azure DevOps rejects it for this org/project
  private preferStateCategory: boolean;
  private cachedIdentity?: { id?: string; displayName?: string; uniqueName?: string };
  private identityName?: string; // Fallback identity name for on-prem servers
  public baseUrl: string; // Store the base URL for browser URL generation
  private apiBaseUrl: string; // Store the API base URL (for on-premises support)
  private static readonly connectionDataApiVersions = [
    '7.1-preview.1',
    '7.0-preview.1',
    '6.0-preview.1',
    '5.1-preview.2',
    '5.0-preview.3',
    '5.0-preview',
    '5.0',
  ];

  private _authorizedGet(url: string) {
    // Use existing axios instance so interceptors attach auth headers; override baseURL to avoid double prefixing
    return this.axios.get(url, { baseURL: undefined });
  }

  constructor(
    organization: string,
    project: string,
    credential: string,
    options: ClientOptions = {}
  ) {
    this.organization = organization;
    this.project = project;
    this.credential = credential;
    this.authType = options.authType ?? 'pat';
    this.onAuthFailure = options.onAuthFailure;
    this.team = options.team?.trim() ? options.team.trim() : undefined;
    this.preferStateCategory = options.wiqlPreferStateCategory ?? true;
    this.identityName = options.identityName?.trim() ? options.identityName.trim() : undefined;
    if (this.identityName) {
      console.log(
        '[AzureDevOpsInt] [AzureClient] Configured with fallback identityName for on-prem:',
        this.identityName
      );
    }
    this.encodedOrganization = encodeURIComponent(organization);
    this.encodedProject = encodeURIComponent(project);
    this.encodedTeam = this.team ? encodeURIComponent(this.team) : undefined;

    // Determine the base URL and API base URL
    // Priority: manual apiBaseUrl override > derived from baseUrl > default cloud
    const trimTrailingSlash = (value?: string) => (value ? value.replace(/\/+$/, '') : undefined);

    const normalizeBaseUrl = (value?: string): string => {
      const fallback = `https://dev.azure.com/${this.encodedOrganization}`;
      if (!value) {
        return fallback;
      }

      const trimmed = trimTrailingSlash(value) ?? fallback;
      const withoutApis = trimmed.replace(/\/_apis$/i, '');

      if (/visualstudio\.com/i.test(withoutApis)) {
        return fallback;
      }

      if (/dev\.azure\.com/i.test(withoutApis)) {
        return `https://dev.azure.com/${this.encodedOrganization}`;
      }

      const normalized = stripProjectSegment(withoutApis, this.encodedProject, this.project);
      return trimTrailingSlash(normalized) ?? fallback;
    };

    const ensureApiSuffix = (value: string): string => {
      const trimmed = trimTrailingSlash(value) ?? value;
      return /\/_apis$/i.test(trimmed) ? trimmed : `${trimmed}/_apis`;
    };

    if (options.apiBaseUrl) {
      const manualApi = ensureApiSuffix(options.apiBaseUrl);
      const derivedBase = stripProjectSegment(
        manualApi.replace(/\/_apis$/i, ''),
        this.encodedProject,
        this.project
      );
      const preferredBase = options.baseUrl
        ? normalizeBaseUrl(options.baseUrl)
        : normalizeBaseUrl(derivedBase);

      this.baseUrl = preferredBase;
      this.apiBaseUrl = manualApi.replace(/\/+$/, '');

      console.log('[AzureDevOpsInt] [AzureClient] Using manual API URL override:', {
        apiBaseUrl: this.apiBaseUrl,
        baseUrl: this.baseUrl,
      });
    } else if (options.baseUrl) {
      this.baseUrl = normalizeBaseUrl(options.baseUrl);
      this.apiBaseUrl = `${this.baseUrl}/${this.encodedProject}/_apis`;
    } else {
      // Default to dev.azure.com
      this.baseUrl = `https://dev.azure.com/${this.encodedOrganization}`;
      this.apiBaseUrl = `${this.baseUrl}/${this.encodedProject}/_apis`;
    }

    this.baseUrl = this.baseUrl.replace(/\/+$/, '');
    this.apiBaseUrl = this.apiBaseUrl.replace(/\/+$/, '');

    this.axios = axios.create({
      baseURL: this.apiBaseUrl,
      timeout: 30000, // 30s network timeout for slow Azure DevOps APIs
      headers: {
        'Content-Type': 'application/json',
      },
    });
    // Rate limiter: allow configurable burst & sustained rate (defaults)
    const rps = Math.max(1, Math.min(50, options.ratePerSecond ?? 5));
    const burst = Math.max(1, Math.min(100, options.burst ?? 10));
    this.limiter = new RateLimiter(rps, burst);

    // Attach auth header on each request
    this.axios.interceptors.request.use(async (cfg) => {
      await this.limiter.acquire();

      // Set authorization header based on auth type
      if (this.authType === 'pat') {
        (cfg.headers ||= {} as any)['Authorization'] =
          `Basic ${Buffer.from(':' + this.credential).toString('base64')}`;
      } else {
        // Bearer token for Entra ID
        (cfg.headers ||= {} as any)['Authorization'] = `Bearer ${this.credential}`;
      }

      (cfg as any).__start = Date.now();
      // Attach attempt counter for retries
      (cfg as any).__attempt = ((cfg as any).__attempt || 0) + 1;

      // Enhanced debugging: log token info (first/last chars only for security)
      const authHeader = cfg.headers?.['Authorization'] as string;
      const tokenDebug = authHeader
        ? `${authHeader.substring(0, 20)}...${authHeader.substring(authHeader.length - 10)}`
        : 'NO_AUTH_HEADER';

      console.log(
        '[azureDevOpsInt][HTTP] ',
        cfg.method?.toUpperCase(),
        cfg.url,
        'attempt',
        (cfg as any).__attempt,
        'token:',
        tokenDebug
      );
      return cfg;
    });

    this.axios.interceptors.response.use(
      (resp) => {
        const ms = Date.now() - (resp.config as any).__start;
        console.log(
          '[azureDevOpsInt][HTTP] ',
          resp.config.method?.toUpperCase(),
          resp.config.url,
          resp.status,
          `${ms}ms`
        );
        return resp;
      },
      async (err) => {
        const cfg = err.config || {};
        const ms = cfg.__start ? Date.now() - cfg.__start : 'n/a';
        if (err.response) {
          const { status, statusText, data } = err.response;

          // Enhanced error logging for 404s
          if (status === 404) {
            console.error('[azureDevOpsInt][HTTP][404_DETAILS]', {
              method: cfg.method?.toUpperCase(),
              url: cfg.url,
              fullUrl: cfg.baseURL + cfg.url,
              status,
              statusText,
              responseData: data,
              authType: this.authType,
              organization: this.organization,
              project: this.project,
            });
          }

          console.error(
            '[azureDevOpsInt][HTTP][ERR]',
            cfg.method?.toUpperCase(),
            cfg.url,
            status,
            statusText,
            `${ms}ms`
          );
          if (data) {
            let snippet: string;
            try {
              snippet =
                typeof data === 'string' ? data.slice(0, 500) : JSON.stringify(data).slice(0, 500);
            } catch {
              snippet = '[unserializable response data]';
            }
            console.error('[azureDevOpsInt][HTTP][ERR] body:', snippet);
          }

          // Handle 401 for bearer token auth - throw error immediately to stop retry loops
          // Note: 404 is handled individually by methods since it can have different meanings
          if (status === 401 && this.authType === 'bearer') {
            console.error(`[azureDevOpsInt][HTTP] 401 Unauthorized - authentication required`);
            const authError = new Error(
              `Authentication failed: 401 Unauthorized. Please re-authenticate.`
            );
            authError.name = 'AuthenticationError';
            (authError as any).status = status;
            try {
              this.onAuthFailure?.(authError);
            } catch (callbackError) {
              console.error('[azureDevOpsInt][HTTP] onAuthFailure callback threw', callbackError);
            }
            throw authError;
          }
        } else if (err.code === 'ECONNABORTED') {
          console.error(
            '[azureDevOpsInt][HTTP][TIMEOUT]',
            cfg.method?.toUpperCase(),
            cfg.url,
            `${ms}ms`
          );
        } else {
          console.error('[azureDevOpsInt][HTTP][NETERR]', err.message);
        }
        // Retry logic: 429 or >=500 (excluding 501/505 uncommon) with exponential backoff
        const status = err.response?.status;
        const attempt = (cfg as any).__attempt || 1;
        const maxAttempts = 4;
        if (
          status &&
          (status === 429 || (status >= 500 && status < 600)) &&
          attempt < maxAttempts
        ) {
          const backoffBase = 250 * Math.pow(2, attempt - 1);
          const jitter = Math.random() * backoffBase * 0.3;
          const delay = Math.min(4000, backoffBase + jitter);
          console.warn(
            '[azureDevOpsInt][HTTP][RETRY]',
            cfg.url,
            'status',
            status,
            'retryInMs',
            Math.round(delay),
            'attempt',
            attempt + 1
          );
          return new Promise((resolve) => setTimeout(resolve, delay)).then(() => this.axios(cfg));
        }
        return Promise.reject(err);
      }
    );
  }

  /**
   * Update the credential (PAT or access token)
   * Useful for refreshing Entra ID tokens
   */
  updateCredential(newCredential: string): void {
    this.credential = newCredential;
  }

  buildFullUrl(path: string) {
    return `${this.apiBaseUrl}${path}`;
  }
  getBrowserUrl(path: string) {
    return `${this.baseUrl}/${this.encodedProject}${path}`;
  }
  private buildTeamApiUrl(path: string) {
    if (!this.encodedTeam) return this.buildFullUrl(path);
    // Insert team segment before _apis
    const baseWithoutApis = this.apiBaseUrl.replace(/\/_apis$/, '');
    return `${baseWithoutApis}/${this.encodedTeam}/_apis${path}`;
  }

  // ---------------- Identity Helpers ----------------
  /**
   * Fetches the authenticated Azure DevOps user identity GUID using connectionData.
   * Falls back to null on error.
   */
  async getAuthenticatedUserId(): Promise<string | null> {
    console.log('[AzureDevOpsInt] [AzureClient]  Testing authentication...');
    const identity = await this._getAuthenticatedIdentity();

    if (identity?.id) {
      console.log('[AzureDevOpsInt] [AzureClient]  Authentication successful!');
      console.log(
        `[AzureDevOpsInt] [AzureClient]  User: ${identity.displayName || 'Unknown'} (${identity.uniqueName || 'Unknown'})`
      );
      console.log(
        `[AzureDevOpsInt] [AzureClient]  Authentication method: Personal Access Token (PAT)`
      );
      console.log(`[AzureDevOpsInt] [AzureClient]  Your PAT has at least basic read permissions`);
      console.log(
        `[AzureDevOpsInt] [AzureClient]  If work item creation works, your PAT also has work item write permissions`
      );
    } else {
      console.log(
        '[AzureDevOpsInt] [AzureClient]  Authentication failed - no user identity returned'
      );
    }

    // Debug: log the entire identity object for diagnostics (no secrets)
    try {
      console.log(
        '[AzureDevOpsInt] [AzureClient][DEBUG] resolved identity object:',
        JSON.stringify(identity)
      );
    } catch {
      // ignore stringify errors
    }

    return identity?.id ?? null;
  }

  private async _getAuthenticatedIdentity(): Promise<{
    id?: string;
    displayName?: string;
    uniqueName?: string;
  } | null> {
    if (this.cachedIdentity) return this.cachedIdentity;
    try {
      // Request connectionData. The endpoint location varies:
      // - Cloud (dev.azure.com): https://dev.azure.com/{org}/_apis/connectionData
      // - On-prem/TFS: https://server/collection/_apis/connectionData OR https://server/_apis/connectionData
      // Use api-version=5.0 (widely supported stable version)
      let resp: any;
      const attempts: Array<{ desc: string; url: string }> = [];

      const attemptConnectionData = async (
        basePath: string,
        desc: string,
        useAxiosBase: boolean
      ) => {
        let lastError: unknown;
        const sanitized = basePath.replace(/([&?]+)api-version=[^&]+/i, '').replace(/[&?]+$/, '');
        for (const version of AzureDevOpsIntClient.connectionDataApiVersions) {
          const separator = sanitized.includes('?') ? '&' : '?';
          const fullUrl = `${sanitized}${separator}api-version=${version}`;
          attempts.push({ desc: `${desc} (v=${version})`, url: fullUrl });
          try {
            return useAxiosBase
              ? await this.axios.get(fullUrl)
              : await this._authorizedGet(fullUrl);
          } catch (error) {
            lastError = error;
            if (!this._shouldRetryConnectionDataVersion(error)) {
              throw error;
            }
          }
        }
        throw lastError || new Error('connectionData api-version attempts exhausted');
      };

      try {
        const orgLevel = this.baseUrl.replace(/\/$/, '') + '/_apis/connectionData';
        resp = await attemptConnectionData(orgLevel, 'baseUrl org-level', false);
      } catch (err1) {
        try {
          const apiRoot = stripProjectSegment(
            this.apiBaseUrl.replace(/\/_apis\/?$/, ''),
            this.encodedProject,
            this.project
          );
          const apiConn = apiRoot.replace(/\/$/, '') + '/_apis/connectionData';
          resp = await attemptConnectionData(apiConn, 'apiBaseUrl without project', false);
        } catch (err2) {
          try {
            resp = await attemptConnectionData(
              '/connectionData',
              'relative to axios baseURL',
              true
            );
          } catch (err3) {
            console.error('[azureDevOpsInt] connectionData attempts:', attempts);
            throw err1 || err2 || err3;
          }
        }
      }
      const user = resp.data?.authenticatedUser ?? {};
      // Tolerant identity extraction: accept string or numeric id, and look
      // for several possible unique-name fields used across Azure DevOps and TFS.
      const resolvedId =
        typeof user.id === 'string' || typeof user.id === 'number'
          ? String(user.id)
          : typeof user.descriptor === 'string'
            ? user.descriptor
            : typeof user.subjectDescriptor === 'string'
              ? user.subjectDescriptor
              : undefined;
      const displayName =
        typeof user.providerDisplayName === 'string'
          ? user.providerDisplayName
          : typeof user.displayName === 'string'
            ? user.displayName
            : undefined;
      const uniqueName =
        typeof user.uniqueName === 'string'
          ? user.uniqueName
          : typeof user.unique_name === 'string'
            ? user.unique_name
            : typeof user.mailAddress === 'string'
              ? user.mailAddress
              : typeof user.email === 'string'
                ? user.email
                : typeof user.principalName === 'string'
                  ? user.principalName
                  : typeof user.subjectDescriptor === 'string'
                    ? user.subjectDescriptor
                    : typeof user.descriptor === 'string'
                      ? user.descriptor
                      : undefined;

      const identity = {
        id: resolvedId,
        displayName,
        uniqueName,
      };
      this.cachedIdentity = identity;
      try {
        console.log(
          '[AzureDevOpsInt] [AzureClient][DEBUG] resolved identity object:',
          JSON.stringify(identity)
        );
      } catch {
        /* ignore stringify errors */
      }

      // If we still don't have a uniqueName and identityName was provided (for on-prem),
      // use identityName as the fallback uniqueName
      if (!this.cachedIdentity.uniqueName && this.identityName) {
        console.log(
          '[AzureDevOpsInt] [AzureClient][DEBUG] using provided identityName as fallback:',
          this.identityName
        );
        this.cachedIdentity.uniqueName = this.identityName;
      }

      return this.cachedIdentity;
    } catch (e) {
      console.error('[AzureDevOpsInt] Error fetching authenticated user identity', e);
      // If connectionData fails entirely and we have identityName, use it as a fallback
      if (this.identityName) {
        console.log(
          '[AzureDevOpsInt] [AzureClient][DEBUG] connectionData failed, using identityName fallback:',
          this.identityName
        );
        this.cachedIdentity = {
          uniqueName: this.identityName,
        };
        return this.cachedIdentity;
      }
      return null;
    }
  }
  private _shouldRetryConnectionDataVersion(error: any): boolean {
    const status = error?.response?.status;
    if (status !== 400) {
      return false;
    }
    const message = String(
      error?.response?.data?.message ||
        error?.response?.data?.error?.message ||
        error?.response?.data ||
        error?.message ||
        ''
    );
    return /preview/i.test(message) || /api[- ]?version/i.test(message);
  }

  buildWIQL(queryNameOrText: string) {
    // Use capability cache to decide whether to include [System.StateCategory]
    return this._buildWIQL(queryNameOrText, this.preferStateCategory);
  }

  private _selectFields() {
    return `[System.Id], [System.Title], [System.State], [System.WorkItemType], 
                           [System.AssignedTo], [System.CreatedDate], [System.ChangedDate],
                           [System.IterationPath], [System.Tags], [Microsoft.VSTS.Common.Priority]`;
  }

  private _buildWIQL(queryNameOrText: string, useStateCategory: boolean) {
    const fields = this._selectFields();
    const selectedSprint: string | null = null; // placeholder; inject externally if needed
    const sprintClause = selectedSprint ? `AND [System.IterationPath] = '${selectedSprint}'` : '';
    const activeFilter = this._activeStateFilter(useStateCategory);
    switch (queryNameOrText) {
      case 'My Activity':
        return `SELECT ${fields} FROM WorkItems 
                        WHERE [System.TeamProject] = @Project
                        AND ([System.AssignedTo] = @Me OR [System.CreatedBy] = @Me OR [System.ChangedBy] = @Me)
                        ${activeFilter}
                        ${sprintClause}
                        ORDER BY [System.ChangedDate] DESC`;
      case 'Assigned to me':
      case 'My Work Items':
        return `SELECT ${fields} FROM WorkItems 
                        WHERE [System.TeamProject] = @Project
                        AND [System.AssignedTo] = @Me 
                        ${activeFilter}
                        ${sprintClause}
                        ORDER BY [System.ChangedDate] DESC`;
      case 'Current Sprint':
        return selectedSprint
          ? `SELECT ${fields} FROM WorkItems 
                            WHERE [System.IterationPath] = '${selectedSprint}'
                            ${activeFilter}
                            ORDER BY [Microsoft.VSTS.Common.Priority] ASC, [System.Id] ASC`
          : `SELECT ${fields} FROM WorkItems 
                        WHERE [System.IterationPath] UNDER @CurrentIteration 
                        ${activeFilter}
                        ORDER BY [Microsoft.VSTS.Common.Priority] ASC, [System.Id] ASC`;
      case 'All Active':
        return `SELECT ${fields} FROM WorkItems 
                        WHERE [System.TeamProject] = @Project
                        ${activeFilter}
                        ${sprintClause}
                        ORDER BY [System.ChangedDate] DESC`;
      case 'Recently Updated':
        return `SELECT ${fields} FROM WorkItems 
                        WHERE [System.ChangedDate] >= @Today - 7
                        AND [System.State] <> 'Removed'
                        ${sprintClause}
                        ORDER BY [System.ChangedDate] DESC`;
      default:
        return queryNameOrText;
    }
  }

  private _activeStateFilter(useStateCategory: boolean) {
    if (useStateCategory) {
      return `AND [System.StateCategory] <> 'Completed' AND [System.State] <> 'Removed'`;
    }
    // Legacy fallback: exclude common terminal states across Basic/Agile/Scrum
    return `AND [System.State] NOT IN ('Closed','Done','Resolved','Removed')`;
  }

  private _mapRawWorkItems(rawItems: any[]): WorkItem[] {
    if (!Array.isArray(rawItems)) return [];
    return rawItems
      .filter((item: any) => item && typeof item.id === 'number')
      .map((item: any) => {
        const mapped: WorkItem = {
          id: item.id,
          fields: item.fields || {},
        };

        if (Array.isArray(item.relations) && item.relations.length > 0) {
          const relations = item.relations
            .filter((rel: any) => rel && typeof rel === 'object')
            .map((rel: any) => {
              const attributes =
                rel.attributes && typeof rel.attributes === 'object'
                  ? { ...rel.attributes }
                  : undefined;
              return {
                url: typeof rel.url === 'string' ? rel.url : undefined,
                rel: typeof rel.rel === 'string' ? rel.rel : undefined,
                attributes,
              };
            })
            .filter(Boolean);

          if (relations.length > 0) {
            mapped.relations = relations;
          }
        }

        return mapped;
      });
  }

  private async _fetchWorkItemsByIds(ids: number[]): Promise<WorkItem[]> {
    if (!Array.isArray(ids) || ids.length === 0) return [];
    const uniqueIds = Array.from(new Set(ids.filter((id) => Number.isFinite(id)))) as number[];
    if (uniqueIds.length === 0) return [];
    const results: WorkItem[] = [];
    const chunkSize = 200;
    for (let i = 0; i < uniqueIds.length; i += chunkSize) {
      const chunk = uniqueIds.slice(i, i + chunkSize);
      if (chunk.length === 0) continue;
      try {
        const resp = await this.axios.get(
          `/wit/workitems?ids=${chunk.join(',')}&$expand=all&api-version=7.0`
        );
        const rawItems: any[] = resp.data?.value || [];
        results.push(...this._mapRawWorkItems(rawItems));
      } catch (err: any) {
        console.warn(
          '[azureDevOpsInt][GETWI] Failed to expand work item chunk',
          err?.message || err
        );
      }
    }
    return results;
  }

  private _filterItemsForProject(items: WorkItem[]): WorkItem[] {
    if (!Array.isArray(items) || !this.project) return Array.isArray(items) ? [...items] : [];
    const target = this.project.trim().toLowerCase();
    return items.filter((item) => {
      const projectName = item?.fields?.['System.TeamProject'];
      if (!projectName || typeof projectName !== 'string') return true;
      return projectName.trim().toLowerCase() === target;
    });
  }

  private _sortByChangedDateDesc(items: WorkItem[]): WorkItem[] {
    return [...items].sort((a, b) => {
      const aDate = new Date(a?.fields?.['System.ChangedDate'] || 0).getTime();
      const bDate = new Date(b?.fields?.['System.ChangedDate'] || 0).getTime();
      return bDate - aDate;
    });
  }

  private async _getFollowedWorkItems(): Promise<WorkItem[]> {
    try {
      const resp = await this.axios.get('/work/workitems/favorites?api-version=7.0');
      const entries = Array.isArray(resp.data?.value) ? resp.data.value : [];
      const ids = entries
        .map((entry: any) => {
          if (typeof entry?.workItemId === 'number') return entry.workItemId;
          if (typeof entry?.id === 'number') return entry.id;
          const nested = entry?.workItem?.id;
          return typeof nested === 'number' ? nested : NaN;
        })
        .filter((id: number) => Number.isFinite(id)) as number[];
      if (ids.length === 0) return [];
      const items = await this._fetchWorkItemsByIds(ids);
      const filtered = this._filterItemsForProject(items);
      return this._sortByChangedDateDesc(filtered);
    } catch (err: any) {
      console.warn(
        '[azureDevOpsInt][GETWI] Failed to fetch followed work items',
        err?.message || err
      );
      return [];
    }
  }

  private async _getMentionedWorkItems(): Promise<WorkItem[]> {
    const identity = await this._getAuthenticatedIdentity();
    if (!identity) return [];
    const terms = new Set<string>();
    if (typeof identity.displayName === 'string' && identity.displayName.trim()) {
      terms.add(identity.displayName.trim());
    }
    if (typeof identity.uniqueName === 'string' && identity.uniqueName.trim()) {
      terms.add(identity.uniqueName.trim());
    }
    if (terms.size === 0) return [];
    const clauses = Array.from(terms).map(
      (term) => `[System.History] CONTAINS '${this._escapeWIQL(term)}'`
    );
    const fields = this._selectFields();
    const stateFilter = this._activeStateFilter(this.preferStateCategory);
    const query = `SELECT ${fields} FROM WorkItems 
                        WHERE [System.TeamProject] = @Project
                        AND (${clauses.join(' OR ')})
                        ${stateFilter}
                        ORDER BY [System.ChangedDate] DESC`;
    try {
      return await this.runWIQL(query);
    } catch (err: any) {
      console.warn(
        '[azureDevOpsInt][GETWI] Failed to fetch mentioned work items',
        err?.message || err
      );
      return [];
    }
  }

  async getWorkItems(query: string): Promise<WorkItem[]> {
    return await measureAsync('getWorkItems', async () => {
      let wiql = ''; // Declare outside try block for error reporting
      let wiqlToSend = '';
      try {
        // Check cache first
        const cacheKey = WorkItemCache.generateWorkItemsKey(
          `${this.organization}-${this.project}`,
          query,
          { team: this.team }
        );

        const cached = workItemCache.get(cacheKey);
        if (cached) {
          console.log('[azureDevOpsInt][GETWI] Cache hit for query:', query);
          return cached;
        }

        if (query === 'Following') {
          const result = await this._getFollowedWorkItems();
          workItemCache.setWorkItems(cacheKey, result);
          return result;
        }
        if (query === 'Mentioned') {
          const result = await this._getMentionedWorkItems();
          workItemCache.setWorkItems(cacheKey, result);
          return result;
        }
        wiql = this.buildWIQL(query);
        // If querying Current Sprint, prefer resolving the explicit current iteration path for configured team
        if (query === 'Current Sprint') {
          try {
            const cur = await this.getCurrentIteration();
            const iterPath = cur?.path;
            if (iterPath && typeof iterPath === 'string') {
              const safePath = this._escapeWIQL(iterPath);
              wiql = `SELECT [System.Id], [System.Title], [System.State], [System.WorkItemType], [System.AssignedTo], [System.CreatedDate], [System.ChangedDate], [System.IterationPath], [System.Tags], [Microsoft.VSTS.Common.Priority] FROM WorkItems WHERE [System.IterationPath] UNDER '${safePath}' AND [System.State] <> 'Closed' AND [System.State] <> 'Removed' ORDER BY [Microsoft.VSTS.Common.Priority] ASC, [System.Id] ASC`;
            }
          } catch (e) {
            // Fallback to @CurrentIteration-based WIQL already produced
            console.warn(
              '[azureDevOpsInt][GETWI] getCurrentIteration failed, using @CurrentIteration',
              e
            );
          }
        }
        console.log('[azureDevOpsInt][GETWI] Fetching work items with query:', wiql);
        console.log('[azureDevOpsInt][GETWI] API Base URL:', this.axios.defaults.baseURL);

        // Prepare the WIQL to send. Some on-prem/TFS servers don't reliably resolve the @Me token
        // so, when possible, replace @Me with an explicit identity string (uniqueName, displayName, or id).
        wiqlToSend = wiql;

        // For queries that might return many results, add a hard limit via client-side limiting
        // User-scoped queries (My Activity, Assigned to me) don't need limits as they're naturally bounded
        const knownUserScopedQueries = ['My Activity', 'Assigned to me', 'My Work Items'];
        const isKnownQuery = [
          'Recently Updated',
          'All Active',
          'Current Sprint',
          ...knownUserScopedQueries,
        ].includes(query);
        const needsLimit =
          !knownUserScopedQueries.includes(query) &&
          (['Recently Updated', 'All Active', 'Current Sprint'].includes(query) || !isKnownQuery);

        if (needsLimit) {
          console.log('[azureDevOpsInt][GETWI] Applying hard limit of 100 items for query:', query);
        }

        try {
          if (/@Me\b/i.test(wiql)) {
            const resolved = await this._getAuthenticatedIdentity();
            if (resolved) {
              const idVal = resolved.uniqueName || resolved.displayName || resolved.id;
              if (idVal) {
                const escaped = this._escapeWIQL(String(idVal));
                wiqlToSend = wiql.replace(/@Me\b/g, `'${escaped}'`);
                console.log(
                  '[azureDevOpsInt][GETWI] Replacing @Me with explicit identity for compatibility: ',
                  idVal
                );
              } else {
                console.log(
                  '[azureDevOpsInt][GETWI] _getAuthenticatedIdentity returned no usable identifier'
                );
              }
            } else {
              console.log(
                '[azureDevOpsInt][GETWI] Could not resolve authenticated identity to replace @Me'
              );
            }
          }
        } catch (identErr) {
          console.warn(
            '[azureDevOpsInt][GETWI] Failed resolving identity for @Me replacement',
            identErr
          );
        }

        // First, try the WIQL query
        const wiqlEndpoint = '/wit/wiql?api-version=7.0';

        let wiqlResp;
        try {
          wiqlResp = await this.axios.post(wiqlEndpoint, { query: wiqlToSend });
        } catch (err: any) {
          if (this._isMissingStateCategoryError(err)) {
            console.warn(
              '[azureDevOpsInt][GETWI] StateCategory unsupported in WIQL. Retrying with legacy state filters.'
            );
            // Update capability cache so future queries avoid the failing path for this client lifetime
            this.preferStateCategory = false;
            // Rebuild the WIQL with legacy state filters and retry
            const wiqlLegacy = this._buildWIQL(query, false);
            console.log('[azureDevOpsInt][GETWI] Fallback WIQL:', wiqlLegacy);
            // Apply the same @Me replacement logic to the legacy WIQL as well
            let legacyToSend = wiqlLegacy;
            try {
              if (/@Me\b/i.test(wiqlLegacy)) {
                const resolved2 = await this._getAuthenticatedIdentity();
                if (resolved2) {
                  const idVal2 = resolved2.uniqueName || resolved2.displayName || resolved2.id;
                  if (idVal2) {
                    legacyToSend = wiqlLegacy.replace(
                      /@Me\b/g,
                      `'${this._escapeWIQL(String(idVal2))}'`
                    );
                    console.log(
                      '[azureDevOpsInt][GETWI] Replacing @Me in fallback WIQL with explicit identity:',
                      idVal2
                    );
                  }
                }
              }
            } catch (e) {
              console.warn(
                '[azureDevOpsInt][GETWI] Identity resolution failed for fallback WIQL',
                e
              );
            }

            wiqlResp = await this.axios.post(wiqlEndpoint, { query: legacyToSend });
            // Also update wiql for subsequent logs/context
            wiql = wiqlLegacy;
          } else {
            throw err;
          }
        }
        console.log('[azureDevOpsInt][GETWI] WIQL response status:', wiqlResp.status);
        // Diagnostic: dump a truncated version of the response body to help debug empty results
        try {
          const bodySnippet = JSON.stringify(wiqlResp.data).slice(0, 2000);
          console.log(
            '[azureDevOpsInt][GETWI][DEBUG] WIQL response body (truncated):',
            bodySnippet
          );
        } catch {
          /* ignore */
        }

        let refs = wiqlResp.data?.workItems || [];
        console.log(`[azureDevOpsInt][GETWI] WIQL reference count: ${refs.length}`);

        // Apply client-side limiting for queries that might return many results
        if (needsLimit && refs.length > 100) {
          refs = refs.slice(0, 100);
          console.log(
            `[azureDevOpsInt][GETWI] Applied client-side limit, reduced to ${refs.length} items`
          );
        }

        if (refs.length === 0) {
          console.log(
            '[azureDevOpsInt][GETWI] No work items matched query. Trying simpler query...'
          );

          // Try a simpler query to test authentication
          const simpleWiql =
            'SELECT [System.Id], [System.Title] FROM WorkItems ORDER BY [System.Id] DESC';
          const simpleResp = await this.axios.post('/wit/wiql?api-version=7.0', {
            query: simpleWiql,
          });
          // Diagnostic: log simple query response snippet
          try {
            const simpleSnippet = JSON.stringify(simpleResp.data).slice(0, 1500);
            console.log(
              '[azureDevOpsInt][GETWI][DEBUG] simpleWIQL response body (truncated):',
              simpleSnippet
            );
          } catch {
            /* ignore */
          }
          const simpleRefs = simpleResp.data?.workItems || [];
          console.log(
            `[azureDevOpsInt][GETWI] Simple query returned ${simpleRefs.length} work items`
          );

          if (simpleRefs.length === 0) {
            console.log('[azureDevOpsInt][GETWI] No work items in project at all.');
            return [];
          }

          // Work items exist - fetch and return them instead of returning empty
          console.log(
            '[azureDevOpsInt][GETWI] Original query matched nothing, fetching simple query results...'
          );

          // Use simpleRefs instead of refs to fetch the work items
          const simpleIds = simpleRefs
            .map((w: any) => w.id)
            .filter((id: any) => id != null)
            .join(',');
          if (!simpleIds) {
            console.log('[azureDevOpsInt][GETWI] No valid IDs in simple query results.');
            return [];
          }

          console.log('[azureDevOpsInt][GETWI] Expanding simple query IDs:', simpleIds);
          const fallbackItemsResp = await this.axios.get(
            `/wit/workitems?ids=${simpleIds}&$expand=all&api-version=7.0`
          );
          const fallbackRawItems: any[] = fallbackItemsResp.data?.value || [];
          const fallbackItems: WorkItem[] = this._mapRawWorkItems(fallbackRawItems);

          console.log(
            `[azureDevOpsInt][GETWI] Returning ${fallbackItems.length} work items from simple query fallback.`
          );

          // Cache the fallback result
          workItemCache.setWorkItems(cacheKey, fallbackItems);
          return fallbackItems;
        }

        const ids = refs.map((w: any) => w.id).join(',');
        console.log('[azureDevOpsInt][GETWI] Expanding IDs:', ids);

        const itemsResp = await this.axios.get(
          `/wit/workitems?ids=${ids}&$expand=all&api-version=7.0`
        );
        const rawItems: any[] = itemsResp.data?.value || [];
        console.log('[azureDevOpsInt][GETWI] Raw expanded item count:', rawItems.length);

        const items: WorkItem[] = this._mapRawWorkItems(rawItems);

        console.log(`[azureDevOpsInt][GETWI] Returning ${items.length} mapped work items.`);

        // Cache the result
        workItemCache.setWorkItems(cacheKey, items);
        return items;
      } catch (err: any) {
        console.error('[azureDevOpsInt][GETWI][ERROR]', err?.message || err);

        // Build detailed error message for users
        let errorMessage = 'Failed to fetch work items';
        const status = err?.response?.status;

        if (status === 401 || status === 403) {
          errorMessage =
            `Authentication failed (${status}). Please check:\n` +
            ' Your Personal Access Token (PAT) is valid and not expired\n' +
            ' The PAT has "Work Items (Read)" permission\n' +
            ' You have access to this project';
        } else if (status === 404) {
          // Log 404 details for debugging
          console.log('[azureDevOpsInt][GETWI] 404 Not Found', {
            organization: this.organization,
            project: this.project,
            authType: this.authType,
            url: err?.request?.url || err?.config?.url,
          });

          // For both PAT and Entra ID, treat 404 as project/access issue, not auth failure
          // Auth failures should return 401, not 404
          errorMessage =
            `Project not found (404). Please verify:\n` +
            ` Organization: "${this.organization}"\n` +
            ` Project: "${this.project}"\n` +
            ' The project name matches exactly (case-sensitive)';
        } else if (status === 400) {
          // 400 Bad Request - likely WIQL syntax or field name issue
          const apiError = err?.response?.data?.message || err?.response?.data?.value?.Message;
          if (apiError) {
            errorMessage = `Invalid query (400): ${apiError}`;
          } else {
            errorMessage = `Invalid query (400). The WIQL query may contain unsupported fields or syntax for this project.\nQuery name: "${query}"`;
          }
          // Log the actual WIQL that failed for debugging
          console.error('[azureDevOpsInt][GETWI][ERROR] WIQL that failed:', wiqlToSend || wiql);
        } else if (status >= 500) {
          errorMessage = `Azure DevOps server error (${status}). The service may be temporarily unavailable.`;
        } else if (err?.code === 'ENOTFOUND' || err?.code === 'ECONNREFUSED') {
          errorMessage =
            `Network error: Cannot reach Azure DevOps.\n` +
            ' Check your internet connection\n' +
            ' Verify your base URL is correct\n' +
            ` Current URL: ${this.axios.defaults.baseURL}`;
        } else if (err?.message) {
          errorMessage = `${errorMessage}: ${err.message}`;
        }

        if (err?.response) {
          console.error('[azureDevOpsInt][GETWI][ERROR] HTTP status:', status);
          try {
            console.error(
              '[azureDevOpsInt][GETWI][ERROR] Response data:',
              JSON.stringify(err.response.data).slice(0, 600)
            );
          } catch {
            /* ignore */
          }
        }

        // Re-throw with detailed message instead of silently returning []
        throw new Error(errorMessage);
      }
    });
  }

  private _isMissingStateCategoryError(err: any) {
    const status = err?.response?.status;
    const bodyMessage = String(
      err?.response?.data?.message || err?.response?.data || err?.message || ''
    );
    // Some server versions or languages may return different wording. Test for 'statecategory' anywhere in the message.
    if (status === 400 && /statecategory/i.test(bodyMessage)) return true;
    // Also handle server responses where the error mentions an unknown field name without the exact 'System.StateCategory' token
    if (
      status === 400 &&
      /\bSystem\.State\b/i.test(bodyMessage) &&
      /not found|unknown|does not exist|is not valid/i.test(bodyMessage)
    )
      return true;
    return false;
  }

  async getWorkItemById(id: number): Promise<WorkItem | null> {
    try {
      const resp = await this.axios.get(`/wit/workitems/${id}?$expand=all&api-version=7.0`);
      return { id: resp.data.id, fields: resp.data.fields } as WorkItem;
    } catch (err) {
      console.error('[AzureDevOpsInt] Error fetching work item by id:', err);
      return null;
    }
  }

  async runWIQL(wiql: string): Promise<WorkItem[]> {
    try {
      const wiqlResp = await this.axios.post('/wit/wiql?api-version=7.0', { query: wiql });
      if (!wiqlResp.data.workItems || wiqlResp.data.workItems.length === 0) return [];
      const ids = wiqlResp.data.workItems.map((w: any) => w.id).join(',');
      const itemsResp = await this.axios.get(
        `/wit/workitems?ids=${ids}&$expand=all&api-version=7.0`
      );
      const raw = itemsResp.data.value || [];
      return raw.map((r: any) => ({ id: r.id, fields: r.fields }) as WorkItem);
    } catch (err) {
      console.error('[AzureDevOpsInt] runWIQL failed:', err);
      return [];
    }
  }

  async searchWorkItems(term: string): Promise<WorkItem[]> {
    if (!term?.trim()) return [];
    const safe = this._escapeWIQL(term.trim());
    const wiql = `SELECT [System.Id], [System.Title], [System.State] FROM WorkItems 
                 WHERE [System.Title] CONTAINS '${safe}' OR [System.Id] = '${safe}'
                 ORDER BY [System.ChangedDate] DESC`;
    return this.runWIQL(wiql);
  }

  async filterWorkItems(filter: any): Promise<WorkItem[]> {
    const base =
      'SELECT [System.Id], [System.Title], [System.State], [System.WorkItemType], [System.AssignedTo], [System.IterationPath] FROM WorkItems WHERE ';
    const clauses: string[] = [];
    if (filter?.sprint && filter.sprint !== 'All') {
      if (filter.sprint === '@CurrentIteration')
        clauses.push('[System.IterationPath] UNDER @CurrentIteration');
      else clauses.push(`[System.IterationPath] = '${this._escapeWIQL(filter.sprint)}'`);
    }
    if (filter?.includeState)
      clauses.push(`[System.State] = '${this._escapeWIQL(filter.includeState)}'`);
    else if (Array.isArray(filter?.excludeStates) && filter.excludeStates.length) {
      filter.excludeStates.forEach((st: string) =>
        clauses.push(`[System.State] <> '${this._escapeWIQL(st)}'`)
      );
    }
    if (filter?.type && filter.type !== 'All')
      clauses.push(`[System.WorkItemType] = '${this._escapeWIQL(filter.type)}'`);
    if (filter?.assignedTo === 'Me') clauses.push('[System.AssignedTo] = @Me');
    else if (filter?.assignedTo === 'Unassigned') clauses.push('[System.AssignedTo] = ""');
    if (clauses.length === 0) clauses.push('[System.State] <> "Removed"');
    const wiql = base + clauses.join(' AND ') + ' ORDER BY [System.ChangedDate] DESC';
    return this.runWIQL(wiql);
  }

  async createWorkItem(
    type: string,
    title: string,
    description?: string,
    assignedTo?: string,
    extraFields?: Record<string, unknown>
  ): Promise<WorkItem> {
    console.log('[AzureDevOpsInt] [AzureClient]  Creating work item:', {
      type,
      title: title.substring(0, 50) + '...',
    });

    const patch: any[] = [{ op: 'add', path: '/fields/System.Title', value: title }];
    if (description)
      patch.push({ op: 'add', path: '/fields/System.Description', value: description });
    if (assignedTo) patch.push({ op: 'add', path: '/fields/System.AssignedTo', value: assignedTo });
    if (extraFields && typeof extraFields === 'object') {
      for (const [field, value] of Object.entries(extraFields)) {
        if (value === undefined || value === null || value === '') continue;
        patch.push({ op: 'add', path: `/fields/${field}`, value });
      }
    }

    try {
      const resp = await this.axios.post(`/wit/workitems/$${type}?api-version=7.0`, patch, {
        headers: { 'Content-Type': 'application/json-patch+json' },
      });

      console.log('[AzureDevOpsInt] [AzureClient]  Work item created successfully!');
      console.log(`[AzureDevOpsInt] [AzureClient]  Work Item ID: ${resp.data.id}`);
      console.log(
        `[AzureDevOpsInt] [AzureClient]  This confirms your authentication has work item WRITE permissions`
      );
      console.log(
        `[AzureDevOpsInt] [AzureClient]  Required permission: vso.work_write (for PAT) or equivalent scope (for OAuth)`
      );

      return { id: resp.data.id, fields: resp.data.fields } as WorkItem;
    } catch (error: any) {
      console.error(
        '[AzureDevOpsInt] [AzureClient]  Work item creation failed:',
        error.response?.status,
        error.response?.statusText
      );
      if (error.response?.status === 403) {
        console.error(
          '[AzureDevOpsInt] [AzureClient]  Permission denied - your authentication lacks work item write permissions'
        );
        console.error(
          '[AzureDevOpsInt] [AzureClient]  Check your PAT scopes or Azure AD permissions'
        );
      }
      throw error;
    }
  }

  async updateWorkItem(id: number, patchOps: any[]): Promise<WorkItem> {
    const resp = await this.axios.patch(`/wit/workitems/${id}?api-version=7.0`, patchOps, {
      headers: { 'Content-Type': 'application/json-patch+json' },
    });
    return { id: resp.data.id, fields: resp.data.fields } as WorkItem;
  }

  async addWorkItemComment(id: number, text: string) {
    const resp = await this.axios.post(`/wit/workitems/${id}/comments?api-version=7.0-preview.3`, {
      text,
    });
    return resp.data; // comment response shape not forced into WorkItem
  }

  async addTimeEntry(id: number, hours: number, note?: string) {
    if (typeof hours !== 'number' || hours <= 0) throw new Error('hours must be positive number');
    const patch = [
      { op: 'add', path: '/fields/Microsoft.VSTS.Scheduling.CompletedWork', value: hours },
    ];
    await this.updateWorkItem(id, patch);
    if (note) await this.addWorkItemComment(id, `Time tracked: ${hours} hours. ${note}`);
  }

  async getWorkItemTypes(): Promise<any[]> {
    try {
      const resp = await this.axios.get(`/wit/workitemtypes?api-version=7.0`);
      return resp.data.value || [];
    } catch (e) {
      console.error('[AzureDevOpsInt] Error fetching work item types:', e);
      return [];
    }
  }

  async getWorkItemTypeStates(workItemType: string): Promise<string[]> {
    try {
      const resp = await this.axios.get(
        `/wit/workitemtypes/${encodeURIComponent(workItemType)}?api-version=7.0`
      );
      const states = resp.data.states || [];
      return states.map((state: any) => state.name || state);
    } catch (e) {
      console.error(
        `[AzureDevOpsInt] Error fetching states for work item type ${workItemType}:`,
        e
      );
      return [];
    }
  }

  async getIterations() {
    try {
      const url = this.buildTeamApiUrl('/work/teamsettings/iterations?api-version=7.0');
      const resp = await this.axios.get(url);
      return resp.data.value || [];
    } catch (err) {
      console.error('[AzureDevOpsInt] Error fetching iterations:', err);
      return [];
    }
  }

  async getCurrentIteration() {
    return await measureAsync('getCurrentIteration', async () => {
      try {
        // Check cache first
        const cacheKey = WorkItemCache.generateMetadataKey(
          `${this.organization}-${this.project}`,
          `currentIteration-${this.team || 'default'}`
        );

        const cached = workItemCache.get(cacheKey);
        if (cached) {
          return cached;
        }

        const url = this.buildTeamApiUrl(
          '/work/teamsettings/iterations?$timeframe=current&api-version=7.0'
        );
        const resp = await this.axios.get(url);
        const result = resp.data.value?.[0] || null;

        // Cache the result
        workItemCache.setMetadata(cacheKey, result);
        return result;
      } catch (err) {
        console.error('[AzureDevOpsInt] Error fetching current iteration:', err);
        return null;
      }
    });
  }

  private _escapeWIQL(value: string) {
    return String(value).replace(/'/g, "''");
  }

  // ---------------- Git & PR Helpers ----------------
  async getTeams() {
    return await measureAsync('getTeams', async () => {
      try {
        // Check cache first
        const cacheKey = WorkItemCache.generateMetadataKey(
          `${this.organization}-${this.project}`,
          'teams'
        );

        const cached = workItemCache.get(cacheKey);
        if (cached) {
          return cached;
        }

        // Call the org-level projects API directly using the configured baseUrl so we target
        // the correct host regardless of axios.baseURL (which may include project/_apis).
        const orgTeamsUrl =
          this.baseUrl.replace(/\/$/, '') +
          `/_apis/projects/${this.encodedProject}/teams?api-version=7.0`;
        // Note: build the absolute URL so axios will call the intended host rather than prefixing baseURL.
        const resp = await this.axios.get(orgTeamsUrl);
        const result = resp.data?.value || [];

        // Cache the result
        workItemCache.setMetadata(cacheKey, result);
        return result;
      } catch (e) {
        console.error('[AzureDevOpsInt] Error fetching teams', e);
        return [];
      }
    });
  }
  async getRepositories(force = false) {
    return await measureAsync('getRepositories', async () => {
      if (this._repoCache && !force) return this._repoCache;

      // Check cache first
      const cacheKey = WorkItemCache.generateMetadataKey(
        `${this.organization}-${this.project}`,
        'repositories'
      );

      if (!force) {
        const cached = workItemCache.get(cacheKey);
        if (cached) {
          this._repoCache = cached;
          return cached;
        }
      }

      try {
        const resp = await this.axios.get(`/git/repositories`);
        this._repoCache = resp.data.value || [];

        // Cache the result
        workItemCache.setMetadata(cacheKey, this._repoCache);
        return this._repoCache;
      } catch (e) {
        console.error('[AzureDevOpsInt] Error fetching repositories', e);
        return [];
      }
    });
  }

  async getDefaultRepository() {
    const repos = await this.getRepositories();
    return Array.isArray(repos) ? repos[0] : undefined;
  }

  async getPullRequests(repositoryId: string, status: string = 'active') {
    try {
      const resp = await this.axios.get(`/git/pullrequests`, {
        params: { 'searchCriteria.repositoryId': repositoryId, 'searchCriteria.status': status },
      });
      const prs = resp.data.value || [];
      return prs.map((pr: any) => ({
        id: pr.pullRequestId,
        title: pr.title,
        status: pr.status,
        createdBy: pr.createdBy?.displayName,
        sourceRefName: pr.sourceRefName,
        targetRefName: pr.targetRefName,
        repository: pr.repository?.name,
        webUrl:
          pr._links?.web?.href ||
          this.getBrowserUrl(
            `/_git/${encodeURIComponent(pr.repository?.name)}/pullrequest/${pr.pullRequestId}`
          ),
      }));
    } catch (e) {
      console.error('[AzureDevOpsInt] Error fetching pull requests', e);
      return [];
    }
  }

  /**
   * Fetch pull requests across all repositories in the project, filtered by creator or reviewer = current user.
   * Merges results for authored and reviewing PRs to emulate an OR filter. Duplicates are de-duplicated by PR id.
   */
  async getMyPullRequestsAcrossRepos(status: string = 'active') {
    const me = await this.getAuthenticatedUserId();
    if (!me) {
      console.warn(
        '[azureDevOpsInt] Could not determine authenticated user id; returning empty PR list'
      );
      return [] as Array<{
        id: number;
        title: string;
        status: string;
        createdBy?: string;
        sourceRefName?: string;
        targetRefName?: string;
        repository?: string;
        webUrl?: string;
      }>;
    }

    try {
      // Query without repositoryId to search across all repos in the project
      const [authoredResp, reviewingResp] = await Promise.all([
        this.axios.get(`/git/pullrequests`, {
          params: { 'searchCriteria.creatorId': me, 'searchCriteria.status': status },
        }),
        this.axios.get(`/git/pullrequests`, {
          params: { 'searchCriteria.reviewerId': me, 'searchCriteria.status': status },
        }),
      ]);
      const authored = authoredResp.data?.value || [];
      const reviewing = reviewingResp.data?.value || [];
      const combined = [...authored, ...reviewing];
      const seen = new Set<number>();
      const mapped = combined
        .filter((pr: any) => pr && typeof pr.pullRequestId === 'number')
        .filter((pr: any) => {
          if (seen.has(pr.pullRequestId)) return false;
          seen.add(pr.pullRequestId);
          return true;
        })
        .map((pr: any) => ({
          id: pr.pullRequestId,
          title: pr.title,
          status: pr.status,
          createdBy: pr.createdBy?.displayName,
          sourceRefName: pr.sourceRefName,
          targetRefName: pr.targetRefName,
          repository: pr.repository?.name,
          webUrl:
            pr._links?.web?.href ||
            this.getBrowserUrl(
              `/_git/${encodeURIComponent(pr.repository?.name)}/pullrequest/${pr.pullRequestId}`
            ),
          createdDate: pr.creationDate,
        }));
      // Sort by creation date desc if available
      mapped.sort((a: any, b: any) => {
        const at = a.createdDate ? new Date(a.createdDate).getTime() : 0;
        const bt = b.createdDate ? new Date(b.createdDate).getTime() : 0;
        return bt - at;
      });
      return mapped;
    } catch (e) {
      console.error('[AzureDevOpsInt] Error fetching my pull requests across repos', e);
      return [];
    }
  }

  async createPullRequest(
    repositoryId: string,
    sourceRefName: string,
    targetRefName: string,
    title: string,
    description?: string
  ) {
    try {
      const payload: any = { sourceRefName, targetRefName, title };
      if (description) payload.description = description;
      const resp = await this.axios.post(
        `/git/repositories/${repositoryId}/pullrequests?api-version=7.0`,
        payload
      );
      return resp.data;
    } catch (e) {
      console.error('[AzureDevOpsInt] Error creating pull request', e);
      throw e;
    }
  }

  // ---------------- Build Helpers ----------------
  async getRecentBuilds(
    options: {
      top?: number;
      branchName?: string;
      repositoryId?: string;
      definitions?: number | number[];
      resultFilter?: string;
      statusFilter?: string;
    } = {}
  ): Promise<WorkItemBuildSummary[]> {
    const { top = 10, branchName, repositoryId, definitions, resultFilter, statusFilter } = options;

    try {
      const params: Record<string, any> = {
        $top: top,
        queryOrder: 'finishTimeDescending',
        'api-version': '7.0',
      };
      if (branchName) params.branchName = branchName;
      if (repositoryId) params.repositoryId = repositoryId;
      if (definitions !== undefined) {
        params.definitions = Array.isArray(definitions) ? definitions.join(',') : definitions;
      }
      if (resultFilter) params.resultFilter = resultFilter;
      if (statusFilter) params.statusFilter = statusFilter;

      const resp = await this.axios.get(`/build/builds`, { params });
      const values = Array.isArray(resp.data?.value) ? resp.data.value : [];
      return values.map((b: any) => ({
        id: b.id,
        buildNumber: b.buildNumber,
        status: b.status,
        result: b.result,
        definition: b.definition?.name,
        queueTime: b.queueTime,
        finishTime: b.finishTime,
        webUrl: b._links?.web?.href,
      })) as WorkItemBuildSummary[];
    } catch (e) {
      console.error('[AzureDevOpsInt] Error fetching builds', e);
      return [];
    }
  }
}

