      connected: {
        entry: ['notifyConnectionSuccess'],
        on: {
          DISCONNECT: 'disconnected',
          CONNECTION_FAILED: 'connection_error',
          TOKEN_EXPIRED: 'token_refresh',
          REFRESH_AUTH: 'token_refresh',
