      },

      auth_failed: {
        entry: ['notifyAuthFailure'],
        on: {
          RETRY: {
            target: 'authenticating',
            guard: 'canRetry',
          },
          CONNECT: {
            target: 'authenticating',
            actions: assign({
              retryCount: 0,
            }),
          },
          DISCONNECT: 'disconnected',
