function activateConnection(context: ApplicationContext, connectionId: string) {
  if (context.isDeactivating) return;
  const actor = context.connectionActors.get(connectionId);
  if (actor) {
    actor.send({ type: 'CONNECT' });
  }
}

function startAuthentication(context: ApplicationContext, connectionId: string) {
  if (context.isDeactivating) return;
  const authActor = context.authActors.get(connectionId);
  if (authActor) {
    authActor.send({ type: 'AUTHENTICATE' });
  }
}

import type { ApplicationContext } from '../../machines/applicationMachine.js';

export type KanbanColumn = { id: string; title: string; itemIds: number[] };

// Pure function: derives columns from work item list based on System.State
export function computeKanbanColumns(workItems: any[]): KanbanColumn[] {
  const stateMap = new Map<string, number[]>();
  for (const wi of workItems) {
    const id = typeof wi?.id === 'number' ? wi.id : undefined;
    if (id === undefined) continue;
    const state = wi?.fields?.['System.State'] || 'Unknown';
    if (!stateMap.has(state)) stateMap.set(state, []);
    stateMap.get(state)!.push(id);
  }
  const columns: KanbanColumn[] = [];
  for (const [state, ids] of stateMap.entries()) {
    columns.push({ id: state, title: state, itemIds: ids });
  }
  return columns;
}

// Optional helper to access from FSM context
export function getKanbanColumns(context: ApplicationContext): KanbanColumn[] | undefined {
  return context.kanbanColumns;
}

import { fsmLogger, FSMComponent } from '../../logging/FSMLogger.js';
import type { ApplicationContext } from '../../machines/applicationMachine.js';

/**
 * Pure function to toggle debug view visibility.
 * Enforces FSM-first pattern: receives context, returns partial context updates only.
 */
export function toggleDebugView(context: Pick<ApplicationContext, 'debugViewVisible'>) {
  const next = !context.debugViewVisible;
  try {
    fsmLogger.debug(
      FSMComponent.WEBVIEW,
      'Toggling debug view visibility',
      {
        state: next ? 'visible' : 'hidden',
        event: 'TOGGLE_DEBUG_VIEW',
        component: FSMComponent.WEBVIEW,
      },
      { previous: context.debugViewVisible, next }
    );
  } catch {
    // Ignore logging errors (e.g. during early activation or test environments)
  }
  return { debugViewVisible: next };
}

import { createMachine, assign } from 'xstate';
import { TimerContext, TimerEvent } from '../types';
import { FSM_CONFIG } from '../config';
import { createComponentLogger, FSMComponent } from '../logging/FSMLogger';

// Create logger for timer machine
const logger = createComponentLogger(FSMComponent.MACHINE, 'timerMachine');

// Create the timer state machine using XState v5 syntax
export const timerMachine = createMachine({
  id: 'timer',
  initial: 'idle',

  types: {} as {
    context: TimerContext;
    events: TimerEvent;
  },

  context: {
    workItemId: undefined,
    workItemTitle: undefined,
    startTime: undefined,
    pausedAt: undefined,
    isPaused: false,
    lastActivity: Date.now(),
    inactivityTimeoutSec: FSM_CONFIG.timer.inactivityTimeoutSec,
    defaultElapsedLimitHours: FSM_CONFIG.timer.defaultElapsedLimitHours,
    pomodoroEnabled: false,
    pomodoroCount: 0,
  },

  states: {
    idle: {
      on: {
        START: {
          target: 'running',
          actions: [
            assign(({ event }) => ({
              workItemId: event.workItemId,
              workItemTitle: event.workItemTitle,
              startTime: Date.now(),
              isPaused: false,
              lastActivity: Date.now(),
            })),
          ],
        },
        RESTORE: [
          {
            target: 'paused',
            guard: ({ event }) => event.type === 'RESTORE' && event.isPaused,
            actions: [
              assign(({ event }) => ({
                workItemId: event.workItemId,
                workItemTitle: event.workItemTitle,
                startTime: event.startTime,
                isPaused: event.isPaused,
                lastActivity: Date.now(),
              })),
            ],
          },
          {
            target: 'running',
            actions: [
              assign(({ event }) => ({
                workItemId: event.workItemId,
                workItemTitle: event.workItemTitle,
                startTime: event.startTime,
                isPaused: event.isPaused,
                lastActivity: Date.now(),
              })),
            ],
          },
        ],
      },
    },

    running: {
      entry: [assign(() => ({ lastActivity: Date.now() }))],

      on: {
        PAUSE: {
          target: 'paused',
          actions: [
            assign(() => ({
              isPaused: true,
              pausedAt: Date.now(),
            })),
          ],
        },

        STOP: {
          target: 'idle',
          actions: [
            assign(() => ({
              workItemId: undefined,
              workItemTitle: undefined,
              startTime: undefined,
              pausedAt: undefined,
              isPaused: false,
              pomodoroCount: 0,
            })),
          ],
        },

        ACTIVITY: {
          actions: [assign(() => ({ lastActivity: Date.now() }))],
        },

        INACTIVITY_TIMEOUT: {
          target: 'paused',
          actions: [
            assign(() => ({
              isPaused: true,
              pausedAt: Date.now(),
            })),
            () => logger.info('Timer paused due to inactivity'),
          ],
        },
      },
    },

    paused: {
      entry: [],
      on: {
        RESUME: {
          target: 'running',
          actions: [
            assign(({ context }) => {
              const now = Date.now();
              const pauseDuration =
                context.pausedAt && context.startTime ? now - context.pausedAt : 0;
              return {
                isPaused: false,
                startTime: context.startTime ? context.startTime + pauseDuration : now,
                pausedAt: undefined,
                lastActivity: now,
              };
            }),
          ],
        },

        STOP: {
          target: 'idle',
          actions: [
            assign(() => ({
              workItemId: undefined,
              workItemTitle: undefined,
              startTime: undefined,
              pausedAt: undefined,
              isPaused: false,
              pomodoroCount: 0,
            })),
          ],
        },

        ACTIVITY: [
          {
            target: 'running',
            guard: ({ context }) => context.isPaused && context.lastActivity > 0,
            actions: [
              assign(({ context }) => {
                const now = Date.now();
                const pauseDuration =
                  context.pausedAt && context.startTime ? now - context.pausedAt : 0;
                return {
                  isPaused: false,
                  startTime: context.startTime ? context.startTime + pauseDuration : now,
                  pausedAt: undefined,
                  lastActivity: now,
                };
              }),
            ],
          },
          {
            actions: [assign(() => ({ lastActivity: Date.now() }))],
          },
        ],
      },
    },
  },
});

import { createMachine, assign, fromPromise } from 'xstate';
import type { ProjectConnection } from './connectionTypes.js';
import { getPat, getEntraIdToken } from '../functions/auth/authentication.js';
import type { ExtensionContext } from 'vscode';

export type AuthContext = {
  connection: ProjectConnection;
  extensionContext: ExtensionContext;
  token?: string;
  error?: string;
};

export type AuthEvent =
  | { type: 'AUTHENTICATE' }
  | { type: 'LOGOUT' }
  | { type: 'AUTH_SUCCESS'; token: string }
  | { type: 'AUTH_FAILED'; error: string };

export const authMachine = createMachine(
  {
    id: 'authentication',
    types: {} as {
      context: AuthContext;
      events: AuthEvent;
    },
    initial: 'idle',
    context: ({
      input,
    }: {
      input: { connection: ProjectConnection; extensionContext: ExtensionContext };
    }) => ({
      connection: input.connection,
      extensionContext: input.extensionContext,
    }),
    states: {
      idle: {
        on: {
          AUTHENTICATE: 'authenticating',
        },
      },
      authenticating: {
        invoke: {
          src: 'authenticate',
          input: ({ context }) => ({
            connection: context.connection,
            extensionContext: context.extensionContext,
          }),
          onDone: {
            target: 'authenticated',
            actions: assign({
              token: ({ event }) => event.output,
            }),
          },
          onError: {
            target: 'failed',
            actions: assign({
              error: ({ event }) => (event.error as Error).message,
            }),
          },
        },
      },
      authenticated: {
        on: {
          LOGOUT: {
            target: 'idle',
            actions: [
              async ({ context }) => {
                const { connection, extensionContext } = context;
                if (connection.authMethod === 'pat' && connection.patKey) {
                  await extensionContext.secrets.delete(connection.patKey);
                } else if (connection.authMethod === 'entra' && connection.tenantId) {
                  await extensionContext.secrets.delete(`entra:${connection.tenantId}`);
                }
              },
              assign({
                token: undefined,
              }),
            ],
          },
        },
      },
      failed: {
        on: {
          AUTHENTICATE: 'authenticating',
        },
      },
    },
  },
  {
    actors: {
      authenticate: fromPromise(
        async ({
          input,
        }: {
          input: {
            connection: ProjectConnection;
            extensionContext: ExtensionContext;
          };
        }) => {
          const { connection, extensionContext } = input;
          if (connection.authMethod === 'pat') {
            return getPat(extensionContext, connection.patKey);
          }
          // Assuming 'entra' is the only other option
          return getEntraIdToken(extensionContext, connection.tenantId);
        }
      ),
    },
  }
);

import type { ExtensionContext } from 'vscode';
import * as msal from '@azure/msal-node';
import * as vscode from 'vscode';

export async function getPat(context: ExtensionContext, patKey?: string): Promise<string> {
  if (!patKey) {
    throw new Error('PAT key is not defined for this connection.');
  }
  const pat = await context.secrets.get(patKey);
  if (!pat) {
    throw new Error(`PAT not found in secret storage for key: ${patKey}`);
  }
  return pat;
}

const msalConfig = {
  auth: {
    clientId: 'a5243d69-523e-496b-a22c-7ff3b5a3e85b', // Replace with your client ID
    authority: 'https://login.microsoftonline.com/common',
  },
};

export async function getEntraIdToken(
  context: ExtensionContext,
  tenantId?: string
): Promise<string> {
  if (!tenantId) {
    throw new Error('Tenant ID is not defined for this connection.');
  }
  const secretKey = `entra:${tenantId}`;
  // Try to get cached token first
  const cachedToken = await context.secrets.get(secretKey);
  if (cachedToken) {
    return cachedToken;
  }

  const pca = new msal.PublicClientApplication(msalConfig);
  const deviceCodeRequest = {
    deviceCodeCallback: (response: any) => {
      vscode.window.showInformationMessage(response.message);
    },
    scopes: ['499b84ac-1321-427f-aa17-267ca6975798/.default'], // Azure DevOps scope
  };

  try {
    const tokenResponse = await pca.acquireTokenByDeviceCode(deviceCodeRequest);
    if (tokenResponse && tokenResponse.accessToken) {
      // Store the token securely
      await context.secrets.store(secretKey, tokenResponse.accessToken);
      return tokenResponse.accessToken;
    } else {
      throw new Error('Failed to acquire Entra ID token.');
    }
  } catch (error) {
    throw new Error(`Entra ID token acquisition failed: ${error}`);
  }
}

import { createMachine, assign, fromPromise } from 'xstate';
import { getWorkItemTrackingApi } from '../functions/azure/azure-devops-client.js';
import type { WorkItem } from 'azure-devops-node-api/interfaces/WorkItemTrackingInterfaces.js';

export type DataContext = {
  serverUrl: string;
  token: string;
  workItems?: WorkItem[];
  error?: string;
};

export type DataEvent =
  | { type: 'FETCH' }
  | { type: 'FETCH_SUCCESS'; workItems: WorkItem[] }
  | { type: 'FETCH_ERROR'; error: string };

export const dataMachine = createMachine(
  {
    id: 'data',
    types: {} as {
      context: DataContext;
      events: DataEvent;
    },
    initial: 'idle',
    context: ({ input }: { input: { serverUrl: string; token: string } }) => ({
      serverUrl: input.serverUrl,
      token: input.token,
    }),
    states: {
      idle: {
        on: {
          FETCH: 'fetching',
        },
      },
      fetching: {
        invoke: {
          src: 'fetchWorkItems',
          input: ({ context }) => ({
            serverUrl: context.serverUrl,
            token: context.token,
          }),
          onDone: {
            target: 'success',
            actions: assign({
              workItems: ({ event }) => event.output,
            }),
          },
          onError: {
            target: 'error',
            actions: assign({
              error: ({ event }) => (event.error as Error).message,
            }),
          },
        },
      },
      success: {
        on: {
          FETCH: 'fetching',
        },
      },
      error: {
        on: {
          FETCH: 'fetching',
        },
      },
    },
  },
  {
    actors: {
      fetchWorkItems: fromPromise(
        async ({ input }: { input: { serverUrl: string; token: string } }) => {
          const witApi = await getWorkItemTrackingApi(input.serverUrl, input.token);
          // This is a simplified query. In a real scenario, you would use a more complex WIQL query.
          const queryResult = await witApi.queryByWiql({
            query:
              "SELECT [System.Id] FROM workitems WHERE [System.State] <> 'Closed' AND [System.State] <> 'Done'",
          });

          if (queryResult.workItems) {
            const workItemIds = queryResult.workItems
              .map((wi) => wi.id)
              .filter((id): id is number => id !== undefined);
            if (workItemIds.length > 0) {
              return await witApi.getWorkItems(workItemIds);
            }
          }
          return [];
        }
      ),
    },
  }
);

import * as azdo from 'azure-devops-node-api';

export function getAzureDevOpsApi(serverUrl: string, token: string): azdo.WebApi {
  const authHandler = azdo.getPersonalAccessTokenHandler(token);
  const connection = new azdo.WebApi(serverUrl, authHandler);
  return connection;
}

export async function getWorkItemTrackingApi(serverUrl: string, token: string) {
  const webApi = getAzureDevOpsApi(serverUrl, token);
  return await webApi.getWorkItemTrackingApi();
}

import type { ApplicationContext, ProjectConnection } from '../machines/applicationMachine.js';

export function saveConnection(
  context: ApplicationContext,
  event: { type: 'SAVE_CONNECTION'; connection: ProjectConnection }
): ProjectConnection[] {
  const newConnection = event.connection;
  const existingIndex = context.connections.findIndex((c) => c.id === newConnection.id);
  const newConnections = [...context.connections];
  if (existingIndex > -1) {
    newConnections[existingIndex] = newConnection;
  } else {
    newConnections.push(newConnection);
  }
  // TODO: Persist to storage here
  return newConnections;
}

export function deleteConnection(
  context: ApplicationContext,
  event: { type: 'CONFIRM_DELETE_CONNECTION'; connectionId: string }
): ProjectConnection[] {
  const newConnections = context.connections.filter((c) => c.id !== event.connectionId);
  // TODO: Persist to storage here
  return newConnections;
}

import type {
  ApplicationContext,
  AuthReminderReason,
  AuthReminderState,
} from '../../machines/applicationMachine.js';
import { buildAuthReminder } from './buildAuthReminder.js';

export type AuthReminderActionMessage = {
  type: 'authReminderAction';
  connectionId?: unknown;
  action?: unknown;
};

export type AuthReminderActionPlan = {
  handled: boolean;
  connectionId?: string;
  pendingAuthReminders: Map<string, AuthReminderState>;
  notify?: {
    type: 'clear';
    connectionId: string;
  };
  interactiveAuth?: {
    connectionId: string;
    reason: AuthReminderReason;
    detail?: string;
  };
  warnings: string[];
};

export const AUTH_REMINDER_SNOOZE_MS = 30 * 60 * 1000;

export function handleAuthReminderAction(
  context: ApplicationContext,
  message: AuthReminderActionMessage,
  options?: {
    now?: number;
    snoozeMs?: number;
  }
): AuthReminderActionPlan {
  const warnings: string[] = [];
  const connectionId = normalizeConnectionId(message?.connectionId);

  if (!connectionId) {
    warnings.push('missing_connection_id');
    return {
      handled: false,
      pendingAuthReminders: context.pendingAuthReminders,
      warnings,
    };
  }

  const action = normalizeAction(message?.action);
  if (!action) {
    warnings.push('missing_action');
    return {
      handled: false,
      connectionId,
      pendingAuthReminders: context.pendingAuthReminders,
      warnings,
    };
  }

  const nextReminders = new Map(context.pendingAuthReminders);
  const now = typeof options?.now === 'number' ? options!.now : Date.now();
  const snoozeMs =
    typeof options?.snoozeMs === 'number' ? options!.snoozeMs : AUTH_REMINDER_SNOOZE_MS;

  if (action === 'signIn') {
    const existing = nextReminders.get(connectionId);
    if (existing) {
      nextReminders.delete(connectionId);
    } else {
      nextReminders.delete(connectionId);
      warnings.push('reminder_not_found');
    }

    const reason: AuthReminderReason = existing?.reason ?? 'authFailed';

    return {
      handled: true,
      connectionId,
      pendingAuthReminders: nextReminders,
      notify: { type: 'clear', connectionId },
      interactiveAuth: {
        connectionId,
        reason,
        detail: existing?.detail,
      },
      warnings,
    };
  }

  if (action === 'dismiss') {
    const snoozeUntil = now + snoozeMs;
    let reminder = nextReminders.get(connectionId);

    if (!reminder) {
      const built = buildAuthReminder(context, {
        connectionId,
        reason: 'authFailed',
        now,
      });

      if (!built) {
        warnings.push('reminder_unavailable');
        return {
          handled: false,
          connectionId,
          pendingAuthReminders: context.pendingAuthReminders,
          warnings,
        };
      }

      reminder = built;
    }

    nextReminders.set(connectionId, {
      ...reminder,
      status: 'dismissed',
      snoozeUntil,
    });

    return {
      handled: true,
      connectionId,
      pendingAuthReminders: nextReminders,
      warnings,
    };
  }

  warnings.push('unknown_action');
  return {
    handled: false,
    connectionId,
    pendingAuthReminders: context.pendingAuthReminders,
    warnings,
  };
}

function normalizeConnectionId(raw: unknown): string | undefined {
  if (typeof raw !== 'string') {
    return undefined;
  }

  const value = raw.trim();
  return value.length > 0 ? value : undefined;
}

function normalizeAction(raw: unknown): string | undefined {
  if (typeof raw !== 'string') {
    return undefined;
  }

  const value = raw.trim();
  return value.length > 0 ? value : undefined;
}

import type {
  ApplicationContext,
  AuthReminderReason,
  AuthReminderState,
  ProjectConnection,
} from '../../machines/applicationMachine.js';

export type BuildAuthReminderParams = {
  connectionId: string;
  reason: AuthReminderReason;
  detail?: string;
  now?: number;
};

const REMINDER_MESSAGES: Record<AuthReminderReason, string> = {
  tokenExpired: 'Microsoft Entra access expired for {{label}}.',
  refreshFailed: 'Microsoft Entra sign-in required for {{label}}: token refresh failed.',
  authFailed: 'Microsoft Entra sign-in required for {{label}}.',
};

const WEBVIEW_ACTION_DETAIL =
  'Use Sign In here or select the authentication status bar item to reconnect.';

export function buildAuthReminder(
  context: ApplicationContext,
  params: BuildAuthReminderParams
): AuthReminderState | null {
  const { connectionId, reason, detail, now: nowOverride } = params;
  const connectionState = context.connectionStates.get(connectionId);
  if (!connectionState) {
    return null;
  }

  const now = typeof nowOverride === 'number' ? nowOverride : Date.now();
  const existing = context.pendingAuthReminders.get(connectionId);

  if (existing) {
    if (existing.status === 'pending') {
      return null;
    }

    if (
      existing.status === 'dismissed' &&
      typeof existing.snoozeUntil === 'number' &&
      existing.snoozeUntil > now
    ) {
      return null;
    }
  }

  const connectionConfig = connectionState.config ?? findConnection(context, connectionId);
  const label = formatConnectionLabel(connectionConfig, connectionId);
  const authMethod = connectionConfig?.authMethod ?? connectionState.authMethod ?? 'pat';

  const details: string[] = [];
  if (typeof detail === 'string' && detail.trim().length > 0) {
    details.push(detail.trim());
  }
  details.push(WEBVIEW_ACTION_DETAIL);

  return {
    connectionId,
    status: 'pending',
    reason,
    detail: details.join('\n\n'),
    message: renderReminderMessage(reason, label),
    label,
    authMethod,
  };
}

function renderReminderMessage(reason: AuthReminderReason, label: string): string {
  const template = REMINDER_MESSAGES[reason] ?? REMINDER_MESSAGES.tokenExpired;
  return template.replace('{{label}}', label);
}

function findConnection(
  context: ApplicationContext,
  connectionId: string
): ProjectConnection | undefined {
  return context.connections.find((connection) => connection.id === connectionId);
}

function formatConnectionLabel(
  connection: ProjectConnection | undefined,
  fallbackId: string
): string {
  if (connection?.label && connection.label.trim().length > 0) {
    return connection.label;
  }

  const parts: string[] = [];
  if (connection?.organization) {
    parts.push(connection.organization);
  }
  if (connection?.project) {
    parts.push(connection.project);
  }

  if (parts.length > 0) {
    return parts.join('/');
  }

  if (connection?.id) {
    return connection.id;
  }

  return fallbackId;
}

import type { ApplicationContext, AuthReminderReason } from '../../machines/applicationMachine.js';

export type RequireAuthenticationMessage = {
  type: 'requireAuthentication';
  connectionId?: unknown;
};

export type RequireAuthenticationPlan = {
  connectionId?: string;
  interactiveAuth?: {
    connectionId: string;
    reason: AuthReminderReason;
    detail?: string;
  };
  warnings: string[];
};

export function planRequireAuthentication(
  context: ApplicationContext,
  message: RequireAuthenticationMessage
): RequireAuthenticationPlan {
  const warnings: string[] = [];
  const connectionId = normalizeConnectionId(message?.connectionId);

  if (!connectionId) {
    warnings.push('missing_connection_id');
    return {
      warnings,
    };
  }

  const state = context.connectionStates.get(connectionId);
  if (!state) {
    warnings.push('connection_state_missing');
    return {
      connectionId,
      warnings,
    };
  }

  if (state.reauthInProgress) {
    warnings.push('reauth_in_progress');
    return {
      connectionId,
      warnings,
    };
  }

  const reason: AuthReminderReason = 'authFailed';

  return {
    connectionId,
    interactiveAuth: {
      connectionId,
      reason,
    },
    warnings,
  };
}

function normalizeConnectionId(raw: unknown): string | undefined {
  if (typeof raw !== 'string') {
    return undefined;
  }

  const value = raw.trim();
  return value.length > 0 ? value : undefined;
}

import { createActor, Actor } from 'xstate';
import { timerMachine } from './machines/timerMachine';
import { TimerContext, TimerEvent as _TimerEvent } from './types';
import { setupFSMInspector, FSM_CONFIG } from './config';
import { createComponentLogger, FSMComponent } from './logging/FSMLogger.js';
import { fsmTracer } from './logging/FSMTracer.js';

export class FSMManager {
  private timerActor: Actor<typeof timerMachine> | undefined;
  private inspector: any;
  private isStarted = false;
  private logger = createComponentLogger(FSMComponent.TIMER, 'FSMManager');
  private traceCleanup: (() => void) | undefined;

  constructor() {
    this.logger.info('FSMManager created');
    // Setup inspector for development
    this.inspector = setupFSMInspector();
  }

  start(): void {
    if (this.isStarted) {
      this.logger.warn('FSM Manager already started');
      return;
    }

    try {
      // Initialize timer actor
      this.timerActor = createActor(timerMachine);

      // Setup inspector if available
      if (this.inspector) {
        this.inspector.actor(this.timerActor);
      }

      // Setup FSM tracing for full replay capability
      // Cast to any to bypass Actor vs ActorRef type mismatch in XState v5
      this.traceCleanup = fsmTracer.instrumentActor(
        this.timerActor as any,
        FSMComponent.TIMER,
        'timerMachine'
      );
      this.logger.info('Timer FSM instrumented for tracing and replay');

      // Start the actors
      this.timerActor.start();

      // Setup tick interval for timer
      this.setupTimerTick();

      this.isStarted = true;
      this.logger.info('FSM Manager started successfully');
    } catch (error) {
      this.logger.error(
        'Failed to start FSM Manager: ' + (error instanceof Error ? error.message : String(error))
      );
      throw error;
    }
  }

  stop(): void {
    if (!this.isStarted) {
      return;
    }

    // Cleanup tracing
    if (this.traceCleanup) {
      this.traceCleanup();
      this.traceCleanup = undefined;
    }

    try {
      if (this.timerActor) {
        this.timerActor.stop();
        this.timerActor = undefined;
      }

      this.isStarted = false;
      this.logger.info('FSM Manager stopped');
    } catch (error) {
      this.logger.error(
        'Failed to stop FSM Manager: ' + (error instanceof Error ? error.message : String(error))
      );
    }
  }

  // Timer-specific methods
  getTimerActor(): Actor<typeof timerMachine> | undefined {
    return this.timerActor;
  }

  startTimer(workItemId: number, workItemTitle: string): boolean {
    if (!this.timerActor) {
      this.logger.error('Timer actor not initialized');
      return false;
    }

    try {
      const currentState = this.timerActor.getSnapshot();
      if (currentState.matches('idle')) {
        this.timerActor.send({ type: 'START', workItemId, workItemTitle });
        return true;
      } else {
        this.logger.warn('Timer is not idle, cannot start');
        return false;
      }
    } catch (error) {
      this.logger.error(
        'Failed to start timer: ' + (error instanceof Error ? error.message : String(error))
      );
      return false;
    }
  }

  pauseTimer(manual = true): boolean {
    if (!this.timerActor) {
      this.logger.error('Timer actor not initialized');
      return false;
    }

    try {
      const currentState = this.timerActor.getSnapshot();
      if (currentState.matches('running')) {
        this.timerActor.send({ type: 'PAUSE', manual });
        return true;
      } else {
        this.logger.warn('Timer is not running, cannot pause');
        return false;
      }
    } catch (error) {
      this.logger.error(
        'Failed to pause timer: ' + (error instanceof Error ? error.message : String(error))
      );
      return false;
    }
  }

  resumeTimer(fromActivity = false): boolean {
    if (!this.timerActor) {
      this.logger.error('Timer actor not initialized');
      return false;
    }

    try {
      const currentState = this.timerActor.getSnapshot();
      if (currentState.matches('paused')) {
        this.timerActor.send({ type: 'RESUME', fromActivity });
        return true;
      } else {
        this.logger.warn('Timer is not paused, cannot resume');
        return false;
      }
    } catch (error) {
      this.logger.error(
        'Failed to resume timer: ' + (error instanceof Error ? error.message : String(error))
      );
      return false;
    }
  }

  stopTimer(): any {
    if (!this.timerActor) {
      this.logger.error('Timer actor not initialized');
      return null;
    }

    try {
      const currentState = this.timerActor.getSnapshot();
      if (!currentState.matches('idle')) {
        const context = currentState.context;
        const endTime = Date.now();

        // Calculate elapsed seconds from startTime and pausedAt (if paused)
        let elapsedSeconds = 0;
        if (context.startTime) {
          if (context.isPaused && context.pausedAt) {
            // Timer is paused, calculate elapsed up to pause time
            elapsedSeconds = Math.floor((context.pausedAt - context.startTime) / 1000);
          } else {
            // Timer is running, calculate elapsed up to now
            elapsedSeconds = Math.floor((endTime - context.startTime) / 1000);
          }
        }

        this.timerActor.send({ type: 'STOP' });

        // Return timer stop result for compatibility
        return {
          workItemId: context.workItemId,
          startTime: context.startTime,
          endTime: endTime,
          duration: elapsedSeconds,
          hoursDecimal: Number((elapsedSeconds / 3600).toFixed(2)),
          capApplied: elapsedSeconds > context.defaultElapsedLimitHours * 3600,
          capLimitHours: context.defaultElapsedLimitHours,
        };
      } else {
        this.logger.warn('Timer is already idle');
        return null;
      }
    } catch (error) {
      this.logger.error(
        'Failed to stop timer: ' + (error instanceof Error ? error.message : String(error))
      );
      return null;
    }
  }

  activityPing(): void {
    if (this.timerActor) {
      this.timerActor.send({ type: 'ACTIVITY' });
    }
  }

  getTimerSnapshot(): any {
    if (!this.timerActor) {
      return undefined;
    }

    const currentState = this.timerActor.getSnapshot();
    const context = currentState.context;

    if (context.workItemId) {
      // Calculate elapsed seconds from startTime and current/paused time
      let elapsedSeconds = 0;
      if (context.startTime) {
        if (context.isPaused && context.pausedAt) {
          // Timer is paused, calculate elapsed up to pause time
          elapsedSeconds = Math.floor((context.pausedAt - context.startTime) / 1000);
        } else {
          // Timer is running, calculate elapsed up to now
          elapsedSeconds = Math.floor((Date.now() - context.startTime) / 1000);
        }
      }

      return {
        workItemId: context.workItemId,
        workItemTitle: context.workItemTitle,
        elapsedSeconds: elapsedSeconds,
        isPaused: context.isPaused,
        startTime: context.startTime,
        pomodoroCount: context.pomodoroCount,
        running: !context.isPaused,
      };
    }

    return undefined;
  }

  // Private methods
  private setupTimerTick(): void {
    // Setup periodic tick for timer updates
    setInterval(() => {
      if (this.timerActor) {
        const currentState = this.timerActor.getSnapshot();
        if (currentState.matches('running')) {
          this.timerActor.send({ type: 'TICK' });
        }
      }
    }, FSM_CONFIG.timer.tickIntervalMs);
  }

  // Debug and inspection methods
  getStatus(): {
    isStarted: boolean;
    timerState?: string;
    timerContext?: TimerContext;
    hasInspector: boolean;
  } {
    const timerSnapshot = this.timerActor?.getSnapshot();

    return {
      isStarted: this.isStarted,
      timerState: timerSnapshot?.value as string,
      timerContext: timerSnapshot?.context,
      hasInspector: !!this.inspector,
    };
  }

  // Subscribe to state changes
  subscribeToTimer(callback: (snapshot: any) => void): () => void {
    if (!this.timerActor) {
      throw new Error('Timer actor not initialized');
    }

    return this.timerActor.subscribe(callback).unsubscribe;
  }
}

// Singleton instance for global access
let fsmManagerInstance: FSMManager | undefined;

export function getFSMManager(): FSMManager {
  if (!fsmManagerInstance) {
    fsmManagerInstance = new FSMManager();
  }
  return fsmManagerInstance;
}

export function resetFSMManager(): void {
  if (fsmManagerInstance) {
    fsmManagerInstance.stop();
    fsmManagerInstance = undefined;
  }
}

export function makeBranchSlug(id: number | string, title?: string, prefix = 'feature') {
  const raw = String(title || '')
    .toLowerCase()
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/^-+|-+$/g, '')
    .slice(0, 40);
  const slug = raw || `wi-${id}`;
  return `${prefix}/wi-${id}-${slug}`;
}

import { randomUUID } from 'crypto';

import { execFile } from 'child_process';
import * as os from 'node:os';
import * as path from 'node:path';
import { promisify } from 'util';
import * as vscode from 'vscode';
import { TelemetryDatabase } from './telemetryDatabase.js';

const execFileAsync = promisify(execFile);

type SessionEvent = {
  id: string;
  type: string;
  timestamp: number;
  payload: Record<string, unknown>;
};

type GitSnapshot = {
  branch?: string;
  commit?: string;
  upstream?: string;
  ahead?: number;
  behind?: number;
  workingTree?: string[];
  staged?: string[];
  merge?: string[];
  statusShort?: string;
  diffStat?: string;
  diffStatStaged?: string;
};

type ActiveSession = {
  sessionId: string;
  workItemId: number;
  workItemTitle: string;
  startedAt: number;
  repoPath?: string;
  baseline?: GitSnapshot;
  events: SessionEvent[];
  disposables: vscode.Disposable[];
  repo?: any;
};

type SessionRecord = {
  sessionId: string;
  workItemId: number;
  workItemTitle: string;
  startedAt: number;
  stoppedAt: number;
  timerHours?: number;
  capApplied?: boolean;
  capLimitHours?: number;
  baseline?: GitSnapshot;
  finalSnapshot?: GitSnapshot;
  events: SessionEvent[];
};

const TELEMETRY_STORAGE_KEY = 'azureDevOpsIntegration.sessionTelemetry.lastRecord';
const CAPTURE_CONFIG_KEY = 'sessionTelemetry.enableCapture';
const STORAGE_MODE_CONFIG_KEY = 'sessionTelemetry.storageMode';
const SQLITE_PATH_CONFIG_KEY = 'sessionTelemetry.sqlitePath';
const GIT_STATUS_THROTTLE_MS = 4000;

type TelemetryStorageMode = 'sqlite' | 'workspaceState';

async function runGitCommand(repoPath: string, args: string[]): Promise<string | undefined> {
  try {
    const { stdout } = await execFileAsync('git', args, {
      cwd: repoPath,
      encoding: 'utf8',
      windowsHide: true,
    });
    return stdout.trim();
  } catch (error) {
    console.warn('[sessionTelemetry] git command failed', { args, error });
    return undefined;
  }
}

function asRelativePath(uri: vscode.Uri | undefined): string | undefined {
  if (!uri) return undefined;
  const workspace = vscode.workspace.getWorkspaceFolder(uri);
  if (workspace) {
    return vscode.workspace.asRelativePath(uri, false);
  }
  return uri.fsPath;
}

export class SessionTelemetryManager {
  private readonly context: vscode.ExtensionContext;
  private gitApi: any | undefined;
  private activeSession: ActiveSession | undefined;
  private pendingGitPulse: NodeJS.Timeout | undefined;
  private telemetryDb: TelemetryDatabase | undefined;
  private telemetryDbUri: vscode.Uri | undefined;

  constructor(context: vscode.ExtensionContext) {
    this.context = context;
  }

  get isActive() {
    return !!this.activeSession;
  }

  async ensureGitApi(): Promise<any | undefined> {
    if (this.gitApi) return this.gitApi;
    try {
      const gitExt = vscode.extensions.getExtension('vscode.git');
      if (!gitExt) return undefined;
      const api = gitExt.isActive
        ? gitExt.exports.getAPI?.(1)
        : (await gitExt.activate()).getAPI?.(1);
      this.gitApi = api;
      return api;
    } catch (error) {
      console.warn('[sessionTelemetry] Failed to acquire git API', error);
      return undefined;
    }
  }

  async startSession(args: { workItemId: number; workItemTitle: string }) {
    const config = vscode.workspace.getConfiguration('azureDevOpsIntegration');
    const enabled = config.get<boolean>(CAPTURE_CONFIG_KEY, true);
    if (!enabled) {
      this.disposeSession();
      return;
    }

    if (this.activeSession) {
      await this.finalizeSession({ reason: 'overlapping-start' });
    }

    const sessionId = randomUUID();
    const startedAt = Date.now();
    const repo = await this.getPrimaryRepository();
    const repoPath = repo?.rootUri?.fsPath ?? undefined;
    const baseline = repoPath ? await this.captureGitSnapshot(repo, repoPath) : undefined;

    const events: SessionEvent[] = [];
    const disposables: vscode.Disposable[] = [];

    if (repo && typeof repo.state?.onDidChange === 'function') {
      const repoListener = repo.state.onDidChange(() => this.queueGitPulse(repo, 'state-change'));
      disposables.push(repoListener);
    }

    const saveListener = vscode.workspace.onDidSaveTextDocument((doc) => {
      if (!this.activeSession) return;
      const relative = asRelativePath(doc.uri);
      if (!relative) return;
      this.recordEvent('workspace:file-save', {
        file: relative,
        languageId: doc.languageId,
        byteLength: doc.getText().length,
      });
    });
    disposables.push(saveListener);

    this.activeSession = {
      sessionId,
      workItemId: args.workItemId,
      workItemTitle: args.workItemTitle,
      startedAt,
      repoPath,
      baseline,
      events,
      disposables,
      repo,
    };

    this.recordEvent('timer:start', {
      workItemId: args.workItemId,
      workItemTitle: args.workItemTitle,
      baseline,
    });
  }

  async handleTimerStop(entry: {
    workItemId: number;
    duration: number;
    hoursDecimal?: number;
    startTime: number;
    endTime: number;
    capApplied?: boolean;
    capLimitHours?: number;
  }) {
    await this.finalizeSession({ reason: 'timer-stop', entry });
  }

  disposeSession() {
    if (!this.activeSession) return;
    for (const d of this.activeSession.disposables) {
      try {
        d.dispose();
      } catch {
        /* ignore dispose errors */
      }
    }
    this.activeSession = undefined;
    if (this.pendingGitPulse) {
      clearTimeout(this.pendingGitPulse);
      this.pendingGitPulse = undefined;
    }
  }

  private recordEvent(type: string, payload: Record<string, unknown>) {
    if (!this.activeSession) return;
    const event: SessionEvent = {
      id: randomUUID(),
      type,
      timestamp: Date.now(),
      payload,
    };
    this.activeSession.events.push(event);
  }

  private queueGitPulse(repo: any, reason: string) {
    if (!this.activeSession || !this.activeSession.repoPath) return;
    if (this.pendingGitPulse) return;
    this.pendingGitPulse = setTimeout(async () => {
      this.pendingGitPulse = undefined;
      const snapshot = await this.captureGitSnapshot(repo, this.activeSession?.repoPath!, reason);
      if (snapshot) {
        this.recordEvent('git:status', { reason, snapshot });
      }
    }, GIT_STATUS_THROTTLE_MS);
  }

  private async finalizeSession(options: {
    reason: string;
    entry?: {
      workItemId: number;
      duration: number;
      hoursDecimal?: number;
      startTime: number;
      endTime: number;
      capApplied?: boolean;
      capLimitHours?: number;
    };
  }) {
    if (!this.activeSession) return;
    const session = this.activeSession;
    const repo = session.repo;
    const repoPath = session.repoPath;
    let finalSnapshot: GitSnapshot | undefined;
    if (repo && repoPath) {
      finalSnapshot = await this.captureGitSnapshot(repo, repoPath, options.reason);
      if (finalSnapshot) {
        this.recordEvent('git:status', {
          reason: `${options.reason}:final`,
          snapshot: finalSnapshot,
        });
      }
    }

    const stoppedAt = Date.now();
    if (options.entry) {
      this.recordEvent('timer:stop', {
        workItemId: options.entry.workItemId,
        duration: options.entry.duration,
        hoursDecimal: options.entry.hoursDecimal,
        startTime: options.entry.startTime,
        endTime: options.entry.endTime,
        capApplied: options.entry.capApplied,
        capLimitHours: options.entry.capLimitHours,
      });
    } else {
      this.recordEvent('timer:stop', { reason: options.reason });
    }

    const record: SessionRecord = {
      sessionId: session.sessionId,
      workItemId: session.workItemId,
      workItemTitle: session.workItemTitle,
      startedAt: session.startedAt,
      stoppedAt,
      timerHours: options.entry?.hoursDecimal,
      capApplied: options.entry?.capApplied,
      capLimitHours: options.entry?.capLimitHours,
      baseline: session.baseline,
      finalSnapshot,
      events: [...session.events],
    };

    await this.persistRecord(record);
    this.disposeSession();
  }

  private async persistRecord(record: SessionRecord) {
    try {
      if (this.getStorageMode() === 'sqlite') {
        const db = await this.ensureTelemetryDb();
        if (db) {
          await db.persistSession(record);
          return;
        }
      }
      await this.context.workspaceState.update(TELEMETRY_STORAGE_KEY, record);
    } catch (error) {
      console.warn('[sessionTelemetry] Failed to persist session record', error);
      // fallback to workspaceState
      try {
        await this.context.workspaceState.update(TELEMETRY_STORAGE_KEY, record);
      } catch {
        // ignore secondary persist errors
      }
    }
  }

  private getStorageMode(): TelemetryStorageMode {
    const config = vscode.workspace.getConfiguration('azureDevOpsIntegration');
    const value = (config.get<string>(STORAGE_MODE_CONFIG_KEY, 'sqlite') || '').toLowerCase();
    return value === 'workspacestate' ? 'workspaceState' : 'sqlite';
  }

  private async ensureTelemetryDb(): Promise<TelemetryDatabase | undefined> {
    const mode = this.getStorageMode();
    if (mode !== 'sqlite') {
      this.telemetryDb = undefined;
      this.telemetryDbUri = undefined;
      return undefined;
    }

    const sqliteUri = await this.resolveSqliteUri();
    if (!sqliteUri) return undefined;

    if (
      !this.telemetryDb ||
      !this.telemetryDbUri ||
      this.telemetryDbUri.fsPath !== sqliteUri.fsPath
    ) {
      try {
        this.telemetryDb = new TelemetryDatabase(this.context, { storageFile: sqliteUri });
        this.telemetryDbUri = sqliteUri;
      } catch (error) {
        console.warn(
          '[SessionTelemetry] Failed to create TelemetryDatabase, falling back to workspaceState:',
          error
        );
        // Auto-fallback to workspaceState mode when SQLite initialization fails
        const config = vscode.workspace.getConfiguration('azureDevOpsIntegration');
        await config.update(
          STORAGE_MODE_CONFIG_KEY,
          'workspaceState',
          vscode.ConfigurationTarget.Global
        );
        this.telemetryDb = undefined;
        this.telemetryDbUri = undefined;
        return undefined;
      }
    }

    return this.telemetryDb;
  }

  private async resolveSqliteUri(): Promise<vscode.Uri | undefined> {
    const config = vscode.workspace.getConfiguration('azureDevOpsIntegration');
    const rawSetting = config.get<string>(SQLITE_PATH_CONFIG_KEY, '')?.trim();
    if (!rawSetting) {
      return vscode.Uri.joinPath(this.context.globalStorageUri, 'session-telemetry.sqlite');
    }

    const expanded = rawSetting.startsWith('~')
      ? path.join(os.homedir(), rawSetting.slice(1))
      : rawSetting;
    const candidate = path.isAbsolute(expanded)
      ? expanded
      : this.resolveRelativeSqlitePath(expanded);

    if (!candidate) return undefined;
    return vscode.Uri.file(candidate);
  }

  private resolveRelativeSqlitePath(relPath: string): string | undefined {
    const workspaceFolder = vscode.workspace.workspaceFolders?.[0]?.uri.fsPath;
    if (workspaceFolder) {
      return path.join(workspaceFolder, relPath);
    }
    return path.join(this.context.globalStorageUri.fsPath, relPath);
  }

  private async getPrimaryRepository(): Promise<any | undefined> {
    const api = await this.ensureGitApi();
    if (!api) return undefined;
    const repo = api.repositories?.[0];
    return repo;
  }

  private async captureGitSnapshot(
    repo: any,
    repoPath: string,
    reason?: string
  ): Promise<GitSnapshot> {
    try {
      const head = repo?.state?.HEAD;
      const workingTree = Array.isArray(repo?.state?.workingTreeChanges)
        ? (repo.state.workingTreeChanges
            .map((change: any) => asRelativePath(change.resourceUri || change.uri))
            .filter(Boolean) as string[])
        : [];
      const staged = Array.isArray(repo?.state?.indexChanges)
        ? (repo.state.indexChanges
            .map((change: any) => asRelativePath(change.resourceUri || change.uri))
            .filter(Boolean) as string[])
        : [];
      const merge = Array.isArray(repo?.state?.mergeChanges)
        ? (repo.state.mergeChanges
            .map((change: any) => asRelativePath(change.resourceUri || change.uri))
            .filter(Boolean) as string[])
        : [];

      const statusShort = await runGitCommand(repoPath, ['status', '--short']);
      const diffStat = await runGitCommand(repoPath, ['diff', '--stat']);
      const diffStatStaged = await runGitCommand(repoPath, ['diff', '--stat', '--cached']);

      return {
        branch: head?.name,
        commit: head?.commit,
        upstream: head?.upstream?.name,
        ahead: head?.ahead,
        behind: head?.behind,
        workingTree,
        staged,
        merge,
        statusShort,
        diffStat,
        diffStatStaged,
      };
    } catch (error) {
      console.warn('[sessionTelemetry] captureGitSnapshot failed', { reason, error });
      return {};
    }
  }
}

import * as path from 'node:path';
import * as vscode from 'vscode';

type TelemetryDatabaseOptions = {
  storageFile?: vscode.Uri;
};

// Lazy-loaded types for SQL.js to avoid build-time dependency
type Database = any;
type SqlJsStatic = any;

export class TelemetryDatabase {
  private db: Database | undefined;
  private sqlJs: SqlJsStatic | undefined;
  private ready: Promise<void>;

  constructor(
    private readonly context: vscode.ExtensionContext,
    private readonly options: TelemetryDatabaseOptions = {}
  ) {
    this.ready = this.init();
  }

  async persistSession(record: {
    sessionId: string;
    workItemId: number;
    workItemTitle: string;
    startedAt: number;
    stoppedAt: number;
    timerHours?: number;
    capApplied?: boolean;
    capLimitHours?: number;
    baseline?: unknown;
    finalSnapshot?: unknown;
    events: Array<{
      id: string;
      type: string;
      timestamp: number;
      payload: unknown;
    }>;
  }) {
    await this.ready;
    if (!this.db) return;
    this.db.run(
      `INSERT OR REPLACE INTO session (sessionId, workItemId, workItemTitle, startedAt, stoppedAt, timerHours, capApplied, capLimitHours, baseline, finalSnapshot)
       VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?)`.trim(),
      [
        record.sessionId,
        record.workItemId,
        record.workItemTitle,
        record.startedAt,
        record.stoppedAt,
        record.timerHours ?? null,
        record.capApplied ? 1 : 0,
        record.capLimitHours ?? null,
        record.baseline ? JSON.stringify(record.baseline) : null,
        record.finalSnapshot ? JSON.stringify(record.finalSnapshot) : null,
      ]
    );
    for (const event of record.events) {
      this.db.run(
        `INSERT OR REPLACE INTO event (id, sessionId, type, timestamp, payload) VALUES (?, ?, ?, ?, ?)`.trim(),
        [
          event.id,
          record.sessionId,
          event.type,
          event.timestamp,
          event.payload ? JSON.stringify(event.payload) : null,
        ]
      );
    }
    await this.save();
  }

  async getSessions() {
    await this.ready;
    if (!this.db) return [];
    const stmt = this.db.prepare('SELECT * FROM session ORDER BY startedAt DESC');
    const sessions: Array<Record<string, unknown>> = [];
    try {
      while (stmt.step()) {
        const row = stmt.getAsObject();
        row.baseline = row.baseline ? JSON.parse(row.baseline as string) : undefined;
        row.finalSnapshot = row.finalSnapshot ? JSON.parse(row.finalSnapshot as string) : undefined;
        sessions.push(row);
      }
    } finally {
      stmt.free();
    }
    return sessions;
  }

  private async init() {
    try {
      // Lazy load sql.js only when needed
      const sqlJsModule = await import('sql.js');
      const initSqlJs = sqlJsModule.default || sqlJsModule;
      this.sqlJs = await initSqlJs();
      const bytes = await this.loadFromDisk();
      this.db = this.sqlJs ? new this.sqlJs.Database(bytes ?? undefined) : undefined;
      this.createTables();
    } catch (error) {
      console.warn(
        '[TelemetryDatabase] Failed to initialize SQLite, disabling database features:',
        error
      );
      // Gracefully degrade - we'll rely on sessionTelemetry's workspaceState fallback
      this.db = undefined;
      this.sqlJs = undefined;
    }
  }

  private createTables() {
    if (!this.db) return;
    this.db.run(
      `CREATE TABLE IF NOT EXISTS session (
         sessionId TEXT PRIMARY KEY,
         workItemId INTEGER,
         workItemTitle TEXT,
         startedAt INTEGER,
         stoppedAt INTEGER,
         timerHours REAL,
         capApplied INTEGER,
         capLimitHours REAL,
         baseline TEXT,
         finalSnapshot TEXT
       );
       CREATE TABLE IF NOT EXISTS event (
         id TEXT PRIMARY KEY,
         sessionId TEXT,
         type TEXT,
         timestamp INTEGER,
         payload TEXT
       );`
    );
  }

  private async save() {
    if (!this.db) return;
    const storageFile = await this.ensureStorageFile();
    if (!storageFile) return;
    const data = this.db.export();
    await vscode.workspace.fs.writeFile(storageFile, data);
  }

  private async loadFromDisk(): Promise<Uint8Array | undefined> {
    const storageFile = await this.ensureStorageFile(false);
    if (!storageFile) return undefined;
    try {
      const bytes = await vscode.workspace.fs.readFile(storageFile);
      return bytes;
    } catch (error) {
      if (
        (error as { code?: string }).code === 'FileNotFound' ||
        (error as any)?.name === 'EntryNotFound'
      ) {
        return undefined;
      }
      console.warn('[telemetryDatabase] Failed to read existing database', error);
      return undefined;
    }
  }

  private async ensureStorageFile(createDir = true): Promise<vscode.Uri | undefined> {
    const target =
      this.options.storageFile ??
      vscode.Uri.joinPath(this.context.globalStorageUri, 'telemetry.sqlite');
    if (!target) return undefined;
    if (createDir) {
      const dir = vscode.Uri.file(path.dirname(target.fsPath));
      await vscode.workspace.fs.createDirectory(dir);
    }
    return target;
  }
}

import { EventBus } from './events.js';
import type {
  RepositoryProvider,
  WorkItemRepository,
  CommentRepository,
  TimerRepository,
  PullRequestRepository,
} from './repository.js';
import type { ID, WorkItem, Comment, TimerEntry, PullRequest } from './types.js';

class InMemoryWorkItems implements WorkItemRepository {
  private items = new Map<ID, WorkItem>();
  private bus = new EventBus();

  on(listener: (ev: { type: 'workItem'; ids: Array<ID> }) => void) {
    // Adapt to ChangeEvent shape used by EventBus
    return this.bus.on((ev) => {
      if (ev.type === 'workItem') listener({ type: 'workItem', ids: ev.ids });
    });
  }

  async getById(id: ID): Promise<WorkItem | undefined> {
    return this.items.get(id);
  }

  async query(): Promise<WorkItem[]> {
    return Array.from(this.items.values());
  }

  async upsert(items: WorkItem[]): Promise<void> {
    const ids: ID[] = [];
    for (const it of items) {
      this.items.set(it.id, it);
      ids.push(it.id);
    }
    // debug instrumentation
    this.bus.emit({ type: 'workItem', ids, reason: 'upsert' });
  }

  async delete(ids: ID[]): Promise<void> {
    for (const id of ids) this.items.delete(id);
    // debug instrumentation
    // @ts-ignore access private for debug
    this.bus.emit({ type: 'workItem', ids, reason: 'delete' });
  }
}

class InMemoryComments implements CommentRepository {
  private byWi = new Map<ID, Comment[]>();
  private bus = new EventBus();
  on(listener: (ev: { type: 'comment'; ids: Array<ID> }) => void) {
    return this.bus.on((ev) => {
      if (ev.type === 'comment') listener({ type: 'comment', ids: ev.ids });
    });
  }
  async listForWorkItem(workItemId: ID): Promise<Comment[]> {
    return this.byWi.get(workItemId) ?? [];
  }
  async upsert(items: Comment[]): Promise<void> {
    const ids: ID[] = [];
    for (const c of items) {
      const arr = this.byWi.get(c.workItemId) ?? [];
      const idx = arr.findIndex((x) => x.id === c.id);
      if (idx >= 0) arr[idx] = c;
      else arr.push(c);
      this.byWi.set(c.workItemId, arr);
      ids.push(c.id);
    }
    this.bus.emit({ type: 'comment', ids, reason: 'upsert' });
  }
}

class InMemoryTimers implements TimerRepository {
  private byWi = new Map<ID, TimerEntry[]>();
  private bus = new EventBus();
  on(listener: (ev: { type: 'timerEntry'; ids: Array<ID> }) => void) {
    return this.bus.on((ev) => {
      if (ev.type === 'timerEntry') listener({ type: 'timerEntry', ids: ev.ids });
    });
  }
  async listForWorkItem(workItemId: ID): Promise<TimerEntry[]> {
    return this.byWi.get(workItemId) ?? [];
  }
  async upsert(entries: TimerEntry[]): Promise<void> {
    const ids: ID[] = [];
    for (const e of entries) {
      const arr = this.byWi.get(e.workItemId) ?? [];
      const idx = arr.findIndex((x) => x.id === e.id);
      if (idx >= 0) arr[idx] = e;
      else arr.push(e);
      this.byWi.set(e.workItemId, arr);
      ids.push(e.id);
    }
    this.bus.emit({ type: 'timerEntry', ids, reason: 'upsert' });
  }
}

class InMemoryPRs implements PullRequestRepository {
  private items: PullRequest[] = [];
  private bus = new EventBus();
  on(listener: (ev: { type: 'pullRequest'; ids: Array<ID> }) => void) {
    return this.bus.on((ev) => {
      if (ev.type === 'pullRequest') listener({ type: 'pullRequest', ids: ev.ids });
    });
  }
  async listMine(): Promise<PullRequest[]> {
    return this.items;
  }
  async upsert(items: PullRequest[]): Promise<void> {
    const ids: ID[] = [];
    for (const p of items) {
      const idx = this.items.findIndex((x) => x.id === p.id);
      if (idx >= 0) this.items[idx] = p;
      else this.items.push(p);
      ids.push(p.id);
    }
    this.bus.emit({ type: 'pullRequest', ids, reason: 'upsert' });
  }
}

export function createMemoryRepository(): RepositoryProvider {
  return {
    workItems: new InMemoryWorkItems(),
    comments: new InMemoryComments(),
    timers: new InMemoryTimers(),
    pullRequests: new InMemoryPRs(),
  } as RepositoryProvider;
}

export type EntityType = 'workItem' | 'comment' | 'timerEntry' | 'pullRequest';

export interface ChangeEvent {
  type: EntityType;
  ids: Array<string | number>;
  reason?: 'upsert' | 'delete' | 'sync' | 'local-mutation';
}

export type Listener = (ev: ChangeEvent) => void;

export class EventBus {
  private listeners: Set<Listener> = new Set();

  on(listener: Listener): () => void {
    this.listeners.add(listener);
    // noop
    return () => {
      const deleted = this.listeners.delete(listener);
      if (deleted) {
        // noop
      }
    };
  }

  emit(ev: ChangeEvent) {
    const snapshot = Array.from(this.listeners);
    for (const l of snapshot) {
      try {
        l(ev);
      } catch {
        // swallow listener errors to avoid breaking the bus
      }
    }
  }
}

// Lightweight store pattern to avoid adding svelte as a dependency here.
// The apps (extension/web) can wrap these with actual Svelte stores.

// Minimal local copies of types to avoid cross-package TS path issues in the skeleton stage.
type ID = string | number;
export interface WorkItem {
  id: ID;
  title: string;
  state: string;
  assignedTo?: string;
  tags?: string[];
  iteration?: string;
  area?: string;
  updated?: string;
}

export interface RepositoryProvider {
  workItems: {
    query(filters?: Record<string, unknown>): Promise<WorkItem[]>;
    on(listener: () => void): () => void;
  };
}

type Subscriber<T> = (value: T) => void;

class ReadableStore<T> {
  private value: T;
  private subs: Set<Subscriber<T>> = new Set();
  constructor(initial: T) {
    this.value = initial;
  }
  subscribe(fn: Subscriber<T>) {
    this.subs.add(fn);
    fn(this.value);
    return () => {
      const deleted = this.subs.delete(fn);
      if (deleted) {
        // noop
      }
    };
  }
  protected set(v: T) {
    this.value = v;
    // Copy subscribers to avoid mutation issues during iteration
    const subsSnapshot = Array.from(this.subs);
    for (const s of subsSnapshot) {
      try {
        s(v);
      } catch {
        // Never let one subscriber break the chain
      }
    }
  }
}

export class WorkItemsView extends ReadableStore<WorkItem[]> {
  private unlisten: (() => void) | null = null;
  constructor(private repo: RepositoryProvider) {
    super([]);
  }
  async init() {
    const items = await this.repo.workItems.query();
    this.set(items);
    this.unlisten = this.repo.workItems.on(async () => {
      const fresh = await this.repo.workItems.query();
      this.set(fresh);
    });
    return () => this.dispose();
  }
  dispose() {
    if (this.unlisten) this.unlisten();
    this.unlisten = null;
  }
}

// Filters and derived visible items view
export interface Filters {
  assignee?: string;
  tags?: string[];
}

class WritableStore<T> extends ReadableStore<T> {
  setValue(v: T) {
    // expose a setter for external updates (e.g., UI interactions)
    // accessing protected via class method is allowed
    console.log('[stores] Writable.setValue', v);
    this.set(v);
  }
}

export class FiltersStore extends WritableStore<Filters> {
  constructor(initial: Filters = {}) {
    super(initial);
  }
}

export class VisibleWorkItemsView extends ReadableStore<WorkItem[]> {
  private unsubscribers: Array<() => void> = [];
  private currentItems: WorkItem[] = [];
  private currentFilters: Filters = {};

  constructor(itemsView: WorkItemsView, filters: FiltersStore) {
    super([]);
    // Subscribe to items and filters
    const onItems = (items: WorkItem[]) => {
      this.currentItems = items;
      this.recompute();
    };
    const unsubItems = itemsView.subscribe(onItems);
    const onFilters = (f: Filters) => {
      this.currentFilters = f;
      this.recompute();
    };
    const unsubFilters = filters.subscribe(onFilters);
    this.unsubscribers.push(unsubItems, unsubFilters);
  }

  private recompute() {
    const { assignee, tags } = this.currentFilters;
    let out = this.currentItems;
    if (assignee && assignee.length > 0) {
      out = out.filter((w) => (w.assignedTo ?? '').toLowerCase() === assignee.toLowerCase());
    }
    if (tags && tags.length > 0) {
      out = out.filter((w) => {
        const wt = new Set((w.tags ?? []).map((t) => t.toLowerCase()));
        return tags.every((t) => wt.has(t.toLowerCase()));
      });
    }
    this.set(out);
  }

  dispose() {
    for (const u of this.unsubscribers) u();
    this.unsubscribers = [];
  }
}

// Shared normalization utilities for work items
// Supports both raw Azure DevOps shape: { id, fields: { 'System.Title': ... } }
// and previously flattened shape: { id, title, state, type, assignedTo, ... }

export interface NormalizedWorkItem {
  id: number;
  title: string;
  type: string;
  state: string;
  assignedTo: string | undefined;
  priority: number | undefined;
  tags: string[];
  iterationPath?: string;
  description?: string;
  raw: any; // original object for advanced scenarios
}

export function getField(item: any, field: string): any {
  if (!item) return undefined;
  switch (field) {
    case 'System.Id':
      return item.id ?? item.fields?.['System.Id'];
    case 'System.Title':
      return item.title ?? item.fields?.['System.Title'];
    case 'System.State':
      return item.state ?? item.fields?.['System.State'];
    case 'System.WorkItemType':
      return item.type ?? item.fields?.['System.WorkItemType'];
    case 'System.AssignedTo': {
      const a = item.assignedTo || item.fields?.['System.AssignedTo'];
      if (a && typeof a === 'object') return a.displayName || a.uniqueName || a.name;
      return a;
    }
    case 'System.Tags': {
      if (item.tags) return Array.isArray(item.tags) ? item.tags.join(';') : item.tags;
      return item.fields?.['System.Tags'];
    }
    case 'Microsoft.VSTS.Common.Priority':
      return item.priority ?? item.fields?.['Microsoft.VSTS.Common.Priority'];
    case 'System.IterationPath':
      return item.iterationPath ?? item.fields?.['System.IterationPath'];
    case 'System.AreaPath':
      return item.areaPath ?? item.fields?.['System.AreaPath'];
    case 'System.Description':
      return item.description ?? item.fields?.['System.Description'];
    default:
      return item[field] ?? item.fields?.[field];
  }
}

export function toNormalized(item: any): NormalizedWorkItem {
  const id = Number(getField(item, 'System.Id'));
  const title = String(getField(item, 'System.Title') || `Work Item #${id}`);
  const state = String(getField(item, 'System.State') || 'Unknown');
  const type = String(getField(item, 'System.WorkItemType') || 'Unknown');
  const assignedRaw = getField(item, 'System.AssignedTo');
  const assignedTo = assignedRaw || undefined;
  const priority = getField(item, 'Microsoft.VSTS.Common.Priority');
  const tagsField = getField(item, 'System.Tags');
  const tags =
    typeof tagsField === 'string'
      ? tagsField.split(';').filter(Boolean)
      : Array.isArray(tagsField)
        ? tagsField
        : [];
  const iterationPath = getField(item, 'System.IterationPath');
  const description = getField(item, 'System.Description');

  return {
    id,
    title,
    state,
    type,
    assignedTo,
    priority,
    tags,
    iterationPath,
    description,
    raw: item,
  };
}

export function normalizeArray(items: any[]): NormalizedWorkItem[] {
  return items.map(toNormalized);
}
