async function createComponent(name, options) {
  const outputDir = options.directory || join(process.cwd(), "src", "components");
  const componentDir = join(outputDir, name);
  if (existsSync(componentDir)) {
    console.error(`
\u2717 Component '${name}' already exists at ${componentDir}`);
    console.error("  Please choose a different name or remove the existing component.\n");
    process.exit(1);
  }
  console.log(`
\u{1F9E9} Creating component: ${name}`);
  console.log(`   Location: ${componentDir}
`);
  await mkdir(componentDir, { recursive: true });
  await writeFile(join(componentDir, `${name}.svelte`), generateComponentSvelte(name));
  await writeFile(join(componentDir, `${name}.schema.js`), generateComponentSchema(name));
  await writeFile(
    join(componentDir, "index.ts"),
    `export { default as ${name} } from './${name}.svelte';
`
  );
  console.log(`\u2713 Created ${name}.svelte`);
  console.log(`\u2713 Created ${name}.schema.js`);
  console.log(`\u2713 Created index.ts`);
  console.log(`
\u2705 Component created successfully!

Import it in your application:

  import { ${name} } from './components/${name}';

Or use the schema for generation:

  praxis generate --schema src/components/${name}/${name}.schema.js
`);
}
async function create(type, name, options) {
  if (!name) {
    console.error("\n\u2717 Please provide a name for the " + type);
    console.error(`  Usage: praxis create ${type} <name>
`);
    process.exit(1);
  }
  if (!/^[a-zA-Z][a-zA-Z0-9-_]*$/.test(name)) {
    console.error("\n\u2717 Invalid name format");
    console.error(
      "  Name must start with a letter and contain only letters, numbers, hyphens, and underscores.\n"
    );
    process.exit(1);
  }
  switch (type) {
    case "app":
      await createApp(name, options);
      break;
    case "component":
      await createComponent(name, options);
      break;
    default:
      console.error(`
\u2717 Unknown type: ${type}`);
      console.error("  Valid types: app, component\n");
      process.exit(1);
  }
}
